INSERT INTO `tinbai` (`id`, `idTheLoai`, `TieuDe`, `TrichDan`, `anhtrichdan`, `NoiDung`, `NgayDang`, `TacGia`) VALUES
(1, 2, 'Sẵn sàng mở lại trường học đón học sinh đến trường', 'VHO – Nhận định dạy học trực tuyến là giải pháp để học sinh “tạm ngừng đến trường nhưng không ngừng học”, tuy nhiên, việc dạy và học trực tuyến, dạy học qua truyền hình chỉ là hình thức bổ trợ và không tránh khỏi những tác động không mong muốn, Phó Thủ tướng Vũ Đức Đam đã chỉ đạo Bộ Y tế phối hợp với Bộ GDĐT, Bộ Thông tin và Truyền thông chuẩn bị đủ nguồn vắc xin và tổ chức ngay việc đăng ký tiêm vắc xin cho trẻ em từ 12-17 tuổi, để sẵn sàng mở lại trường học đón học sinh đến trường.', 'images/anh_giaoduc_1.jpg', '<p>Nhận định năm học 2021-2022 sẽ kh&oacute; hơn nhiều, Ph&oacute; Thủ tướng chia sẻ, đến thời điểm n&agrave;y nhiều địa phương chưa thể học trực tiếp, nhiều nơi trong v&ugrave;ng dịch rất kh&oacute; khăn. Mặc d&ugrave; đ&atilde; c&oacute; nhiều nỗ lực để đ&aacute;p ứng một phần y&ecirc;u cầu, song dạy học trực tuyến, dạy học qua truyền h&igrave;nh chỉ l&agrave; h&igrave;nh thức bổ trợ v&agrave; kh&ocirc;ng tr&aacute;nh khỏi những t&aacute;c động kh&ocirc;ng mong muốn.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46284/86839di-hoc-3.jpg\" style=\"height:400px; width:572px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Ph&oacute; Thủ&nbsp;</strong><strong>tư</strong><strong>ớng Vũ&nbsp;</strong><strong>Đ</strong><strong>ức&nbsp;</strong><strong>Đam&nbsp;</strong><strong>chủ tr&igrave; cuộc họp</strong></p>\r\n\r\n<p>Ph&oacute; Thủ tướng y&ecirc;u cầu, phải c&oacute; kế hoạch, giải ph&aacute;p rất chủ động để ho&agrave;n th&agrave;nh kế hoạch năm học; c&ugrave;ng với đ&oacute; l&agrave; chủ động c&oacute; kế hoạch b&ugrave; đắp kiến thức cho học sinh bằng c&aacute;c h&igrave;nh thức ph&ugrave; hợp, trong ngắn hạn cũng như trong một v&agrave;i năm tới. &ldquo;Ch&uacute;ng ta cần c&oacute; c&aacute;c giải ph&aacute;p rất nhanh cho năm học n&agrave;y&rdquo; &ndash; Ph&oacute; Thủ tướng nhấn mạnh.</p>\r\n\r\n<p>Thống nhất với phương &aacute;n của Bộ GDĐT l&agrave; kế hoạch năm học 2021-2022 sẽ kết th&uacute;c linh hoạt, t&ugrave;y v&agrave;o t&igrave;nh h&igrave;nh của địa phương, Ph&oacute; Thủ tưởng lưu &yacute;, sự linh hoạt kh&ocirc;ng chỉ ở cấp tỉnh m&agrave; c&ograve;n s&acirc;u hơn xuống cấp huyện, cấp x&atilde;; việc thi cử, đ&aacute;nh gi&aacute; kết quả học tập định k&igrave;, cuối năm phải được thực hiện rất linh hoạt.</p>\r\n\r\n<p>Ph&oacute; Thủ tướng cũng đề nghị ng&agrave;nh Gi&aacute;o dục s&aacute;t sao hơn nữa trong việc triển khai đổi mới chương tr&igrave;nh, s&aacute;ch gi&aacute;o khoa gi&aacute;o dục phổ th&ocirc;ng theo h&igrave;nh thức cuốn chiếu. Trong đ&oacute;, việc chuẩn bị s&aacute;ch gi&aacute;o khoa, từ thẩm định, xuất bản, đến ph&aacute;t h&agrave;nh đến tay học sinh, phụ huynh - thời gian qua d&ugrave; dịch bệnh nhưng đ&atilde; l&agrave;m tương đối tốt - cần tiếp tục l&agrave;m tốt hơn nữa; đẩy mạnh ứng dụng đặt h&agrave;ng thương mại điện tử, đưa s&aacute;ch đến tận tay học sinh.</p>\r\n\r\n<p>Về đảm bảo an to&agrave;n trường học, Ph&oacute; Thủ tướng y&ecirc;u cầu, Bộ GDĐT r&agrave; so&aacute;t kỹ c&aacute;c quy định bảo đảm an to&agrave;n học đường. Đồng thời, phải tổ chức ti&ecirc;m vắc xin sớm v&agrave; thật an to&agrave;n cho học sinh trong độ tuổi theo quy định của Bộ Y tế. Ph&oacute; Thủ tướng giao Bộ Y tế phối hợp với Bộ GDĐT, Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tổ chức ngay việc đăng k&yacute; ti&ecirc;m vắc xin cho trẻ em từ 12-17 tuổi, để sẵn s&agrave;ng khi c&oacute; vắc xin sẽ tổ chức ti&ecirc;m nhanh v&agrave; an to&agrave;n.</p>\r\n\r\n<p>Với tinh thần đến trường phải an to&agrave;n, Ph&oacute; Thủ tướng chỉ đạo Bộ Y tế, Bộ GDĐT r&agrave; so&aacute;t lại, chuẩn bị hệ thống gi&aacute;m s&aacute;t y tế học đường, đảm bảo tất cả học sinh đến trường học đều c&oacute; đầu mối cụ thể theo d&otilde;i sức khỏe. Ngo&agrave;i an to&agrave;n về sức khỏe, Ph&oacute; Thủ tướng cũng lưu &yacute; việc chăm s&oacute;c, gi&aacute;o dục học sinh to&agrave;n diện; đặc biệt l&agrave; vấn đề t&acirc;m l&yacute; học đường.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46284/86841di-hoc1.jpg\" style=\"height:338px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Quang cảnh cuộc họp</strong></p>\r\n\r\n<p>Về vấn đề đảm bảo an to&agrave;n cho học sinh đến trường, Thứ trưởng Bộ Y tế Đỗ Xu&acirc;n Tuy&ecirc;n cho biết, Bộ Y tế đ&atilde; lựa chọn được loại vắc xin ph&ugrave; hợp v&agrave; đang chuẩn bị đủ nguồn vắc xin để sẵn s&agrave;ng ti&ecirc;m cho trẻ em từ 12-17 tuổi.</p>\r\n\r\n<p>Theo r&agrave; so&aacute;t của Bộ Y tế, cả nước hiện c&oacute; tr&ecirc;n 8 triệu trẻ em từ 12-17 tuổi, với số lượng vắc xin cần để ti&ecirc;m hai mũi l&agrave; khoảng 16 triệu liều. Bộ Y tế phấn đấu trong qu&yacute; IV năm 2021 sẽ ti&ecirc;m đủ 2 mũi cho tr&ecirc;n 95% trẻ em thuộc đối tượng n&agrave;y.</p>\r\n\r\n<p>Đối với trẻ em từ 3-11 tuổi (khoảng tr&ecirc;n 14 triệu trẻ em), Bộ Y tế đang tiếp cận nguồn vắc xin ph&ugrave; hợp, đồng thời tham khảo kinh nghiệm thế giới v&agrave; c&aacute;c chuy&ecirc;n gia, nh&agrave; khoa học trong nước để khi đảm bảo điều kiện sẽ sẵn s&agrave;ng ti&ecirc;m cho trẻ em lứa tuổi n&agrave;y.</p>\r\n', NULL, NULL),
(2, 2, 'Tai nạn khi trẻ học online, phụ huynh lo lắng', 'VHO- Chỉ trong hơn một tháng đã xảy ra 2 vụ trẻ em học online bị tai nạn thương tâm liên quan tới điện thoại và máy tính. Trong tình hình hàng triệu học sinh buộc phải học trực tuyến, tai nạn về điện thoại và các nguyên nhân khác luôn tiềm ẩn có thể đến với học sinh bất cứ lúc nào nếu không có biện pháp phòng bị.', 'images/anh_giaoduc_2.jpg', '<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46414/87152IMG-4405.JPG\" /></p>\r\n\r\n<p>&nbsp;<strong>Trẻ học trực tuyến tại nh&agrave;</strong></p>\r\n\r\n<p>&nbsp;C&aacute;ch đ&acirc;y v&agrave;i ng&agrave;y, một vụ tai nạn thương t&acirc;m đ&atilde; xảy ra đối với nam sinh lớp 5 tại Nghệ An khi em n&agrave;y đang học online ở nh&agrave; th&igrave; chiếc điện thoại ph&aacute;t nổ, khiến em tử vong sau khi được đưa đến bệnh viện cấp cứu. Điều tra ban đầu cho thấy, v&agrave;o khoảng 16h chiều, khi em đang vừa học vừa sạc điện thoại th&igrave; bất ngờ chiếc điện thoại ph&aacute;t nổ, l&agrave;m ch&aacute;y sang quần &aacute;o. Học sinh được h&agrave;ng x&oacute;m đưa đi cấp cứu trong t&igrave;nh trạng bị bỏng nặng v&agrave; đ&atilde; kh&ocirc;ng qua khỏi.</p>\r\n\r\n<p>Trước đ&oacute; khoảng hơn một th&aacute;ng, một học sinh 10 tuổi đang học lớp 5 ở Thanh Xu&acirc;n, H&agrave; Nội bị tử vong thương t&acirc;m do bị điện giật khi đang học trực tuyến tr&ecirc;n m&aacute;y t&iacute;nh. Theo đ&oacute;, vụ việc xảy ra khoảng 9h30 s&aacute;ng 10.9, khi b&eacute; trai đang học online bằng m&aacute;y t&iacute;nh đ&atilde; bị điện giật, mặc d&ugrave; được gia đ&igrave;nh nhanh ch&oacute;ng đưa đi cấp cứu nhưng kh&ocirc;ng qua khỏi. Ngay sau khi tai nạn thương t&acirc;m xảy ra đối với học sinh tại quận Thanh Xu&acirc;n, nhiều trường học tr&ecirc;n địa b&agrave;n H&agrave; Nội đ&atilde; th&ocirc;ng tin, cảnh b&aacute;o đến gi&aacute;o vi&ecirc;n chủ nhiệm, phụ huynh quan t&acirc;m hơn đến c&aacute;c giải ph&aacute;p đảm bảo an to&agrave;n cho học sinh trong thời gian học trực tuyến. Được biết, Bộ GD&amp;ĐT đ&atilde; c&oacute; khuyến c&aacute;o c&aacute;c bậc phụ huynh v&agrave; thầy c&ocirc; gi&aacute;o về tăng cường c&aacute;c biện ph&aacute;p hỗ trợ, nhắc nhở, dạy cho c&aacute;c em một số kỹ năng cơ bản, đặc biệt l&agrave; hướng dẫn v&agrave; hỗ trợ học sinh tiểu học trong qu&aacute; tr&igrave;nh sử dụng c&aacute;c thiết bị học tập cũng như thiết bị trong gia đ&igrave;nh để đảm bảo an to&agrave;n.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, nhiều người cho rằng, việc khuyến c&aacute;o l&agrave; cần thiết nhưng đ&atilde; qu&aacute; muộn khi tai nạn đ&atilde; xảy ra v&agrave; &iacute;t nhất đ&atilde; c&oacute; 2 học sinh tử vong. Một chuy&ecirc;n gia gi&aacute;o dục cho rằng, việc trang bị c&aacute;c kiến thức cơ bản về ph&ograve;ng chống ch&aacute;y nổ, sơ cứu khi gặp tai nạn phải được phổ biến tới học sinh thường xuy&ecirc;n hơn v&agrave; b&agrave;i bản, thay v&igrave; &ldquo;nước đến ch&acirc;n mới nhảy&rdquo;. Theo Viện Khoa học An to&agrave;n Việt Nam, kỹ năng về an to&agrave;n khi sử dụng điện l&agrave; b&agrave;i học đặc biệt quan trọng đối với trẻ nhỏ. Phụ huynh cần giải th&iacute;ch cho con về c&aacute;ch thức hoạt động của điện như c&aacute;c đường d&acirc;y điện trong m&ocirc;i trường xung quanh, c&aacute;c ph&iacute;ch cắm, ổ cắm. Từ đ&oacute;, khuyến c&aacute;o c&aacute;c em kh&ocirc;ng nh&eacute;t bất cứ thứ g&igrave; v&agrave;o ổ cắm điện. Đ&aacute;ng lưu &yacute;, phụ huynh cần nhắc nhở li&ecirc;n tục những kiến thức về an to&agrave;n điện v&agrave; cho trẻ r&egrave;n luyện thường xuy&ecirc;n. Từ đ&oacute;, trẻ sẽ x&acirc;y dựng được kỹ năng về an to&agrave;n điện.</p>\r\n\r\n<p>Một phụ huynh ở H&agrave; Nội c&oacute; con em đang trong diện học trực tuyến cho biết, sau khi đọc được th&ocirc;ng tin về những vụ tai nạn tr&ecirc;n, gia đ&igrave;nh rất hoang mang. Anh đ&atilde; dặn d&ograve; con kh&ocirc;ng được vừa sạc vừa sử dụng c&aacute;c thiết bị điện tử như điện thoại, m&aacute;y t&iacute;nh bảng hay thậm ch&iacute; l&agrave; cả laptop&hellip; Anh cũng hướng dẫn c&aacute;c con tuyệt đối kh&ocirc;ng chạm v&agrave;o c&aacute;c thiết bị điện tử khi tay c&ograve;n ướt&hellip; Một phụ huynh kh&aacute;c cũng rất c&oacute; l&yacute; khi chia sẻ, khi trẻ phải tự học trực tuyến tại nh&agrave;, sẽ tốt hơn rất nhiều nếu c&oacute; sự hỗ trợ của người lớn. Đồng quan điểm, b&agrave; Nguyễn Phương Linh, Viện trưởng Viện Nghi&ecirc;n cứu quản l&yacute; ph&aacute;t triển bền vững cho rằng, theo nguy&ecirc;n tắc an to&agrave;n, trẻ dưới 8 tuổi kh&ocirc;ng n&ecirc;n ở nh&agrave; một m&igrave;nh, với những độ tuổi lớn hơn cũng vẫn cần sự gi&aacute;m s&aacute;t của người lớn. Trong điều kiện b&igrave;nh thường, tỷ lệ tai nạn thương t&iacute;ch ở trẻ em tại nước ta đ&atilde; kh&aacute; cao. Trong bối cảnh học sinh học trực tuyến d&agrave;i ng&agrave;y, nhiều gia đ&igrave;nh bố mẹ phải đi l&agrave;m kh&ocirc;ng c&oacute; người lớn ở b&ecirc;n cạnh, con phải tự sử dụng c&aacute;c thiết bị điện, th&igrave; vấn đề về đảm bảo an to&agrave;n cho trẻ c&agrave;ng cần được ch&uacute; &yacute; hơn bao giờ hết.</p>\r\n\r\n<p>&Ocirc;ng Đặng Hoa Nam, Cục trưởng Cục Trẻ em (Bộ LĐ,TB&amp;XH) cho biết, độ an to&agrave;n c&aacute;c thiết bị kết nối đầu cuối (m&aacute;y t&iacute;nh, điện thoại) khi sử dụng để học online rất quan trọng. Học sinh học online sẽ phải sử dụng c&aacute;c thiết bị trong nhiều giờ, nếu thiết bị đ&oacute; kh&ocirc;ng tốt phải sạc điện khi học, nguy cơ tai nạn lại c&agrave;ng cao hơn. Để đảm bảo an to&agrave;n cho trẻ em, tới đ&acirc;y, Bộ LĐ,TB&amp;XH, Cục Trẻ em sẽ chủ tr&igrave;, phối hợp với c&aacute;c Bộ, ng&agrave;nh để triển khai một số c&aacute;c giải ph&aacute;p để đảm bảo an to&agrave;n cho trẻ em trong ng&ocirc;i nh&agrave; của m&igrave;nh khi học trực tuyến. Đ&oacute; l&agrave; tăng cường cảnh b&aacute;o truyền th&ocirc;ng cho c&aacute;c bậc cha mẹ về việc phải gi&aacute;m s&aacute;t, kiểm tra độ an to&agrave;n của c&aacute;c trang thiết bị khi cho c&aacute;c con d&ugrave;ng v&agrave; hướng dẫn c&aacute;c con tu&acirc;n thủ những ti&ecirc;u chuẩn về mặt kỹ thuật. Theo khuyến c&aacute;o, để tr&aacute;nh những tai nạn đ&aacute;ng tiếc xảy ra khi học trực tuyến, bố mẹ học sinh cần ch&uacute; &yacute; thường xuy&ecirc;n r&agrave; so&aacute;t, kiểm tra t&igrave;nh trạng hoạt động, chất lượng của c&aacute;c thiết bị điện, điện tử m&agrave; con đang sử dụng. Bố mẹ n&ecirc;n sạc đầy pin, kiểm tra kỹ c&aacute;c thiết bị trước khi cho con học online, cũng cần lưu &yacute; con tr&aacute;nh trường hợp vừa sạc vừa học, đặc biệt với c&aacute;c thiết bị như điện thoại, ipad. B&ecirc;n cạnh đ&oacute;, c&aacute;c gia đ&igrave;nh cũng n&ecirc;n trang bị c&aacute;c loại ổ cắm chống giật, d&ograve; điện để tăng t&iacute;nh an to&agrave;n.</p>\r\n\r\n<p>Để đảm bảo an to&agrave;n cho học sinh khi tham gia học trực tuyến, một đơn vị tr&ecirc;n địa b&agrave;n H&agrave; Nội đ&atilde; y&ecirc;u cầu c&aacute;c trường tiểu học tr&ecirc;n địa b&agrave;n thực hiện tốt 4 nội dung, trong đ&oacute; c&oacute; việc tổ chức qu&aacute;n triệt lại đến từng gi&aacute;o vi&ecirc;n c&aacute;c quy định về đảm bảo an to&agrave;n cho học sinh khi tham gia học trực tuyến (gi&aacute;o vi&ecirc;n điểm danh, nắm r&otilde; số học sinh tham gia học tập, l&yacute; do vắng mặt của học sinh b&aacute;o c&aacute;o nh&agrave; trường sau mỗi buổi học), tuy&ecirc;n truyền, giải th&iacute;ch để cha mẹ học sinh hỗ trợ tốt nhất về cơ sở vật chất cho con khi học tập như: Kiểm tra mạng, đường truyền, ổ điện, d&acirc;y điện đảm bảo an to&agrave;n; vị tr&iacute; ngồi học phải đủ &aacute;nh s&aacute;ng, đảm bảo sự ri&ecirc;ng tư, tạo điều kiện cho học sinh tập trung khi tham gia học tập.&nbsp;</p>\r\n', NULL, NULL),
(3, 2, 'Hướng dẫn học sinh vượt qua cảm xúc tiêu cực khi học trực tuyến', 'VHO - Trong điều kiện dịch bệnh Covid-19 diễn ra phức tạp, học online hay học trực tuyến vẫn là giải pháp tình thế mà các nhà trường đang phải áp dụng. Học trực tuyến trong điều kiện học sinh học tập tại nhà, thiếu vắng các mối quan hệ bạn bè, khó khăn trong tương tác trực tiếp với giáo viên, không gian và môi trường học tập không đảm bảo... khiến cho nhiều học sinh gặp phải những khó khăn tâm lý ở các mức độ khác nhau.', 'images/anh_giaoduc_3.jpg', '<p><strong>Học trực tuyến v&agrave; những vấn&nbsp;</strong><strong>đ</strong><strong>ề t&acirc;m l&yacute;</strong></p>\r\n\r\n<p>Theo nhiều chuy&ecirc;n gia, Covid-19 l&agrave; một sang chấn nghi&ecirc;m trọng t&aacute;c động đến t&acirc;m l&yacute; con người khiến ch&uacute;ng ta dễ mắc phải những vấn đề t&acirc;m l&yacute; như trầm cảm, lo &acirc;u, rối loạn stress sau sang chấn. Đối với trẻ em trong lứa tuổi học sinh, những vấn đề t&acirc;m l&yacute; mắc phải sau một thời gian d&agrave;i sống trong c&aacute;c điều kiện hạn chế việc đi lại, giao tiếp, việc học tập trực tuyến k&eacute;o d&agrave;i, c&aacute;c mối quan hệ giao tiếp trực tiếp bị gi&aacute;n đoạn,... khiến cho học sinh trở th&agrave;nh nh&oacute;m đối tượng dễ mắc phải những vấn đề t&acirc;m l&yacute; nhất, như việc bị sa s&uacute;t tr&iacute; tuệ, lo &acirc;u, sợ h&atilde;i, căng thẳng,..</p>\r\n\r\n<p>Thời gian học online v&agrave; phải ở nh&agrave; qu&aacute; l&acirc;u n&ecirc;n học sinh c&oacute; thể bị mất tập trung, giảm hứng th&uacute; v&agrave; động cơ học tập; cảm thấy ch&aacute;n học, hoặc căng thẳng trong học tập. Nhiều học sinh cảm thấy c&ocirc; đơn, bối rối, buồn, quan hệ bạn b&egrave; giảm s&uacute;t, k&eacute;m chất lượng. Nguy&ecirc;n nh&acirc;n bởi thiếu đi sự tương t&aacute;c bạn b&egrave;, thiếu tương t&aacute;c cảm x&uacute;c khi giao tiếp bị hạn chế.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46542/87462hoc-truc-tuyen-2.jpeg\" style=\"height:400px; width:533px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Học trực tuyến, c&aacute;c em sẽ kh&ocirc;ng c&oacute; c&aacute;c hoạt động tập thể như khi học trực tiếp</strong></p>\r\n\r\n<p>Tham gia v&agrave;o mạng x&atilde; hội hoặc tương t&aacute;c qua c&aacute;c ững dụng học tập khiến cho học sinh trở n&ecirc;n ngại giao tiếp tương t&aacute;c thật, c&oacute; thể thu minh hoặc giao du th&aacute;i qu&aacute; v&agrave; bị lệ thuộc v&agrave; bạn b&egrave; tr&ecirc;n mạng x&atilde; hội.</p>\r\n\r\n<p>Việc ngồi qu&aacute; nhiều trước c&aacute;c thiết bị điện tử sẽ c&oacute; hại cho mắt, n&atilde;o v&agrave; c&aacute;c bộ phận kh&aacute;c của cơ thể. B&ecirc;n cạnh đ&oacute;, t&acirc;m l&yacute; học sinh cũng sẽ kh&ocirc;ng ổn định, dễ c&aacute;u gắt, giảm khả năng tập trung v&agrave; sa s&uacute;t tr&iacute; nhớ. Sau thời gian d&agrave;i học trực tuyến, nhiều học sinh đ&atilde; xuất hiện c&aacute;c dấu hiệu như ch&aacute;n nản, lo lắng, buồn, sợ h&atilde;i, bối rối, giảm hứng th&uacute;, bất an, bồn chồn, mất tập trung, mệt mỏi, kh&oacute; giữ được cảm x&uacute;c. Điều n&agrave;y khiến khả năng ch&uacute; &yacute;, tập trung của người học hạn chế n&ecirc;n khi ngồi trước điện thoại, m&aacute;y t&iacute;nh l&acirc;u sẽ cảm thấy mệt mỏi.</p>\r\n\r\n<p>Học online đồng nghĩa với việc c&aacute;c em kh&ocirc;ng được tham gia c&aacute;c hoạt động tập thể, cộng đồng v&agrave; c&aacute;c phong tr&agrave;o trong trường học. Ch&iacute;nh v&igrave; thế, nhiều em học sinh cảm thấy rất bức bối, b&iacute; b&aacute;ch. Bởi trước đ&acirc;y, ngo&agrave;i việc học, c&aacute;c em c&ograve;n được tham gia v&agrave;o c&aacute;c c&acirc;u lạc bộ, c&aacute;c hoạt động nh&oacute;m hay c&aacute;c hoạt động thể dục thể thao, c&aacute;c phong tr&agrave;o tập thể ngo&agrave;i giờ học ch&iacute;nh kho&aacute;.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, h&agrave;ng ng&agrave;y học sinh thường xuy&ecirc;n tiếp nhận nhiều th&ocirc;ng tin truyền th&ocirc;ng về dịch bệnh, tệ nạn, kh&oacute; khăn, thiếu thốn của mọi người, sự chết ch&oacute;c của những người bị nhiễm bệnh, về việc trẻ em bị nhiễm bệnh, trẻ em mồ c&ocirc;i do cha mẹ mất v&igrave; Covid-19 cũng sẽ dẫn đến t&acirc;m l&yacute; lo lắng, bất an.</p>\r\n\r\n<p><strong>Hư</strong><strong>ớng dẫn họ</strong><strong>c sinh vư</strong><strong>ợt qua cảm x&uacute;c ti&ecirc;u cực khi học trực tuyến</strong></p>\r\n\r\n<p>Theo c&aacute;c chuy&ecirc;n gia, với nh&oacute;m kh&oacute; khăn t&acirc;m l&yacute; li&ecirc;n quan đến sức khoẻ về thể chất, gi&aacute;o vi&ecirc;n cần hướng dẫn học sinh chủ động chăm s&oacute;c bản th&acirc;n, l&ecirc;n kế hoạch sinh hoạt khoa học như ăn, ngủ đ&uacute;ng giờ, ngủ đủ giấc; thời gian ngủ n&ecirc;n trước&nbsp;10h30&#39; tối v&agrave; thức dậy v&agrave;o giờ ph&ugrave; hợp để kịp ăn uống, học online m&agrave; kh&ocirc;ng qu&aacute; cập rập. C&ugrave;ng với đ&oacute;, tập thể dục h&agrave;ng ng&agrave;y, vận động nhẹ nh&agrave;ng trong giờ học, c&oacute; thể nhắm mắt khi nghe giảng b&agrave;i để hạn chế nh&igrave;n m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46542/87463hoc-truc-tuyen-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&oacute; thể nhắm mắt khi nghe giảng b&agrave;i để hạn chế nh&igrave;n m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh</strong></p>\r\n\r\n<p>Với những vấn đề t&acirc;m l&yacute; li&ecirc;n quan đến học tập, gi&aacute;o vi&ecirc;n cần hướng dẫn học sinh c&aacute;ch suy nghĩ t&iacute;ch cực; chủ động mở l&ograve;ng chia sẻ với thầy c&ocirc;, cha mẹ, người th&acirc;n về kh&oacute; khăn của m&igrave;nh để nhận được sự trợ gi&uacute;p.</p>\r\n\r\n<p>Khi học căng thẳng, gi&aacute;o vi&ecirc;n tổ chức cho học sinh vận động nhẹ nh&agrave;ng, h&iacute;t thở thật s&acirc;u để thư gi&atilde;n; trong khi học n&ecirc;n c&oacute; những qu&atilde;ng nghỉ để thay đổi tư thế, kh&ocirc;ng ngồi qu&aacute; l&acirc;u trước m&agrave;n h&igrave;nh. Gi&aacute;o vi&ecirc;n hướng dẫn c&aacute;c em c&oacute; một số vật dụng gi&uacute;p thư gi&atilde;n sau giờ học như đ&aacute;nh đ&agrave;n, chơi b&oacute;ng, gấp giấy,...</p>\r\n\r\n<p>Với học sinh kh&oacute; khăn t&acirc;m l&yacute; li&ecirc;n quan đến giao tiếp, gi&aacute;o vi&ecirc;n cần hướng dẫn c&aacute;c em chia sẻ những kh&oacute; khăn, vướng mắc của m&igrave;nh với bạn b&egrave;, người th&acirc;n, thầy c&ocirc; gi&aacute;o hay bất kỳ ai m&agrave; m&igrave;nh thấy tin tưởng.</p>\r\n\r\n<p>Gi&aacute;o vi&ecirc;n phối hợp với phụ huynh thiết lập thời gian sử dụng điện thoại, Internet trong ng&agrave;y hợp l&yacute;. Cố gắng hướng học sinh tăng cường n&oacute;i chuyện, tương t&aacute;c với người th&acirc;n trong gia đ&igrave;nh.</p>\r\n\r\n<p>Khi c&oacute; vấn đề t&acirc;m l&yacute; li&ecirc;n quan đến gia đ&igrave;nh, gi&aacute;o vi&ecirc;n h&atilde;y gợi mở cho học sinh biết c&aacute;ch t&igrave;m đến người m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch để chia sẻ, kh&ocirc;ng n&ecirc;n giữ trong l&ograve;ng v&agrave; chịu đựng một m&igrave;nh; giữ khoảng c&aacute;ch với th&agrave;nh vi&ecirc;n m&agrave; bản th&acirc;n thấy kh&ocirc;ng hợp; t&igrave;m đến c&aacute;c th&uacute; vui của bản th&acirc;n như nghe nhạc, xem phim h&agrave;i,..</p>\r\n\r\n<p>Học sinh cũng kh&ocirc;ng n&ecirc;n t&igrave;m hiểu s&acirc;u, qu&aacute; nhiều về những th&ocirc;ng tin li&ecirc;n quan đến dịch bệnh hay tệ nạn x&atilde; hội, m&agrave; d&agrave;nh thời gian cho c&ocirc;ng việc gia đ&igrave;nh như nấu cơm, dọn rửa nh&agrave; cửa,..</p>\r\n\r\n<p>Gi&aacute;o vi&ecirc;n l&agrave; người trực tiếp ảnh hưởng đến sự h&igrave;nh th&agrave;nh nh&acirc;n c&aacute;ch v&agrave; tiếp thu kiến thức của học sinh. Việc ứng xử đ&uacute;ng c&aacute;ch của thầy/ c&ocirc;, sự quan t&acirc;m, thấu hiểu, chia sẻ của gi&aacute;o vi&ecirc;n trong qu&aacute; tr&igrave;nh dạy học, đặc biệt l&agrave; trong điều kiện dạy học trực tuyến, sẽ gi&uacute;p học sinh vượt qua những cảm x&uacute;c ti&ecirc;u cực, c&acirc;n bằng t&acirc;m l&yacute; v&agrave; ph&aacute;t triển một c&aacute;ch b&igrave;nh thường trong điều kiện dạy học &quot;kh&ocirc;ng b&igrave;nh thường&quot;.</p>\r\n', NULL, NULL),
(4, 2, 'Nhiều địa phương cho học sinh trở lại trường từ đầu tháng 11', 'VHO- TP.HCM và một số địa phương đang ráo riết đẩy nhanh tiến độ sửa chữa, vệ sinh trường lớp để đón học sinh quay lại học trực tiếp. Trước đó, theo hướng dẫn của Bộ GD&ĐT và có ý kiến thống nhất từ Bộ Y tế, các địa bàn được xác định dịch ở cấp độ 1 và cấp độ 2 có thể tổ chức dạy học trực tiếp. Hiện nhiều tỉnh thành trên cả nước đang sẵn sàng các điều kiện đón học sinh ngay từ đầu tháng 11 này.', 'images/anh_giaoduc_4.jpg', '<h4><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46568/87499H%E1%BB%8Dc-sinh-x%C3%A3-%C4%91%E1%BA%A3o-Th%E1%BA%A1nh-An,-huy%E1%BB%87n-C%E1%BA%A7n-Gi%E1%BB%9D,-TP.HCM-%C4%91%C3%A3-tr%E1%BB%9F-l%E1%BA%A1i-tr%C6%B0%E1%BB%9Dng-t%E1%BB%AB-ng%C3%A0y-20.10.jpg\" style=\"height:355px; width:600px\" /></h4>\r\n\r\n<p>&nbsp;<strong>Học sinh x&atilde; đảo Thạnh An, huyện Cần Giờ (TP.HCM) đ&atilde; trở lại trường từ ng&agrave;y 20.10</strong></p>\r\n\r\n<p>&nbsp;<strong>Nhiều trường học b&agrave;n giao lại cho ng&agrave;nh Gi&aacute;o dục</strong></p>\r\n\r\n<p>Tại tỉnh B&igrave;nh Dương, học sinh c&aacute;c địa phương sẽ trở lại trường học trực tiếp v&agrave;o th&aacute;ng 11 sau 2 th&aacute;ng học trực tuyến. Theo dự kiến, c&aacute;c địa b&agrave;n &ldquo;v&ugrave;ng xanh&rdquo; như huyện Dầu Tiếng, Ph&uacute; Gi&aacute;o, B&agrave;u B&agrave;ng, Bắc T&acirc;n Uy&ecirc;n sẽ triển khai trước. Trong khi chờ quyết định ch&iacute;nh thức, c&aacute;c địa phương n&agrave;y đang khẩn trương chuẩn bị trường lớp sạch sẽ, an to&agrave;n đ&oacute;n học sinh trở lại. Trước đ&oacute;, to&agrave;n tỉnh c&oacute; gần 200 trường học c&aacute;c cấp được trưng dụng cho c&ocirc;ng t&aacute;c ph&ograve;ng, chống Covid-19. Tại huyện Dầu Tiếng, 22 trường học c&aacute;c cấp đ&atilde; được b&agrave;n giao lại cho ng&agrave;nh Gi&aacute;o dục từ ng&agrave;y 30.9. L&atilde;nh đạo Ph&ograve;ng GD&amp;ĐT huyện Dầu Tiếng cho hay, chậm nhất đến ng&agrave;y 30.10, tất cả c&aacute;c trường phải được sửa chữa xong để đ&oacute;n học sinh trở lại. Trong khi đ&oacute;, huyện B&agrave;u B&agrave;ng vẫn c&ograve;n 2 &ldquo;v&ugrave;ng v&agrave;ng&rdquo; l&agrave; khu phố B&agrave;u B&agrave;ng v&agrave; Đồng Sổ của thị trấn Lai Uy&ecirc;n. Do đ&oacute;, ng&agrave;nh GD&amp;ĐT huyện đ&atilde; x&acirc;y dựng c&aacute;c phương &aacute;n ph&ugrave; hợp với t&igrave;nh h&igrave;nh thực tế dịch bệnh của địa phương. Đối với huyện Bắc T&acirc;n Uy&ecirc;n, ng&agrave;nh GD&amp;ĐT huyện n&agrave;y cũng đ&atilde; chủ động c&aacute;c bước để đ&aacute;p ứng việc tổ chức dạy học trực tiếp khi c&oacute; quyết định ch&iacute;nh thức của tỉnh&hellip;</p>\r\n\r\n<p>Tại Đ&agrave; Nẵng, học sinh cơ bản sẽ đi học lại từ 1.11. Trước đ&oacute;, TP Đ&agrave; Nẵng tổ chức th&iacute; điểm cho học sinh phổ th&ocirc;ng tr&ecirc;n địa b&agrave;n x&atilde; H&ograve;a Bắc, huyện H&ograve;a Vang đi học từ ng&agrave;y 18.10 v&agrave; học sinh lớp 12 Trường THPT Chuy&ecirc;n L&ecirc; Qu&yacute; Đ&ocirc;n sẽ đi học từ ng&agrave;y 25.10. C&aacute;c cấp học kh&aacute;c tr&ecirc;n địa b&agrave;n to&agrave;n th&agrave;nh phố, về cơ bản sẽ đi học lại từ ng&agrave;y 1.11.</p>\r\n\r\n<p>Tương tự, tỉnh Bạc Li&ecirc;u cũng dự kiến đầu th&aacute;ng 11 học sinh c&aacute;c cấp sẽ trở lại trường học. Theo Gi&aacute;m đốc Sở Gi&aacute;o dục Khoa học v&agrave; C&ocirc;ng nghệ tỉnh Bạc Li&ecirc;u L&acirc;m Thị Sang, tỉnh dự kiến cho học sinh học trực tiếp từ ng&agrave;y 4.10, nhưng thời điểm n&agrave;y c&oacute; nhiều người tự ph&aacute;t về qu&ecirc;, Ban chỉ đạo Ph&ograve;ng, chống dịch Covid-19 trưng dụng c&aacute;c trường học l&agrave;m khu c&aacute;ch ly tập trung n&ecirc;n tạm thời l&ugrave;i thời gian học tập trung theo kế hoạch. Khả năng thời gian học trực tiếp sẽ v&agrave;o đầu th&aacute;ng 11.</p>\r\n\r\n<p>T&iacute;nh đến nay, trong số hơn 700 cơ sở gi&aacute;o dục được trưng dụng l&agrave;m c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch Covid-19 tại TP.HCM, đ&atilde; c&oacute; 234 trường học được b&agrave;n giao về cho ng&agrave;nh Gi&aacute;o dục. Lộ tr&igrave;nh b&agrave;n giao trường học đang được tiếp tục tiến h&agrave;nh, trong đ&oacute; một số địa phương như Quận 3, Quận 8 c&oacute; 100% đơn vị trường học đ&atilde; được b&agrave;n giao lại, c&aacute;c khu vực kh&aacute;c sẽ &ldquo;cuốn chiếu&rdquo; dần để c&aacute;c trường đang tổ chức vệ sinh, sắp xếp trang thiết bị. Trước đ&oacute;, ng&agrave;y 20.10, gần 250 học sinh khối 1, 2, 6, 9 v&agrave; 12 của Trường tiểu học Thạnh An v&agrave; Trường THCS-THPT Thạnh An (Cần Giờ, TP.HCM) đ&atilde; được đi học trở lại.</p>\r\n\r\n<p><strong>Kiểm so&aacute;t được dịch th&igrave; chủ động cho học sinh trở lại trường</strong></p>\r\n\r\n<p>Nhằm đ&aacute;nh gi&aacute; mức độ an to&agrave;n trường học, UBND TP.HCM cũng vừa c&oacute; quyết định ban h&agrave;nh Bộ ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; an to&agrave;n trong ph&ograve;ng, chống dịch Covid-19 đối với cơ sở gi&aacute;o dục. Theo đ&oacute;, trải qua 4 lần tr&igrave;nh điều chỉnh, tiếp thu g&oacute;p &yacute;, Bộ ti&ecirc;u ch&iacute; đ&aacute;nh gi&aacute; an to&agrave;n trường học ch&iacute;nh thức ban h&agrave;nh với 10 ti&ecirc;u ch&iacute; th&agrave;nh phần. Trong đ&oacute;, ở ti&ecirc;u ch&iacute; 1, gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n l&agrave;m việc trực tiếp tại cơ sở gi&aacute;o dục phải c&oacute; m&atilde; QR xanh thể hiện lịch sử ti&ecirc;m chủng. Trường hợp kh&ocirc;ng c&oacute; m&atilde; QR xanh th&igrave; phải xuất tr&igrave;nh giấy tờ như: Người mắc Covid-19 đ&atilde; khỏi bệnh dưới 6 th&aacute;ng; đ&atilde; ti&ecirc;m chủng đủ liều vắc xin; đ&atilde; ti&ecirc;m mũi 1 &iacute;t nhất 14 ng&agrave;y sau ti&ecirc;m. Ở ti&ecirc;u ch&iacute; 2, số lượng trẻ em, gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n tập trung tối đa trong một thời điểm tại cơ sở gi&aacute;o dục phải đ&aacute;p ứng ti&ecirc;u ch&iacute; số nh&oacute;m, lớp v&agrave; số trẻ trong mỗi nh&oacute;m, lớp theo quy định của ng&agrave;nh gi&aacute;o dục&hellip;</p>\r\n\r\n<p>Như vậy, so với dự thảo Bộ ti&ecirc;u ch&iacute; an to&agrave;n trường học lần thứ 4, ti&ecirc;u ch&iacute; về số lượng trẻ em, học sinh tập trung tối đa trong một thời điểm đ&atilde; được điều chỉnh từ 50% theo quy định th&agrave;nh số lượng tối đa học sinh ở mỗi cấp học&hellip; Đối với ti&ecirc;u ch&iacute; thứ 3, khoảng c&aacute;ch trẻ em, học sinh, gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n trong ph&ograve;ng chăm s&oacute;c, gi&aacute;o dục trẻ, ph&ograve;ng l&agrave;m việc đảm bảo từ 1m trở l&ecirc;n v&agrave; khoảng c&aacute;ch trẻ em, học sinh, gi&aacute;o vi&ecirc;n, c&aacute;n bộ, nh&acirc;n vi&ecirc;n ngo&agrave;i ph&ograve;ng l&agrave;m việc từ 2m trở l&ecirc;n được đ&aacute;nh gi&aacute; đạt về y&ecirc;u cầu khoảng c&aacute;ch&hellip; Bậc mầm non khuyến kh&iacute;ch cơ sở gi&aacute;o dục kh&ocirc;ng hoạt động sau 16h30, c&aacute;c trường phổ th&ocirc;ng được khuyến kh&iacute;ch kh&ocirc;ng tổ chức hoạt động nội tr&uacute;.</p>\r\n\r\n<p>Mới đ&acirc;y, Bộ GD&amp;ĐT cũng đ&atilde; c&oacute; hướng dẫn đề nghị c&aacute;c địa phương căn cứ v&agrave;o đ&aacute;nh gi&aacute;, ph&acirc;n loại x&aacute;c định cấp độ dịch theo từng địa b&agrave;n phường/x&atilde;, cấp huyện, cấp tỉnh/th&agrave;nh phố để quyết định tổ chức hoạt động dạy học trực tiếp đối với gi&aacute;o dục mầm non, phổ th&ocirc;ng v&agrave; GDTX cho ph&ugrave; hợp. Nguy&ecirc;n tắc đặt ra l&agrave; nơi n&agrave;o kiểm so&aacute;t được dịch th&igrave; chủ động cho học sinh trở lại trường học tập v&agrave; đảm bảo c&aacute;c biện ph&aacute;p an to&agrave;n. Đồng thời vẫn phải củng cố hạ tầng c&ocirc;ng nghệ th&ocirc;ng tin, trang thiết bị, phương tiện để sẵn s&agrave;ng chuyển sang h&igrave;nh thức dạy học kh&aacute;c khi dịch c&oacute; diễn biến phức tạp l&ecirc;n. Những nơi được x&aacute;c định dịch cấp độ 3 (nguy cơ cao) tổ chức dạy học trực tiếp kết hợp trực tuyến, qua truyền h&igrave;nh.</p>\r\n\r\n<p>C&aacute;c địa phương x&acirc;y dựng phương &aacute;n dạy học cho từng lớp, khối lớp, cấp học phổ th&ocirc;ng. Trong đ&oacute; ưu ti&ecirc;n dạy trực tiếp với lớp 1, lớp 2, lớp 6, lớp 9 v&agrave; lớp 12, đảm bảo gi&atilde;n c&aacute;ch ph&ugrave; hợp với cơ sở vật chất v&agrave; điều kiện ph&ograve;ng chống dịch. Những nơi được x&aacute;c định cấp độ 4 (nguy cơ rất cao), c&aacute;c địa phương căn cứ v&agrave;o t&igrave;nh h&igrave;nh thực tế v&agrave; tiến độ triển khai kế hoạch năm học để tổ chức dạy học trực tuyến, qua truyền h&igrave;nh, giao b&agrave;i tập với cấp học mầm non, phổ th&ocirc;ng&hellip;&nbsp;</p>\r\n', NULL, NULL),
(5, 2, 'Lễ khai giảng đặc biệt của Trường Đại học Văn hóa Hà Nội', 'VHO- Sáng 26.10.2021, Trường Đại học Văn hóa Hà Nội đã trang trọng tổ chức lễ khai giảng năm học mới 2021-2022 dưới hình thức trực tiếp kết hợp trực tuyến trên nền các nền tảng mạng xã hội: Youtube, Facebook và MS Teams. Do ảnh hưởng của đại dịch Covid-19, năm nay, các sinh viên, cựu sinh viên và học viên Trường Đại học Văn hóa Hà Nội ở khắp các tỉnh thành đã không thể trực tiếp tham dự buổi lễ mà chỉ được nghe tiếng trống khai giảng qua các nền tảng số. Đây là lễ khai giảng đặc biệt nhất từ trư', 'images/anh_giaoduc_5.jpg', '<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46682/87768z2878069295394_eb4c08b7a23635012234d4813ca1375d.jpg\" style=\"height:399px; width:599px\" /></p>\r\n\r\n<p><strong>PGS.TS&nbsp;Phạm Thị Thu Hương, Hiệu trưởng Trường Đại học Văn h&oacute;a H&agrave; Nội đ&aacute;nh trống khai giảng năm học mới</strong></p>\r\n\r\n<p>Ch&agrave;o mừng 36 t&acirc;n học vi&ecirc;n cao học kh&oacute;a 2021-2023&nbsp;v&agrave; 1589 t&acirc;n sinh vi&ecirc;n K62, PGS.TS Phạm Thị Thu Hương, Hiệu trưởng Trường Đại học Văn h&oacute;a H&agrave; Nội chia sẻ, năm học 2021-2022, đ&ocirc;ng đảo th&iacute; sinh đ&atilde; chọn Trường Đại học Văn h&oacute;a H&agrave; Nội l&agrave;m nơi học tập, nghi&ecirc;n cứu v&agrave; r&egrave;n luyện. Kết quả tuyển sinh phần n&agrave;o thể hiện niềm tin m&agrave; x&atilde; hội v&agrave; gia đ&igrave;nh c&aacute;c th&iacute; sinh đ&atilde; đặt v&agrave;o nh&agrave; trường, một địa chỉ đ&agrave;o tạo uy t&iacute;n v&agrave; chất lượng. &ldquo;Điều đ&oacute; đặt ra tr&aacute;ch nhiệm nặng nề với với tập thể l&atilde;nh đạo, giảng vi&ecirc;n v&agrave; vi&ecirc;n chức nh&agrave; trường trong việc x&acirc;y dựng m&ocirc;i trường học tập, nghi&ecirc;n cứu khoa học, r&egrave;n luyện tốt để đ&aacute;p ứng y&ecirc;u cầu x&atilde; hội v&agrave; tạo th&ecirc;m niềm tin y&ecirc;u của sinh vi&ecirc;n...&rdquo;, PGS.TS Phạm Thị Thu Hương nhấn mạnh.</p>\r\n\r\n<p>Hiệu trưởng nh&agrave; trường nhắn nhủ c&aacute;c c&aacute;n bộ, giảng vi&ecirc;n, vi&ecirc;n chức l&agrave;m hết sức m&igrave;nh v&igrave; chất lượng đ&agrave;o tạo của nh&agrave; trường. &ldquo;H&atilde;y gi&aacute;o dục c&aacute;c em bằng cả tr&aacute;i tim, t&igrave;nh thương y&ecirc;u, bằng những h&agrave;nh động cụ thể. H&atilde;y đặt m&igrave;nh v&agrave;o vai tr&ograve; người cha, người mẹ, người anh, người chị của c&aacute;c em. H&atilde;y xem sự trưởng th&agrave;nh của c&aacute;c em l&agrave; niềm vui, hạnh ph&uacute;c của ch&uacute;ng ta. Cơ sở vật chất c&ograve;n ngh&egrave;o n&agrave;n, ch&uacute;ng ta sẽ dần khắc phục, nhưng b&agrave;i giảng ngh&egrave;o n&agrave;n, nh&acirc;n vi&ecirc;n phục vụ v&ocirc; cảm th&igrave; thật đ&aacute;ng buồn...&rdquo;, b&agrave; Hương bộc bạch.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46682/87770z2878374655168_a2283e7a8c9cb41d7e20c6a8b4162f7e.jpg\" style=\"height:399px; width:599px\" /></p>\r\n\r\n<p><strong>PGS.TS L&ecirc; Anh Tuấn, Vụ trưởng Vụ Đ&agrave;o tạo (Bộ VHTTDL) ph&aacute;t biểu tại lễ khai giảng</strong></p>\r\n\r\n<p>Hiệu trưởng cho biết, năm học 2021- 2022, Trường Đại học Văn h&oacute;a H&agrave; Nội sẽ tập trung thực hiện c&aacute;c nhiệm vụ v&agrave; giải ph&aacute;p trọng t&acirc;m như: đẩy mạnh chuyển đổi số trong gi&aacute;o dục đ&agrave;o tạo; x&acirc;y dựng m&ocirc;i trường l&agrave;m việc, giảng dạy v&agrave; học tập t&iacute;ch cực; tăng cường gi&aacute;o dục đạo đức, lối sống song song với giảng dạy kiến thức, kỹ năng nghề nghiệp. Tiếp tục kiện to&agrave;n, n&acirc;ng cao chất lượng đội ngũ giảng vi&ecirc;n đ&aacute;p ứng y&ecirc;u cầu quản trị đại học, ph&ugrave; hợp với định hướng ph&aacute;t triển trong điều kiện tự chủ đại học v&agrave; hội nhập quốc tế. Đẩy mạnh nghi&ecirc;n cứu khoa học dưới nhiều h&igrave;nh thức. Ho&agrave;n th&agrave;nh kế hoạch kiểm định chất lượng gi&aacute;o dục nh&agrave; trường v&agrave; chuẩn bị điều kiện để thực hiện kiểm định một số chương tr&igrave;nh đ&agrave;o tạo...</p>\r\n\r\n<p>Cũng theo PGS.TS Phạm Thị Thu Hương, năm học 2020-2021 vừa qua đ&atilde; diễn ra trong bối cảnh đầy kh&oacute; khăn, th&aacute;ch thức do những diễn biến phức tạp của dịch bệnh Covid-19. Nh&agrave; trường buộc phải chuyển trạng th&aacute;i từ l&agrave;m việc, giảng dạy, học tập trực tiếp sang h&igrave;nh thức online&nbsp; v&agrave; linh hoạt &aacute;p dụng nhiều h&igrave;nh thức kh&aacute;c, ph&ugrave; hợp với điều kiện c&ocirc;ng t&aacute;c từng đơn vị. Với sự nỗ lực, nh&agrave; trường đ&atilde; đạt được những th&agrave;nh tựu quan trọng về nhiều mặt; thực hiện tốt mục ti&ecirc;u k&eacute;p, vừa t&iacute;ch cực thực hiện c&aacute;c giải ph&aacute;p ph&ograve;ng, chống dịch, bảo đảm an to&agrave;n trường học; vừa ra sức phấn đấu khắc phục kh&oacute; khăn, ho&agrave;n th&agrave;nh nhiệm vụ năm học, đ&aacute;p ứng y&ecirc;u cầu đổi mới v&agrave; bảo đảm chất lượng gi&aacute;o dục.</p>\r\n\r\n<p>&ldquo;Để ứng ph&oacute; với đại dịch Covid- 19, nh&agrave; trường tăng cường hoạt động chuyển đổi số trong tuyển sinh v&agrave; đ&agrave;o tạo. Trong 62 năm x&acirc;y dựng v&agrave; ph&aacute;t triển, đ&acirc;y l&agrave; lần đầu ti&ecirc;n nh&agrave; trường tiếp nhận hồ sơ tuyển sinh trực tuyến, tuyển sinh năng khiếu trực tuyến, nhập học trực tuyến, giảng dạy trực tuyến, tổ chức thi kết th&uacute;c học kỳ trực tuyến, bảo vệ kh&oacute;a luận, luận văn v&agrave; luận &aacute;n trực tuyến...&rdquo;, b&agrave; Hương cho biết.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46682/87767z2878069214183_2cf2293e7e911f7784c4c4402a2d9cd1.jpg\" style=\"height:399px; width:599px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>PGS.TS Phạm Thị Thu Hương ph&aacute;t biểu tại lễ khai giảng đặc biệt của nh&agrave; trường</strong></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, hoạt động khoa học; c&ocirc;ng t&aacute;c ch&iacute;nh trị, tư tưởng&nbsp; của nh&agrave; trường tiếp tục đạt được những th&agrave;nh quả đ&aacute;ng ghi nhận. Trong bối cảnh gi&aacute;o dục đại học cạnh tranh ng&agrave;y c&agrave;ng cao, &nbsp;việc chuẩn h&oacute;a chương tr&igrave;nh, nội dung đ&agrave;o tạo, n&acirc;ng cao chất lượng đội ngũ giảng vi&ecirc;n v&agrave; hội nhập quốc tế s&acirc;u rộng được Trường Đại học Văn h&oacute;a x&aacute;c định l&agrave; giải ph&aacute;p then chốt.</p>\r\n\r\n<p>Ch&uacute;c mừng những th&agrave;nh t&iacute;ch nh&agrave; trường&nbsp;đạt được trong năm học vừa qua, PGS.TS L&ecirc; Anh Tuấn, Vụ trưởng Vụ Đ&agrave;o tạo (Bộ VHTTDL) ghi nhận, Trường Đại học Văn h&oacute;a H&agrave; Nội l&agrave; một trong những trường Đại học lớn, trọng điểm của Bộ VHTTDL-&nbsp;nơi đ&agrave;o tạo nguồn nh&acirc;n lực v&agrave; nghi&ecirc;n cứu khoa học chất lượng cao trong lĩnh vực văn h&oacute;a nghệ thuật v&agrave; du lịch ở Việt Nam. Năm học 2020-2021, mặc d&ugrave; chịu nhiều ảnh hưởng của đại dịch Covid-19 song&nbsp;nh&agrave; trường&nbsp; đ&atilde; nỗ lực vượt kh&oacute;, đạt được nhiều th&agrave;nh quả trong c&ocirc;ng t&aacute;c đ&agrave;o tạo,&nbsp;nghi&ecirc;n cứu khoa học;&nbsp;c&ocirc;ng t&aacute;c gi&aacute;o dục ch&iacute;nh trị, đạo đức, lối sống cho sinh vi&ecirc;n được ch&uacute; trọng... Đặc biệt, c&ocirc;ng t&aacute;c tuyển sinh đại học năm 2021 đ&atilde; đạt tr&ecirc;n 100% chỉ ti&ecirc;u với chất lượng đầu v&agrave;o&nbsp; cao.</p>\r\n\r\n<p>Với sứ mệnh đ&agrave;o tạo nguồn nh&acirc;n lực văn h&oacute;a nghệ thuật v&agrave; du lịch chất lượng cao cho đất nước, th&iacute;ch ứng với bối cảnh mới, theo PGS.TS L&ecirc; Anh Tuấn, trong năm học mới 2021-2022, Trường Đại học Văn h&oacute;a cần tiếp tục tập trung c&aacute;c điều kiện để n&acirc;ng cao chất lượng đội ngũ giảng vi&ecirc;n v&agrave; c&aacute;n bộ quản l&yacute; của nh&agrave; trường, ch&uacute; trọng đội ngũ giảng vi&ecirc;n, nghi&ecirc;n cứu vi&ecirc;n, c&aacute;n bộ quản l&yacute; kế cận. Đổi&nbsp; mới c&ocirc;ng t&aacute;c đ&agrave;o tạo, tăng cường chuyển đổi số trong tổ chức đ&agrave;o tạo, th&iacute;ch ứng với bối cảnh c&aacute;ch mạng c&ocirc;ng nghệ 4.0 v&agrave; bối cảnh dịch bệnh Covid-19.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46682/87769z2878069385366_709b833011b2aa2595584b8c8f60bdd3.jpg\" style=\"height:399px; width:599px\" /></p>\r\n\r\n<p><strong>C&aacute;c đại biểu dự lễ khai giảng tại Trường Đại học Văn h&oacute;a H&agrave; Nội</strong></p>\r\n\r\n<p>Cũng theo Vụ trưởng L&ecirc; Anh Tuấn, Đại học Văn h&oacute;a H&agrave; Nội cần triển khai nhanh c&ocirc;ng t&aacute;c kiểm định chất lượng gi&aacute;o dục đại học, đảm bảo sớm c&oacute; giấy chứng nhận kiểm định chất lượng&nbsp; gi&aacute;o dục đại học, tiến tới kiểm định chất lượng chương tr&igrave;nh đ&agrave;o tạo để khẳng định&nbsp; uy t&iacute;n v&agrave; thương hiệu của nh&agrave; trường. Đẩy mạnh thực hiện&nbsp; tự chủ đại học, ph&aacute;t huy tối đa sức mạnh nội lực v&agrave; huy động tốt nhất c&aacute;c nguồn lực x&atilde; hội, đưa nh&agrave; trường ph&aacute;t triển mạnh mẽ, bền vững.</p>\r\n\r\n<p>&ldquo;Thực hiện hiệu quả Đề &aacute;n của ng&agrave;nh VHTTDL đ&atilde; được Thủ tướng Ch&iacute;nh phủ ph&ecirc; duyệt, trong đ&oacute;, ch&uacute; trọng c&ocirc;ng t&aacute;c đ&agrave;o tạo t&agrave;i năng lĩnh vực văn h&oacute;a v&agrave; chuẩn bị c&aacute;c điều kiện về chuy&ecirc;n m&ocirc;n v&agrave; ngoại ngữ, c&oacute; kế hoạch cụ thể cử sinh vi&ecirc;n, giảng vi&ecirc;n đi đ&agrave;o tạo ở nước ngo&agrave;i&rdquo;, Vụ trưởng L&ecirc; Anh Tuấn ph&aacute;t biểu.</p>\r\n\r\n<p>Tại lễ khai giảng, Trường Đại học Văn h&oacute;a H&agrave; Nội đ&atilde; trang trọng c&ocirc;ng bố c&aacute;c quyết định c&ocirc;ng nhận v&agrave; ch&agrave;o mừng 36 t&acirc;n học vi&ecirc;n cao học&nbsp;kh&oacute;a 2021-2023;&nbsp;1589 t&acirc;n sinh vi&ecirc;n K62. Buổi lễ cũng c&ocirc;ng bố danh s&aacute;ch khen thưởng c&aacute;c t&acirc;n sinh vi&ecirc;n đạt điểm xuất sắc trong kỳ thi tuyển sinh vừa qua.</p>\r\n', NULL, NULL),
(11, 4, 'Hà Nội sẵn sàng kích hoạt 2.500 điểm bán hàng lưu động phục vụ chống dịch', 'VHO- Trên địa bàn Hà Nội có 8.216 điểm bán hàng bình ổn giá được niêm yết công khai để phục vụ nhân dân và sẵn sàng kích hoạt 2.500 điểm bán hàng lưu động để phục vụ công tác phòng, chống Covid-19.', 'images/anh_kinhte_11.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/43622/81025bigc.jpg\" /></p>\r\n\r\n<p><strong>C&aacute;c si&ecirc;u thị tăng nguồn cung dự trữ h&agrave;ng h&oacute;a, đảm bảo b&igrave;nh ổn thị trường</strong></p>\r\n\r\n<p>Trước diễn biến phức tạp của dịch Covid-19, th&agrave;nh phố H&agrave; Nội đ&atilde; chỉ đạo c&aacute;c sở, ng&agrave;nh, quận, huyện x&acirc;y dựng c&aacute;c phương &aacute;n đảm bảo cung ứng h&agrave;ng h&oacute;a phục vụ nh&acirc;n d&acirc;n Thủ đ&ocirc;.</p>\r\n\r\n<p>T&iacute;nh đến nay, tr&ecirc;n địa b&agrave;n th&agrave;nh phố c&oacute; 8.216 điểm b&aacute;n h&agrave;ng b&igrave;nh ổn gi&aacute; được Sở C&ocirc;ng Thương ni&ecirc;m yết c&ocirc;ng khai để phục vụ nh&acirc;n d&acirc;n v&agrave; sẵn s&agrave;ng k&iacute;ch hoạt 2.500 điểm b&aacute;n h&agrave;ng lưu động do c&aacute;c quận, huyện, thị x&atilde; bố tr&iacute;.</p>\r\n\r\n<p>C&aacute;c doanh nghiệp, c&aacute;c địa phương đ&atilde; tăng cường mở th&ecirc;m c&aacute;c điểm b&aacute;n, đơn cử hệ thống VinShop đ&atilde; mở đưa v&agrave;o hoạt động 800 điểm b&aacute;n h&agrave;ng thiết yếu tr&ecirc;n 30 quận, huyện, thị x&atilde;. Ngo&agrave;i ra, Sở C&ocirc;ng Thương cũng phối hợp với Bưu điện H&agrave; Nội để tiếp tục mở th&ecirc;m 472 điểm b&aacute;n h&agrave;ng thiết yếu.</p>\r\n\r\n<p>Hiện nay, c&aacute;c quận, huyện, thị x&atilde; cũng x&acirc;y dựng phương &aacute;n tổ chức triển khai điểm b&aacute;n h&agrave;ng trong t&igrave;nh huống dịch Covid-19; trong đ&oacute; mỗi phường, x&atilde; &iacute;t nhất tổ chức th&ecirc;m 1 điểm b&aacute;n, những nơi chưa c&oacute; chợ tổ chức tối thiểu từ 2 điểm b&aacute;n trở l&ecirc;n; đồng thời r&agrave; so&aacute;t c&aacute;c chợ đang hoạt động chưa hết c&ocirc;ng suất&hellip; đối với c&aacute;c địa phương ở c&aacute;c cửa ng&otilde; ra v&agrave;o thủ đ&ocirc; để gi&uacute;p th&agrave;nh phố l&agrave;m nơi trung chuyển h&agrave;ng h&oacute;a, hỗ trợ cho c&aacute;c chợ đầu mối đang dừng hoạt động.</p>\r\n\r\n<p>&quot;Nhờ chuẩn bị tốt, n&ecirc;n mặc d&ugrave; c&oacute; một số chợ, si&ecirc;u thị, cửa h&agrave;ng tiện lợi bị đ&oacute;ng cửa do Covid-19 song h&agrave;ng h&oacute;a dồi d&agrave;o, gi&aacute; cả cơ bản ổn định, người d&acirc;n mua b&aacute;n thuận tiện, đảm bảo đ&aacute;p ứng đầy đủ nhu cầu nh&acirc;n d&acirc;n,&quot; đại diện Sở C&ocirc;ng Thương th&ocirc;ng tin.</p>\r\n\r\n<p>C&ugrave;ng với đ&oacute;, l&atilde;nh đạo th&agrave;nh phố cũng y&ecirc;u cầu c&aacute;c đơn vị r&agrave; so&aacute;t lại c&aacute;c v&ugrave;ng trồng, chăn nu&ocirc;i gia s&uacute;c, gia cầm tr&ecirc;n địa b&agrave;n th&agrave;nh phố để cơ cấu tổ chức lại sản xuất, chăn nu&ocirc;i, nhất l&agrave; mặt h&agrave;ng rau ăn l&aacute;, củ, quả, trứng gia cầm&hellip; ph&ugrave; hợp nhu cầu ti&ecirc;u d&ugrave;ng người d&acirc;n trong ph&ograve;ng chống dịch nhằm đảm bảo nguồn tự cung cao nhất cho H&agrave; Nội.</p>\r\n\r\n<p>Đối với ng&agrave;nh c&ocirc;ng thương, th&agrave;nh phố đề nghị tiếp tục kết nối với c&aacute;c tỉnh, th&agrave;nh phố cung cấp h&agrave;ng h&oacute;a cho H&agrave; Nội qua c&aacute;c k&ecirc;nh ph&acirc;n phối nhằm c&acirc;n đối cung-cầu, đảm bảo đủ nhu cầu phục vụ nh&acirc;n d&acirc;n tr&ecirc;n địa b&agrave;n.</p>\r\n\r\n<p>&quot;Sở C&ocirc;ng Thương đ&atilde; phối hợp với c&aacute;c quận, huyện, thị x&atilde; r&agrave; so&aacute;t lại nguồn cung h&agrave;ng h&oacute;a tr&ecirc;n địa b&agrave;n, nắm nhu cầu ti&ecirc;u thụ n&ocirc;ng sản thực phẩm của người d&acirc;n để chỉ đạo c&aacute;c hệ thống ph&acirc;n phối thu mua gi&uacute;p người d&acirc;n hạn chế đi lại, đảm bảo c&ocirc;ng t&aacute;c ph&ograve;ng chống dịch tr&ecirc;n địa b&agrave;n v&agrave; đảm bảo tăng nguồn cung h&agrave;ng h&oacute;a cho th&agrave;nh phố,&quot; đại diện Sở C&ocirc;ng Thương H&agrave; Nội cho hay.</p>\r\n', NULL, NULL),
(12, 4, 'Hà Nội sẽ xử lý nghiêm việc găm hàng, thổi giá thực phẩm, rau quả', 'VHO- Một số chợ dân sinh trên địa bàn thành phố Hà Nội có hiện tượng tiểu thương đẩy giá thực phẩm tươi sống, rau xanh lên cao sau khi một số hệ thống siêu thị như BRGMart, VinMart... bị phong tỏa.', 'images/anh_kinhte_12.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t1000/uploaded/pcfo/2021_08_05/ttxvn_0508_cho_dan_sinh.jpg\" /></p>\r\n\r\n<p><strong>Tại c&aacute;c chợ d&acirc;n sinh, lượng h&agrave;ng h&oacute;a dồi d&agrave;o, đảm bảo cung ứng đủ</strong></p>\r\n\r\n<p>Trước t&igrave;nh h&igrave;nh một số chợ truyền thống, chợ đầu mối, hệ thống si&ecirc;u thị BRGMart, Minimart Haprofood/BRGMart, một số si&ecirc;u thị VinMart, VinMart+ bị phong tỏa, tạm thời đ&oacute;ng cửa để đảm bảo ph&ograve;ng chống dịch, n&ecirc;n một số chợ d&acirc;n sinh c&oacute; hiện tượng tiểu thương đẩy gi&aacute; thực phẩm tươi sống, rau xanh l&ecirc;n cao.</p>\r\n\r\n<p>Theo Sở C&ocirc;ng Thương th&agrave;nh phố H&agrave; Nội, lực lượng chức năng sẽ tăng cường kiểm tra v&agrave; xử l&yacute; nghi&ecirc;m c&aacute;c trường hợp găm h&agrave;ng thổi gi&aacute;.</p>\r\n\r\n<p>Hiện qua kiểm tra tr&ecirc;n thị trường c&aacute;c mặt h&agrave;ng thiết yếu, lương thực thực phẩm phẩm, rau xanh, củ quả, thịt lợn, b&ograve;, gia s&uacute;c gia cầm, thủy hải sản h&agrave;ng h&oacute;a vẫn dồi d&agrave;o.</p>\r\n\r\n<p>Do c&oacute; sự chuẩn bị từ trước để ứng ph&oacute; với c&aacute;c t&igrave;nh huống dịch xảy ra tr&ecirc;n diện rộng n&ecirc;n H&agrave; Nội đ&atilde; c&oacute; dự trữ h&agrave;ng h&oacute;a với số lượng lớn gấp 3 lần so với b&igrave;nh thường. C&ugrave;ng với đ&oacute; nguồn cung ổn định, phong ph&uacute; n&ecirc;n người d&acirc;n y&ecirc;n t&acirc;m kh&ocirc;ng c&oacute; t&igrave;nh trạng thiếu h&agrave;ng h&oacute;a.</p>\r\n\r\n<p>Qua khảo s&aacute;t tại một số chợ d&acirc;n sinh, chợ truyền thống như Nguyễn C&ocirc;ng Trứ, Chợ H&ocirc;m Đức Vi&ecirc;n, Th&agrave;nh C&ocirc;ng, Kim Li&ecirc;n, Gốc Đề... cho thấy gi&aacute; cả c&aacute;c mặt h&agrave;ng như rau xanh, củ quả, thịt lợn, thịt b&ograve;, thịt gia s&uacute;c gia cầm, thủy hải sản... rất dồi d&agrave;o, người d&acirc;n mua b&aacute;n thuận lợi.</p>\r\n\r\n<p>Cụ thể, tại chợ một số chợ tr&ecirc;n địa b&agrave;n quận Ho&agrave;n Kiếm, gi&aacute; thịt lợn phổ biến ở mức từ 120.000-150.000 đồng/kg t&ugrave;y loại; gi&aacute; thịt g&agrave; ta (nguy&ecirc;n l&ocirc;ng) 120.000 đồng/kg; gi&aacute; lạc từ 80.000-120.000 đồng/kg t&ugrave;y loại (c&oacute; tăng so với trước đ&oacute;); rau muống 15.000 đồng/mớ; b&iacute; xanh 20.000 đồng/kg; trứng g&agrave; 50.000 đồng/chục.</p>\r\n\r\n<p>C&ograve;n tại khu vực Thanh Xu&acirc;n, thịt lợn vai gi&aacute; 150.000 đồng/kg, rau muốn 10.000 đồng/mớ; khoai t&acirc;y 20.000 đồng/kg; bắp cải 20.000 đồng/kg; củ cải 12.000 đồng/kg; m&ugrave;ng tơi 5.000 đồng/mớ; chanh 15.000-20.000 đồng/kg; cam 35.000 đồng/kg; thanh long quả nhỏ 20.000 đồng/kg...</p>\r\n\r\n<p>Tại khu vực Ho&agrave;ng Mai, gi&aacute; thịt b&ograve; phổ biến từ 230.000-280.000 đồng/kg t&ugrave;y loại, rau muống 15.000 đồng/mớ, chanh 20.000 đồng/kg, gừng 25.000 đồng/kg, sả 15.000 đồng/kg. Nguồn cung h&agrave;ng h&oacute;a thực phẩm dồi d&agrave;o, tuy nhi&ecirc;n, gi&aacute; rau xanh c&oacute; tăng nhẹ.</p>\r\n\r\n<p>C&ograve;n về ph&iacute;a c&aacute;c si&ecirc;u thị c&oacute; li&ecirc;n quan đến ca F0 của C&ocirc;ng ty cung ứng thực phẩm Thanh Nga cũng đ&atilde; tổ chức cho phun khử khuẩn b&ecirc;n trong v&agrave; ngo&agrave;i si&ecirc;u thị, nhất l&agrave; c&aacute;c khu vực giao h&agrave;ng.</p>\r\n\r\n<p>Cụ thể, tại si&ecirc;u thị BRGMart 275 Nguyễn Tr&atilde;i, sau khi thực hiện phun khử khuẩn si&ecirc;u thị, BRGMart đ&atilde; cử nh&acirc;n vi&ecirc;n vận h&agrave;nh mới thay thế cho c&aacute;c trường hợp nh&acirc;n vi&ecirc;n đang c&aacute;ch ly theo quyết định của cơ quan chức năng để tiếp tục mở cửa phục vụ nhu cầu mua sắm của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>Đại diện hệ thống si&ecirc;u thị BRGMart cho biết bằng những biện ph&aacute;p ph&ograve;ng dịch quyết liệt, hệ thống si&ecirc;u thị BRGMart cam kết tối đa đảm bảo nguồn cung h&agrave;ng h&oacute;a thiết yếu phục vụ người ti&ecirc;u d&ugrave;ng, đảm bảo an to&agrave;n chống dịch cho kh&aacute;ch h&agrave;ng cũng như an to&agrave;n v&agrave; sức khỏe cho to&agrave;n bộ nh&acirc;n vi&ecirc;n trong c&ocirc;ng ty.</p>\r\n\r\n<p>Trước những diễn biến ng&agrave;y c&agrave;ng phức tạp của dịch Covid-19, để bảo vệ an to&agrave;n cho nh&acirc;n vi&ecirc;n, kh&aacute;ch h&agrave;ng, tr&aacute;nh đứt g&atilde;y chuỗi cung ứng, BRGMart đ&atilde; thực hiện những biện ph&aacute;p ph&ograve;ng dịch quyết liệt hơn. Đ&oacute; l&agrave; li&ecirc;n tục nhắc nhở c&aacute;c Si&ecirc;u thị BRGMart v&agrave; Minimart Haprofood/BRGMart nghi&ecirc;m t&uacute;c thực c&aacute;c biện ph&aacute;p ph&ograve;ng dịch đối với nh&acirc;n vi&ecirc;n, kh&aacute;ch h&agrave;ng v&agrave; nh&agrave; cung cấp như qu&eacute;t QRCode trước khi v&agrave;o si&ecirc;u thị, y&ecirc;u cầu k&ecirc; khai y tế, đo th&acirc;n nhiệt, xịt khuẩn tay, đeo khẩu trang...</p>\r\n\r\n<p>Duy tr&igrave; phun khử khuẩn định kỳ 1 tuần/lần đối với tất cả si&ecirc;u thị, minimart. H&agrave;ng ng&agrave;y y&ecirc;u cầu nh&acirc;n vi&ecirc;n sử dụng cồn 70 độ lau những nơi c&oacute; nguy cơ nhiễm khuẩn cao như tay nắm cửa ra v&agrave;o, quầy thu ng&acirc;n, khu vực b&aacute;n h&agrave;ng thực phẩm...</p>\r\n', NULL, NULL);
INSERT INTO `tinbai` (`id`, `idTheLoai`, `TieuDe`, `TrichDan`, `anhtrichdan`, `NoiDung`, `NgayDang`, `TacGia`) VALUES
(13, 4, 'Đà Nẵng: Quy hoạch nghề cá theo hướng thương mại dịch vụ kết hợp du lịch', 'VHO- UBND thành phố Đà Nẵng vừa có báo cáo về tiến độ triển khai dự án nâng cấp cảng cá Thọ Quang và quy hoạch trung tâm nghề cá lớn thành phố Đà Nẵng theo hướng trung tâm thương mại dịch vụ về nghề cá kết hợp với tham quan du lịch.', 'images/anh_kinhte_13.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/43708/81223IMG_8627.JPG\" /></p>\r\n\r\n<p><strong>&Acirc;u thuyền Thọ Quang</strong></p>\r\n\r\n<p>Trước&nbsp;đ&oacute;, UBND th&agrave;nh phố đ&atilde; ban h&agrave;nh Quyết định số 1796/QĐ-UBND ng&agrave;y 4.4.2017 về việc ph&ecirc; duyệt tổng mặt bằng quy hoạch x&acirc;y dựng chi tiết tỷ lệ 1/500 cảng c&aacute; động lực thuộc trung t&acirc;m nghề c&aacute; lớn th&agrave;nh phố Đ&agrave; Nẵng.</p>\r\n\r\n<p>Dự &aacute;n n&acirc;ng cấp, mở rộng cảng c&aacute; Thọ Quang đ&atilde; ph&ecirc; duyệt tổng mặt bằng quy hoạch x&acirc;y dựng chi tiết tỷ lệ 1/500 theo quyết định của UBND th&agrave;nh phố, do Sở N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n l&agrave;m chủ đầu tư, Ban quản l&yacute; dự &aacute;n đầu tư x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh n&ocirc;ng nghiệp v&agrave; ph&aacute;t triển n&ocirc;ng th&ocirc;n điều h&agrave;nh dự &aacute;n.</p>\r\n\r\n<p>Dự &aacute;n c&oacute; 3 nh&oacute;m hạng mục ch&iacute;nh gồm cải tạo (g&oacute;i thầu x&acirc;y lắp số 12): thi c&ocirc;ng x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh tr&ecirc;n bờ khu n&acirc;ng cấp (bao gồm: san lấp tạo b&atilde;i; đường b&atilde;i s&acirc;n vườn của cảng; nh&agrave; tiếp nhận v&agrave; ph&acirc;n loại thủy sản; nh&agrave; c&ocirc;ng vụ nối với chợ c&aacute; hiện c&oacute;; hệ thống cấp nước nối với hệ thống hiện c&oacute;; hệ thống cấp điện nối với hệ thống hiện c&oacute;; hệ thống tho&aacute;t nước, nước thải nối với hệ thống hiện c&oacute;); hạng mục n&acirc;ng cấp (g&oacute;i thầu x&acirc;y lắp số 11): thi c&ocirc;ng x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh thủy c&ocirc;ng &nbsp;khu n&acirc;ng cấp (bao gồm: bến cập t&agrave;u 600CV, tường chắn, phao b&aacute;o hiệu) v&agrave; hạng mục mở rộng (gồm g&oacute;i thầu x&acirc;y lắp số 9 v&agrave; số 10).</p>\r\n\r\n<p>Vốn bố tr&iacute; năm 2021 gồm vốn ng&acirc;n s&aacute;ch Trung ương l&agrave; 53,7 tỷ đồng (bao gồm chuyển nguồn 3,7 tỷ đồng) v&agrave; vốn ng&acirc;n s&aacute;ch địa phương l&agrave; 42,65 tỷ đồng (bao gồm chuyển nguồn 32,65 tỷ đồng).</p>\r\n', NULL, NULL),
(14, 4, 'Đề xuất lao động ngành thực phẩm tươi sống được lưu thông sau 18h', 'VHO- Tổ công tác đặc biệt Bộ Công Thương kiến nghị xây dựng phương án ưu tiên cho một số lao động trong ngành thực phẩm tươi sống (sơ chế, chế biến, vận chuyển) được phép lưu thông trên đường sau 18h.', 'images/anh_kinhte_14.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t1000/uploaded/fsmsy/2021_08_07/ttxvn_sieu_thi.jpg\" /></p>\r\n\r\n<p><strong>Một si&ecirc;u thị ở Th&agrave;nh phố Hồ Ch&iacute; Minh. (Nguồn: TTXVN)</strong></p>\r\n\r\n<p>Ưu ti&ecirc;n một số lao động ng&agrave;nh thực phẩm tươi sống được ph&eacute;p lưu th&ocirc;ng sau 18h l&agrave; một trong những kiến nghị của Tổ c&ocirc;ng t&aacute;c đặc biệt Bộ C&ocirc;ng Thương về đảm bảo nguồn cung h&agrave;ng h&oacute;a v&agrave; hỗ trợ duy tr&igrave; hoạt động sản xuất, kinh doanh cho c&aacute;c tỉnh, th&agrave;nh phố ph&iacute;a Nam trong bối cảnh dịch bệnh Covid-19 diễn biến phức tạp.</p>\r\n\r\n<p>Theo Tổ c&ocirc;ng t&aacute;c đặc biệt Bộ C&ocirc;ng Thương, để đảm bảo c&aacute;c y&ecirc;u cầu ph&ograve;ng chống dịch, một số địa phương khu vực ph&iacute;a Nam y&ecirc;u cầu người d&acirc;n kh&ocirc;ng ra khỏi nh&agrave; từ 18h h&ocirc;m trước đến 6h s&aacute;ng h&ocirc;m sau nếu kh&ocirc;ng c&oacute; việc cần thiết.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; y&ecirc;u cầu hợp l&yacute; v&agrave; cấp thiết trong bối cảnh dịch Covid-19 diễn biến phức tạp, song theo phản &aacute;nh của một số đơn vị b&aacute;n lẻ, doanh nghiệp vận chuyển mặt h&agrave;ng tươi sống, y&ecirc;u cầu n&agrave;y phần n&agrave;o g&acirc;y ảnh hưởng đến việc vận chuyển h&agrave;ng h&oacute;a tươi sống đến với người ti&ecirc;u d&ugrave;ng.</p>\r\n\r\n<p>Tại buổi l&agrave;m việc trực tuyến giữa Tổ c&ocirc;ng t&aacute;c đặc biệt Bộ C&ocirc;ng Thương với một số doanh nghiệp ph&acirc;n phối lớn, đại diện c&aacute;c đơn vị Saigon Co.op, Central Retail, Aeon Việt Nam, MM Mega Market đ&atilde; ghi nhận sự đồng h&agrave;nh, hỗ trợ kịp của Bộ C&ocirc;ng Thương với doanh nghiệp. B&ecirc;n cạnh đ&oacute;, c&aacute;c doanh nghiệp cũng n&ecirc;u một số kh&oacute; khăn hiện tại v&agrave; đề xuất kiến nghị.</p>\r\n\r\n<p>Theo Tổ c&ocirc;ng t&aacute;c đặc biệt Bộ C&ocirc;ng Thương, c&aacute;c vấn đề được c&aacute;c doanh nghiệp ph&acirc;n phối đề xuất như: ti&ecirc;m vaccine cho lao động, thiếu nh&acirc;n lực l&agrave;m việc, kh&oacute; khăn trong lưu th&ocirc;ng h&agrave;ng h&oacute;a, hướng dẫn mở lại chợ&hellip; đều được Bộ C&ocirc;ng Thương c&oacute; văn bản đề xuất kiến nghị l&ecirc;n c&aacute;c bộ ng&agrave;nh, Ch&iacute;nh phủ để kịp thời xử l&yacute;.</p>\r\n\r\n<p>Tổ c&ocirc;ng t&aacute;c cũng đề nghị Hiệp hội B&aacute;n lẻ thường xuy&ecirc;n cập nhật v&agrave; phổ biến c&aacute;c văn bản li&ecirc;n quan cho c&aacute;c th&agrave;nh vi&ecirc;n Hiệp hội để nắm được th&ocirc;ng tin.</p>\r\n\r\n<p>Mặt kh&aacute;c, c&aacute;c doanh nghiệp cũng cần b&aacute;m s&aacute;t Sở C&ocirc;ng Thương, Ủy ban Nh&acirc;n d&acirc;n địa phương để th&ocirc;ng tin kịp thời nhằm th&aacute;o gỡ kh&oacute; khăn ph&aacute;t sinh cục bộ tr&ecirc;n địa b&agrave;n cũng như đẩy mạnh truyền th&ocirc;ng hơn nữa c&aacute;c phương thức b&aacute;n h&agrave;ng online, m&ocirc; h&igrave;nh b&aacute;n h&agrave;ng theo combo&hellip; để người d&acirc;n biết v&agrave; sử dụng, hạn chế ra đường.</p>\r\n\r\n<p>C&aacute;c doanh nghiệp cần tổ chức hệ thống theo hướng giảm bớt tiếp x&uacute;c, tăng cường biện ph&aacute;p bảo vệ nh&acirc;n vi&ecirc;n trong kh&acirc;u giao nhận h&agrave;ng h&oacute;a.</p>\r\n\r\n<p>Đối với danh mục mặt h&agrave;ng thiết yếu, Bộ C&ocirc;ng Thương đ&atilde; c&oacute; văn bản gửi Sở C&ocirc;ng Thương c&aacute;c tỉnh, th&agrave;nh phố về h&agrave;ng h&oacute;a, dịch vụ thiết yếu. Bộ đề nghị Sở C&ocirc;ng Thương tham mưu cho Ủy ban Nh&acirc;n d&acirc;n tỉnh, th&agrave;nh phố ban h&agrave;nh thống nhất danh mục h&agrave;ng h&oacute;a thiết yếu được ph&eacute;p lưu th&ocirc;ng khi thực hiện gi&atilde;n c&aacute;ch theo Chỉ thị 16/CT-TTg; trong đ&oacute;, c&oacute; c&aacute;c mặt h&agrave;ng kh&aacute;c theo nhu cầu phục vụ sản xuất, sinh hoạt của địa phương.</p>\r\n\r\n<p>V&igrave; vậy, doanh nghiệp c&oacute; thể li&ecirc;n hệ với Sở C&ocirc;ng Thương địa phương, nơi doanh nghiệp c&oacute; chuỗi cung ứng để đề xuất danh mục h&agrave;ng thiết yếu cho ph&ugrave; hợp.</p>\r\n\r\n<p>Ngo&agrave;i ra, đại diện Tổ c&ocirc;ng t&aacute;c đ&atilde; tham gia Đo&agrave;n kiểm tra ph&ograve;ng, chống dịch tại C&ocirc;ng ty cổ phần Việt Nam Kỹ nghệ S&uacute;c sản (Vissan); C&ocirc;ng ty cổ phần Kỹ nghệ thực phẩm Việt Nam (VIFON) v&agrave; C&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn TikiNow Smart Logistics.</p>\r\n\r\n<p>Tại buổi l&agrave;m việc, đại diện C&ocirc;ng ty Vissan cho biết trong 1-2 ng&agrave;y tới, c&ocirc;ng ty đảm bảo sẽ cung ứng đầy đủ 100% nguồn thực phẩm thiết yếu cho c&aacute;c mạng lưới ph&acirc;n phối.</p>\r\n\r\n<p>C&ograve;n với C&ocirc;ng ty VIFON, so với thời gian trước, năng lực sản xuất của doanh nghiệp c&oacute; sụt giảm do nguồn cung ứng nguy&ecirc;n liệu thiếu, lao động sản xuất giảm.</p>\r\n\r\n<p>Trong khi đ&oacute;, đại diện C&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn Tikinow Smart Logistics chia sẻ bất cập trong việc giao h&agrave;ng li&ecirc;n quận, tại c&aacute;c &ldquo;v&ugrave;ng đỏ&rdquo;; bất cập về kho để h&agrave;ng tại một số quận c&oacute; diện t&iacute;ch hạn chế, kh&ocirc;ng đ&aacute;p ứng được nhu cầu người d&acirc;n&hellip;</p>\r\n\r\n<p>Ghi nhận v&agrave; để g&oacute;p phần th&aacute;o gỡ kh&oacute; khăn cho c&aacute;c doanh nghiệp, Tổ c&ocirc;ng t&aacute;c kiến nghị Bộ C&ocirc;ng Thương c&oacute; &yacute; kiến ch&iacute;nh thức gửi l&ecirc;n Ch&iacute;nh phủ, Ban Chỉ đạo quốc gia Ph&ograve;ng, chống dịch Covid-19 chỉ đạo Ủy ban Nh&acirc;n d&acirc;n c&aacute;c tỉnh, th&agrave;nh phố đang &aacute;p dụng giờ giới nghi&ecirc;m.</p>\r\n\r\n<p>Tổ c&ocirc;ng t&aacute;c kiến nghị xem x&eacute;t x&acirc;y dựng phương &aacute;n ưu ti&ecirc;n cho một số lao động trong ng&agrave;nh thực phẩm tươi sống (sơ chế, chế biến, vận chuyển) được ph&eacute;p lưu th&ocirc;ng tr&ecirc;n đường sau 18h hằng ng&agrave;y để kịp thời cung ứng c&aacute;c mặt h&agrave;ng tươi sống cho người d&acirc;n v&agrave;o s&aacute;ng h&ocirc;m sau.</p>\r\n', NULL, NULL),
(15, 4, 'Giá vàng trong nước và thế giới tiếp tục giảm', 'VHO- Giá vàng trong nước và thế giới giảm phiên thứ 4 liên tiếp, vàng trong nước đã xuống dưới 57 triệu đồng/lượng còn vàng thế giới giảm gần tới ngưỡng kháng cự 1.700 USD/oz.', 'images/anh-kinhte10.jpg', '<p>S&aacute;ng 11.8, C&ocirc;ng ty VBĐQ S&agrave;i G&ograve;n ni&ecirc;m yết gi&aacute; v&agrave;ng mua v&agrave;o 56,20 triệu đồng/lượng; gi&aacute; b&aacute;n ra l&agrave; 56,90 triệu đồng/lượng, giảm 50.000 đồng/lượng ở cả chiều mua v&agrave;o v&agrave; b&aacute;n ra so với đ&oacute;ng cửa phi&ecirc;n giao dịch liền trước. Ch&ecirc;nh lệch gi&aacute; b&aacute;n v&agrave;ng hiện đang cao hơn gi&aacute; mua 700.000 đồng/lượng.</p>\r\n\r\n<p>C&ugrave;ng thời điểm, Tập đo&agrave;n DOJI ni&ecirc;m yết gi&aacute; v&agrave;ng mua v&agrave;o - b&aacute;n ra ở mức 56,15 &ndash; 57,65 triệu đồng/lượng. So với chốt phi&ecirc;n giao dịch ng&agrave;y 10/8, gi&aacute; v&agrave;ng giữ nguy&ecirc;n ở chiều mua v&agrave;o v&agrave; b&aacute;n ra. Ch&ecirc;nh lệch gi&aacute; mua &ndash; b&aacute;n v&agrave;ng tại DOJI đang ở mức 1,5 triệu đồng/lượng.</p>\r\n\r\n<p>S&aacute;ng 11.8 (theo giờ Việt Nam) gi&aacute; v&agrave;ng đang được ni&ecirc;m yết tr&ecirc;n Kitco l&agrave; 1.726,9 USD/oz, giảm 1,1 USD/oz so với cuối giờ chiều 10.8.</p>\r\n\r\n<p>Quy đổi gi&aacute; v&agrave;ng thế giới theo tỷ gi&aacute; USD tại Vietcombank ở thời điểm tham chiếu: 1 USD = 22,980 VND, gi&aacute; v&agrave;ng thế giới tương đương 47,81 triệu đồng/lượng, thấp hơn 9,09 triệu đồng/lượng so với gi&aacute; v&agrave;ng SJC b&aacute;n ra ở c&ugrave;ng thời điểm.</p>\r\n\r\n<p>Đ&oacute;ng cửa phi&ecirc;n giao dịch ng&agrave;y 10/8, C&ocirc;ng ty VBĐQ S&agrave;i G&ograve;n ni&ecirc;m yết gi&aacute; v&agrave;ng mua v&agrave;o 56,25 triệu đồng/lượng; gi&aacute; b&aacute;n ra l&agrave; 56,95 triệu đồng/lượng, giữ nguy&ecirc;n ở cả chiều mua v&agrave;o v&agrave; b&aacute;n ra so với mở cửa phi&ecirc;n giao dịch c&ugrave;ng ng&agrave;y. Ch&ecirc;nh lệch gi&aacute; b&aacute;n v&agrave;ng hiện đang cao hơn gi&aacute; mua 700.000 đồng/lượng.</p>\r\n\r\n<p>C&ugrave;ng thời điểm, Tập đo&agrave;n DOJI ni&ecirc;m yết gi&aacute; v&agrave;ng mua v&agrave;o - b&aacute;n ra ở mức 56,15 &ndash; 57,65 triệu đồng/lượng. So với mở cửa phi&ecirc;n giao dịch ng&agrave;y 10/8, gi&aacute; v&agrave;ng giữ nguy&ecirc;n ở chiều mua v&agrave;o v&agrave; b&aacute;n ra. Ch&ecirc;nh lệch gi&aacute; mua &ndash; b&aacute;n v&agrave;ng tại DOJI đang ở mức 1,5 triệu đồng/lượng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/43943/81724gia_vang_7_0.jpg\" /></p>\r\n\r\n<p><strong>Gi&aacute; v&agrave;ng SJC đắt hơn v&agrave;ng thế giới 9,03 triệu đồng/lượng. (Ảnh minh họa: KT)</strong></p>\r\n\r\n<p>Cuối giờ chiều 10.8 (theo giờ Việt Nam) gi&aacute; v&agrave;ng đang được ni&ecirc;m yết tr&ecirc;n Kitco l&agrave; 1.728 USD/oz, giảm 5,8 USD/oz so với đầu giờ s&aacute;ng 10.8.</p>\r\n\r\n<p>Quy đổi gi&aacute; v&agrave;ng thế giới theo tỷ gi&aacute; USD tại Vietcombank ở thời điểm tham chiếu: 1 USD = 23.020 VND, gi&aacute; v&agrave;ng thế giới tương đương 47,92 triệu đồng/lượng, thấp hơn 9,03 triệu đồng/lượng so với gi&aacute; v&agrave;ng SJC b&aacute;n ra ở c&ugrave;ng thời điểm.</p>\r\n', NULL, NULL),
(16, 4, 'Sữa Cô gái Hà Lan tiếp sức y bác sĩ vào miền Nam chống dịch', 'VHO- Trung tuần tháng 8 vừa qua, nhãn hàng sữa Cô Gái Hà Lan (thuộc Tập đoàn FrieslandCampina Việt Nam) đã đồng hành cùng Cục Quản lý khám chữa bệnh (Bộ Y tế) chuyển tặng hơn 100.000 ly sữa để y bác sĩ tại Hà Nội vững vàng tiếp sức các tỉnh-thành miền Nam đẩy lùi dịch Covid-19.', 'images/anh_kinhte_16.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826761.jpg\" /></p>\r\n\r\n<p><strong>Những chuyến xe chở những ly sữa nghĩa t&igrave;nh của C&ocirc; G&aacute;i H&agrave; Lan tiếp sức c&aacute;c y b&aacute;c sĩ l&ecirc;n đường hỗ trợ c&aacute;c tỉnh-th&agrave;nh ph&iacute;a Nam chống dịch Covid-19</strong></p>\r\n\r\n<p><strong>Những chuyến đi kh&ocirc;ng mệt mỏi</strong></p>\r\n\r\n<p>Vừa nhận được lời k&ecirc;u gọi l&ecirc;n đường hỗ trợ miền Nam chống dịch Covid-19 của Thủ tướng Ch&iacute;nh phủ, ngay trong đ&ecirc;m, c&aacute;c y b&aacute;c sĩ của Bệnh viện Bạch Mai (H&agrave; Nội) đ&atilde; thu xếp h&agrave;nh trang chuẩn bị. Trong số c&aacute;c y b&aacute;c sĩ l&ecirc;n đường v&agrave;o TP.HCM lần n&agrave;y, kh&ocirc;ng &iacute;t người vừa trở về từ c&aacute;c v&ugrave;ng dịch như Bắc Giang, Hải Dương, Điện Bi&ecirc;n v&agrave; cả nước L&agrave;o anh em.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826772.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826783.jpg\" /></p>\r\n\r\n<p><strong>Những th&ugrave;ng sữa gửi trao đến c&aacute;c bệnh viện được nhanh ch&oacute;ng sắp xếp, ph&acirc;n chia để kịp ng&agrave;y c&aacute;c y b&aacute;c sĩ ra qu&acirc;n l&ecirc;n đường v&agrave;o c&aacute;c tỉnh-th&agrave;nh miền Nam</strong></p>\r\n\r\n<p>Đ&atilde; c&oacute; hai lần chống dịch tại Hải Dương, hỗ trợ chống dịch tại nước L&agrave;o, một b&aacute;c sĩ đang c&ocirc;ng t&aacute;c tại Trung t&acirc;m Bệnh Nhiệt đới, Bệnh viện Bạch Mai cho biết, đ&atilde; gần một năm trở lại đ&acirc;y, số lần anh được về nh&agrave; với vợ con chỉ đếm đầu ng&oacute;n tay, v&igrave; li&ecirc;n tục l&ecirc;n đường chi viện c&aacute;c địa phương c&oacute; dịch. Anh lu&ocirc;n c&oacute; sẵn một ba l&ocirc; quần &aacute;o chỉ để khi c&oacute; lệnh chi viện l&agrave; xuất ph&aacute;t cho nhanh. &ldquo;Đợt dịch tại TP.HCM lần n&agrave;y kh&aacute; phức tạp, đ&atilde; l&ecirc;n đường l&agrave; chấp nhận nguy hiểm nhưng ch&uacute;ng t&ocirc;i quyết chiến thắng th&igrave; mới quay về&rdquo; &ndash; vị b&aacute;c sĩ n&agrave;y chia sẻ.<br />\r\nLần n&agrave;y, ngo&agrave;i Bệnh viện Bạch Mai c&ograve;n c&oacute; c&aacute;c y b&aacute;c sĩ Bệnh viện E. Theo trưởng đo&agrave;n c&ocirc;ng t&aacute;c Bệnh viện E chia sẻ, ngay từ khi nhận th&ocirc;ng tin bệnh viện sẽ chi viện cho TP.HCM chống dịch, đ&atilde; c&oacute; 200 y b&aacute;c sĩ t&igrave;nh nguyện đăng k&yacute; tham gia. Nhiều b&aacute;c sĩ, điều dưỡng trẻ c&ograve;n tranh nhau đi, sẵn s&agrave;ng gửi con nhỏ cho nội, ngoại hoặc cha mẹ gi&agrave; yếu cho người th&acirc;n để hai vợ chồng c&ugrave;ng đi; ri&ecirc;ng c&aacute;c b&aacute;c sĩ nam c&ograve;n cắt t&oacute;c, cạo đầu ngắn để sẵn s&agrave;ng l&ecirc;n đường&hellip;<br />\r\nĐược biết, Bộ Y tế đ&atilde; huy động 10.000 nh&acirc;n lực y tế từ c&aacute;c bệnh viện Trung ương l&ecirc;n đường chi viện cho TP.HCM v&agrave; c&aacute;c tỉnh-th&agrave;nh ph&iacute;a Nam ph&ograve;ng, chống dịch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826794.jpg\" /></p>\r\n\r\n<p><strong>Nguồn dinh dưỡng từ C&ocirc; G&aacute;i H&agrave; Lan hy vọng sẽ tiếp sức mạnh mẽ về thể chất lẫn tinh thần cho c&aacute;c b&aacute;c sĩ trong đợt ra qu&acirc;n n&agrave;y</strong></p>\r\n\r\n<p><strong>Giọt sữa y&ecirc;u thương tiếp sức sớm chiến thắng đại dịch</strong></p>\r\n\r\n<p>Từ th&aacute;ng 7 đến nay, h&agrave;ng loạt c&aacute;c bệnh viện Trung ương đều tổ chức lễ xuất qu&acirc;n tiễn y b&aacute;c sĩ l&ecirc;n đường hỗ trợ TP.HCM ph&ograve;ng chống dịch Covid-19. Tại khu&ocirc;n vi&ecirc;n Bệnh viện Bạch Mai, Bệnh viện E&hellip; những c&aacute;i bắt tay &ocirc;m nhau, những nụ cười rạng rỡ, những giọt nước mắt bịn rịn của người đi người ở lại, những cuộc gọi video ngắn ngủi động vi&ecirc;n, nhắc nhở &ldquo;con ở nh&agrave; nhớ nghe lời mẹ&rdquo;, &ldquo;chờ anh về m&igrave;nh cưới nhau&rdquo;, &ldquo;ba mẹ nhớ giữ sức khoẻ, ch&uacute;ng con sẽ chiến thắng trở về&rdquo;&hellip; l&agrave;m cho kh&ocirc;ng kh&iacute; vừa thi&ecirc;ng li&ecirc;ng, vừa x&uacute;c động hơn bao giờ hết.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826805.jpg\" /></p>\r\n\r\n<p><strong><strong>Đội ngũ y b&aacute;c sĩ tại bệnh viện E trước khi l&ecirc;n đường hỗ trợ tỉnh Đồng Th&aacute;p ph&ograve;ng chống dịch Covid-19</strong></strong></p>\r\n\r\n<p><strong>V&agrave; h&agrave;nh trang l&ecirc;n đường ấy, ngo&agrave;i quần &aacute;o, vật dụng cần thiết, balo ai cũng&hellip; đầy ắp sữa. Đ&oacute; l&agrave; m&oacute;n qu&agrave; tinh thần m&agrave; C&ocirc; G&aacute;i H&agrave; Lan kịp thời gửi tặng đến hơn 3.000 y b&aacute;c sĩ thuộc 10 bệnh viện v&agrave;o chi viện miền Nam chống dịch như: Bạch Mai, Bệnh viện E, Trung t&acirc;m Tim mạch, Đại học Y H&agrave; Nội, Nhi Trung ương, Phụ sản Trung ương, Phụ sản H&agrave; Nội; Đức Giang, Thanh Nh&agrave;n, Xanh P&ocirc;n.</strong></p>\r\n\r\n<p style=\"text-align:center\"><br />\r\n<img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826816.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826827.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p><strong>Kh&ocirc;ng kh&iacute; buổi xuất qu&acirc;n đầy x&uacute;c động thi&ecirc;ng li&ecirc;ng với những nụ cười, giọt nước mắt, c&aacute;i &ocirc;m, vẫy tay ch&agrave;o nhau</strong></p>\r\n\r\n<p>Chia sẻ cảm x&uacute;c tại buổi lễ ra qu&acirc;n, &ocirc;ng L&ecirc; Đ&igrave;nh Hưng, Bệnh viện E cho biết, đợt dịch Covid-19 lần thứ 4 n&agrave;y tại TP.HCM v&agrave; c&aacute;c tỉnh miền Nam rất phức tạp v&agrave; nghi&ecirc;m trọng. Nhưng anh v&agrave; c&aacute;c y b&aacute;c sĩ đ&atilde; chuẩn bị tinh thần cao độ, quyết t&acirc;m dốc hết sức m&igrave;nh để chiến thắng đại dịch. Những m&oacute;n qu&agrave; tiếp sức kịp thời của doanh nghiệp v&agrave; mạnh thường qu&acirc;n trong l&uacute;c n&agrave;y, đ&atilde; gi&uacute;p c&aacute;c y b&aacute;c sĩ th&ecirc;m ấm l&ograve;ng v&agrave; cảm thấy kh&ocirc;ng đơn độc giữa &ldquo;cuộc chiến&rdquo;.</p>\r\n\r\n<p>Kh&ocirc;ng n&eacute;n được x&uacute;c động, TS.BS Đo&agrave;n Thu Tr&agrave;, Ph&oacute; gi&aacute;m đốc Trung t&acirc;m Bệnh Nhiệt đới (Bệnh viện Bạch Mai) chia sẻ, c&oacute; những y b&aacute;c sĩ kh&ocirc;ng nhớ m&igrave;nh được chọn đi chống dịch lần thứ bao nhi&ecirc;u, n&ecirc;n theo ng&agrave;y th&aacute;ng, tinh thần v&agrave; sức khoẻ c&oacute; phần ảnh hưởng. Chuyến đi v&agrave;o Nam n&agrave;y sẽ gian nan, nhiều thử th&aacute;ch, n&ecirc;n hơn bao giờ hết, họ cần c&oacute; một sức khoẻ bền bỉ. C&ocirc; G&aacute;i H&agrave; Lan đ&atilde; thấu hiểu điều n&agrave;y v&agrave; hỗ trợ rất kịp thời bằng những ly sữa m&aacute;t l&agrave;nh, nhiều chất dinh dưỡng v&agrave; gi&agrave;u y&ecirc;u thương.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826838.jpg\" /><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826838.jpg\" /></p>\r\n\r\n<p><strong>V&agrave; trong h&agrave;nh trang l&ecirc;n đường đồng h&agrave;nh c&ugrave;ng y b&aacute;c sĩ bệnh viện E c&ograve;n c&oacute; những th&ugrave;ng sữa y&ecirc;u thương của C&ocirc; G&aacute;i H&agrave; Lan gửi đến như một lời ch&uacute;c sức khoẻ, động vi&ecirc;n tinh thần c&aacute;c y b&aacute;c sĩ ho&agrave;n th&agrave;nh tốt nhiệm vụ</strong></p>\r\n\r\n<p>B&agrave; Nguyễn Thị Thu Hương, Đại diện nh&atilde;n h&agrave;ng sữa C&ocirc; G&aacute;i H&agrave; Lan khẳng định, trong chương tr&igrave;nh lần n&agrave;y, C&ocirc; G&aacute;i H&agrave; Lan rất vinh dự được phối hợp với Cục Quản l&yacute; Kh&aacute;m chữa bệnh để song h&agrave;nh c&ugrave;ng lực lượng y tế tuyến đầu v&agrave;o miền Nam chống dịch. Bởi đ&acirc;y l&agrave; sự hy sinh qu&yacute; b&aacute;u v&igrave; lợi &iacute;ch chung rất đ&aacute;ng được tri &acirc;n, l&agrave; những điển h&igrave;nh của &ldquo;lương y như từ mẫu&rdquo;. B&agrave; Hương chia sẻ th&ecirc;m, với t&acirc;m niệm lu&ocirc;n đồng h&agrave;nh c&ugrave;ng ng&agrave;nh y tế, c&ocirc;ng ty ch&uacute;ng t&ocirc;i cũng đ&atilde; tham gia rất nhiều chương tr&igrave;nh như h&agrave;nh tr&igrave;nh c&ugrave;ng Hội Thầy thuốc trẻ ngay từ những ng&agrave;y đầu th&agrave;nh lập, phối hợp với c&aacute;c đơn vị ở những chiến dịch kh&aacute;m chữa bệnh cho những người d&acirc;n v&ugrave;ng s&acirc;u, v&ugrave;ng xa</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/44395/826849.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Trước đ&oacute;, trong những ng&agrave;y đầu của l&agrave;n s&oacute;ng Covid-19 lần thứ 4 b&ugrave;ng ph&aacute;t, C&ocirc; G&aacute;i H&agrave; Lan cũng đ&atilde; nhanh ch&oacute;ng song h&agrave;nh c&ugrave;ng c&aacute;c cơ quan chức năng hỗ trợ hơn 700.000 ly sữa để tiếp sức cho c&aacute;c y b&aacute;c sĩ, người d&acirc;n tại c&aacute;c khu phong toả, c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại TP.HCM, B&igrave;nh Dương&hellip; Đ&acirc;y l&agrave; một trong những chuỗi h&agrave;nh tr&igrave;nh &ldquo;Dinh dưỡng y&ecirc;u thương, chung tay chia sẻ&rdquo; mang đầy t&iacute;nh nh&acirc;n văn, tinh thần san sẻ m&agrave; nh&atilde;n h&agrave;ng sữa C&ocirc; G&aacute;i H&agrave; Lan đ&atilde; khởi động từ khi dịch Covid-19 b&ugrave;ng ph&aacute;t lần thứ nhất v&agrave;o đầu năm 2020 với tổng gi&aacute; trị hỗ trợ hơn 25 tỷ đồng.</p>\r\n', NULL, NULL),
(17, 4, 'Hà Nội: Một số TTTM Vincom bắt đầu hoạt động trở lại', 'VHO- Ngay sau khi 19 quận, huyện Hà Nội được nới lỏng giãn cách, nhiều người dân háo hức đặt và mua đồ ăn mang về sau một thời gian dài ăn đồ “home-made”. Hệ thống trung tâm thương mại Vincom cũng từng bước mở cửa trở lại đón cuộc sống bình thường mới, khởi đầu từ chuỗi các gian hàng ẩm thực.', 'images/anh_kinhte_17.jpg', '<p style=\"text-align:center\">Nằm tại quận Ba Đ&igrave;nh, Vincom Center Metropolis ngay ng&agrave;y đầu mở cửa trở lại đ&atilde; ghi nhận lượng lớn đơn đặt h&agrave;ng ẩm thực khi quy tụ c&aacute;c thương hiệu nổi tiếng được y&ecirc;u th&iacute;ch như Starbucks, Highlands Coffee, Paris Baguette...</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://baochinhphu.vn/Uploaded/nguyendieuhuong/2021_09_06/hang%20hoa%201.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Starbucks li&ecirc;n tục đ&oacute;n kh&aacute;ch mua mang về ngay khi mở cửa. Tất cả kh&aacute;ch h&agrave;ng v&agrave; nh&acirc;n vi&ecirc;n đều chủ động tu&acirc;n thủ c&ocirc;ng t&aacute;c ph&ograve;ng dịch, giữ khoảng c&aacute;ch khi mua đồ.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Hội &ldquo;hảo ngọt&rdquo; cũng nhanh ch&oacute;ng được &ldquo;thỏa m&atilde;n&rdquo; cơn th&egrave;m tại Paris Baguette tại tầng BH Vincom Center Metropolis với b&aacute;nh ngọt v&agrave; đồ uống được pha chế tươi, mới. Chị L&ecirc; Phương &ndash; một kh&aacute;ch h&agrave;ng mua đồ tại đ&acirc;y chia sẻ: &ldquo;L&agrave; một nh&acirc;n vi&ecirc;n văn ph&ograve;ng thường xuy&ecirc;n uống c&agrave; ph&ecirc;, tr&agrave; sữa v&agrave; v&igrave; nh&agrave; ngay gần đ&acirc;y n&ecirc;n t&ocirc;i đ&atilde; mua lu&ocirc;n combo b&aacute;nh ngọt, tr&agrave; sữa để&hellip; đỡ th&egrave;m. Cửa h&agrave;ng n&agrave;y đối diện si&ecirc;u thị VinMart n&ecirc;n sau đ&oacute; t&ocirc;i cũng tranh thủ mua th&ecirc;m thực phẩm. TTTM thực hiện ph&ograve;ng dịch rất tốt n&ecirc;n khi mua sắm t&ocirc;i cũng thấy an t&acirc;m hơn.&rdquo;</p>\r\n\r\n<p>Xứng tầm l&agrave; &ldquo;tọa độ v&agrave;ng&rdquo; mua sắm của quận Ba Đ&igrave;nh, cửa h&agrave;ng Muji duy nhất tại H&agrave; Nội cũng đ&atilde; ch&iacute;nh thức mở cửa, nhanh ch&oacute;ng đ&oacute;n kh&aacute;ch mua sắm với c&aacute;c sản phẩm, đồ d&ugrave;ng thiết yếu v&agrave; quầy h&agrave;ng thực phẩm hấp dẫn.</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c bạn trẻ, cặp đ&ocirc;i mua sắm, Muji cũng đ&oacute;n tiếp kh&aacute;ch h&agrave;ng l&agrave; gia đ&igrave;nh tới mua đồ d&ugrave;ng văn ph&ograve;ng phẩm cho con nhỏ. Chị Hương, mẹ của hai em học sinh cấp 1 v&agrave; cấp 2 cho biết đ&atilde; tranh thủ v&agrave;o Muji mua th&ecirc;m b&uacute;t, vở viết cho con học. &ldquo;TTTM c&oacute; si&ecirc;u thị, h&agrave;ng ăn cũng đ&atilde; mở cửa b&aacute;n mang về, c&oacute; cả văn ph&ograve;ng phẩm, đồ gia dụng n&ecirc;n t&ocirc;i chỉ cần đi khoảng 2 tiếng l&agrave; đ&atilde; mua được hết c&aacute;c đồ cần thiết, giảm việc di chuyển, tiếp x&uacute;c c&ocirc;ng cộng nhiều. Trẻ con nh&agrave; t&ocirc;i th&iacute;ch d&ugrave;ng văn ph&ograve;ng phẩm của Muji, mẫu m&atilde; đơn giản nhưng chất lượng tốt&rdquo;, chị Hương cho biết.&nbsp;</p>\r\n\r\n<p>C&aacute;c kh&aacute;ch h&agrave;ng mua sắm tại Muji đều &yacute; thức tu&acirc;n thủ 5K, nh&acirc;n vi&ecirc;n tại đ&acirc;y cũng thường xuy&ecirc;n nhắc nhở, hỗ trợ kh&aacute;ch h&agrave;ng mua sắm để kiểm so&aacute;t an to&agrave;n tối đa.&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t870/uploaded/qfsqy/2021_09_28/ttxvn_trung_tam_thuong_mai4.jpg\" /></p>\r\n\r\n<p>Tại Vincom Center Trần Duy Hưng, The Pizza Company, McDonalds&hellip; cũng đ&atilde; bắt đầu nhận giao h&agrave;ng. Những m&oacute;n ăn &ldquo;fast food&rdquo; quen thuộc trước đ&acirc;y đ&atilde; quay trở lại, sẵn s&agrave;ng &ldquo;giải v&acirc;y&rdquo; cho c&aacute;c b&agrave; nội trợ sau chuỗi ng&agrave;y d&agrave;i gi&atilde;n c&aacute;ch. C&aacute;c shipper cũng &yacute; thức được việc ph&ograve;ng dịch, chủ động v&agrave; nghi&ecirc;m t&uacute;c thực hiện 5K, xếp h&agrave;ng gi&atilde;n c&aacute;ch, đảm bảo an to&agrave;n. &nbsp;</p>\r\n\r\n<p>Đồng h&agrave;nh c&ugrave;ng nhịp sống mới của người d&acirc;n, c&aacute;c TTTM Vincom tại H&agrave; Nội được mở cửa theo quy định đ&atilde; nhanh ch&oacute;ng quy hoạch vị tr&iacute; đỗ xe ri&ecirc;ng ở khu vực thuận tiện v&agrave; miễn ph&iacute; 30 ph&uacute;t gửi xe d&agrave;nh ri&ecirc;ng cho c&aacute;c t&agrave;i xế giao h&agrave;ng. Tất cả t&agrave;i xế ra/v&agrave;o TTTM đều được đo th&acirc;n nhiệt, y&ecirc;u cầu s&aacute;t khuẩn tay v&agrave; giữ khoảng c&aacute;ch an to&agrave;n, gi&uacute;p qu&aacute; tr&igrave;nh nhận h&agrave;ng &ndash; giao h&agrave;ng nhanh ch&oacute;ng, đảm bảo ph&ograve;ng dịch cho ch&iacute;nh t&agrave;i xế, nh&acirc;n vi&ecirc;n gian h&agrave;ng v&agrave; kh&aacute;ch h&agrave;ng.&nbsp;</p>\r\n', NULL, NULL),
(18, 4, 'Xăng dầu đồng loạt tăng giá, RON95 lên 21.397 đồng/lít', 'VHO- Từ 15 giờ hôm nay (10.9), mỗi lít xăng E5 RON 92 tăng 252 đồng, RON 95 tăng 266 đồng, các mặt hàng dầu tăng 320-897đồng/lít/kg.', 'images/anh_kinhte_18.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/45191/84352xangdau.jpg\" style=\"height:399px; width:599px\" /></p>\r\n\r\n<p><strong>Gi&aacute;&nbsp;xăng, dầu đồng loạt tăng từ 15h ng&agrave;y 10.9.&nbsp;Ảnh minh họa: T.L</strong></p>\r\n\r\n<p>Li&ecirc;n bộ C&ocirc;ng Thương - T&agrave;i ch&iacute;nh vừa c&ocirc;ng bố quyết định điều chỉnh gi&aacute; xăng, dầu theo chu kỳ 15 ng&agrave;y một lần. Trong đ&oacute; cho ph&eacute;p c&aacute;c đầu mối kinh doanh tăng gi&aacute; với tất cả mặt h&agrave;ng xăng, dầu.</p>\r\n\r\n<p>Theo đ&oacute;, gi&aacute; xăng E5 RON92 tăng 252 đồng/l&iacute;t so với gi&aacute; hiện h&agrave;nh l&ecirc;n 20.143 đồng/l&iacute;t. Xăng RON95 l&ecirc;n 21.397 đ&ocirc;̀ng/lít, tức l&agrave; tăng 266 đồng/l&iacute;t so với gi&aacute; hiện h&agrave;nh. Gi&aacute; dầu diesel l&ecirc;n 16.022 đồng/l&iacute;t, tăng 355 đồng/l&iacute;t. Dầu hỏa l&ecirc;n 15.082 đồng/l&iacute;t, tăng 320 đồng/l&iacute;t. Dầu mazut l&ecirc;n 15.952 đồng/kg, tăng 897 đồng/kg.</p>\r\n\r\n<p>Tại kỳ điều chỉnh n&agrave;y, nh&agrave; điều h&agrave;nh tr&iacute;ch lập Quỹ BOG với xăng RON95 ở mức 150 đồng/l&iacute;t (như kỳ trước), dầu diesel ở mức 200 đồng/l&iacute;t (kỳ trước l&agrave; 400 đồng/l&iacute;t), dầu hỏa ở mức 200 đồng/l&iacute;t, dầu mazut ở mức 100 đồng/kg.&nbsp;Đồng thời chi sử dụng quỹ với xăng E5RON92 ở mức 900 đồng/l&iacute;t; xăng RON95, dầu diesel, dầu hỏa v&agrave; dầu mazut kh&ocirc;ng chi.</p>\r\n\r\n<p>Tại kỳ điều chỉnh trước đ&oacute;, gi&aacute; xăng E5 RON92 giảm 607 đồng/l&iacute;t, xăng RON95 giảm 550 đồng/l&iacute;t, c&aacute;c mặt h&agrave;ng dầu cũng giảm từ 350 - 506 đồng/l&iacute;t/kg t&ugrave;y loại.</p>\r\n', NULL, NULL),
(19, 4, '“ATM việc làm” tiếp sức cho người lao động', 'VHO- Mặc dù chịu tác động nặng nề do dịch Covid-19 song với quyết tâm vượt qua đại dịch, thích nghi với điều kiện mới, chương trình “ATM việc làm” do Hội Doanh nhân trẻ tỉnh Ninh Thuận triển khai, đang là kênh tiếp sức hiệu quả nhằm giúp NLĐ bị ảnh hưởng bởi dịch tìm được việc làm phù hợp.', 'images/anh_kinhte_19.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/45520/85113A1-(00000003).jpg\" /></p>\r\n\r\n<p>&nbsp;<strong>Chương tr&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo;, kết nối với người lao động c&oacute; nhu cầu t&igrave;m việc ph&ugrave; hợp trong m&ugrave;a dịch</strong></p>\r\n\r\n<p>Ninh Thuận hiện c&oacute; h&agrave;ng ng&agrave;n c&ocirc;ng d&acirc;n l&agrave;m việc trong c&aacute;c khu c&ocirc;ng nghiệp, cơ sở sản xuất tại c&aacute;c tỉnh, th&agrave;nh ph&iacute;a Nam như TP.HCM, Đồng Nai, B&igrave;nh Dương... Khi dịch Covid-19 b&ugrave;ng ph&aacute;t, c&aacute;c địa phương phải thực hiện gi&atilde;n c&aacute;ch x&atilde; hội khiến c&ocirc;ng việc của nhiều NLĐ bị ảnh hưởng, rơi v&agrave;o cảnh thất nghiệp, mất nguồn thu nhập v&agrave; nhiều người đ&atilde; trở về qu&ecirc;, t&igrave;m c&ocirc;ng việc mới. Được sự chỉ đạo của UBND tỉnh, Hội Doanh nh&acirc;n trẻ tỉnh Ninh Thuận phối hợp với Sở LĐ,TB&amp;XH triển khai chương tr&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo;. &Ocirc;ng Nguyễn Thanh Hồng, Chủ tịch Hội Doanh nh&acirc;n trẻ tỉnh Ninh Thuận cho biết, với mong muốn c&ugrave;ng cộng đồng doanh nghiệp trong tỉnh chung tay san sẻ kh&oacute; khăn v&agrave; hỗ trợ NLĐ từ c&aacute;c tỉnh, th&agrave;nh ph&iacute;a Nam về Ninh Thuận, c&ugrave;ng với đ&oacute; tiếp sức cho NLĐ tr&ecirc;n địa b&agrave;n gặp kh&oacute; khăn do gi&atilde;n c&aacute;ch x&atilde; hội sớm c&oacute; việc l&agrave;m, v&igrave; thế đơn vị đ&atilde; tổ chức chương tr&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo; miễn ph&iacute; để kết nối, giới thiệu những người lao động cần hỗ trợ việc l&agrave;m với c&aacute;c doanh nghiệp, c&aacute;c hộ kinh doanh.</p>\r\n\r\n<p>Để tạo thuận lợi cho việc kết nối việc l&agrave;m, văn ph&ograve;ng &ldquo;ATM việc l&agrave;m&rdquo; được th&agrave;nh lập ngay tại trụ sở Hội Doanh nh&acirc;n trẻ tỉnh, NLĐ c&oacute; thể trực tiếp đến li&ecirc;n hệ tại văn ph&ograve;ng hoặc ứng tuyển vị tr&iacute; việc l&agrave;m qua fanpage mạng x&atilde; hội của Hội. Định kỳ hằng tuần, tại trụ sở Hội tổ chức c&aacute;c buổi gặp gỡ theo nh&oacute;m ng&agrave;nh nghề giữa doanh nghiệp v&agrave; NLĐ để trao đổi, thống nhất k&yacute; kết c&aacute;c hợp đồng lao động. Th&ocirc;ng tin từ Hội Doanh nh&acirc;n trẻ tỉnh Ninh Thuận, bắt đầu tổ chức từ cuối th&aacute;ng 8 đến nay, chương tr&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo; đ&atilde; tiếp nhận th&ocirc;ng tin của tr&ecirc;n 1.200 người đăng k&yacute; t&igrave;m việc ở c&aacute;c vị tr&iacute; kh&aacute;c nhau; thu h&uacute;t 23 doanh nghiệp, đơn vị trong tỉnh đăng k&yacute; tuyển dụng tr&ecirc;n 300 lao động l&agrave;m việc ở c&aacute;c ng&agrave;nh, nghề gồm kế to&aacute;n, gi&aacute;o vi&ecirc;n, nh&agrave; h&agrave;ng, nu&ocirc;i trồng thủy hải sản, kỹ sư, lao động phổ th&ocirc;ng v&agrave; c&aacute;c ng&agrave;nh nghề kh&aacute;c. Qua 3 đợt tổ chức, kh&ocirc;ng &iacute;t lao động đ&atilde; kết nối c&ocirc;ng việc th&agrave;nh c&ocirc;ng với c&aacute;c nh&agrave; tuyển dụng.</p>\r\n\r\n<p>Trao đổi với ch&uacute;ng t&ocirc;i, chị Nguyễn Thị Mỹ Hạnh (x&atilde; Bắc Phong, huyện Thuận Bắc) cho biết, chị tốt nghiệp Trung cấp Chuy&ecirc;n nghiệp ng&agrave;nh Quản Trị Kinh Doanh từ năm 2012 v&agrave; l&agrave;m việc tại TP.HCM ở nhiều lĩnh vực. &ldquo;Do ảnh hưởng của dịch Covid-19, nhiều người phải thất nghiệp v&agrave; muốn t&igrave;m một c&ocirc;ng việc mới, th&iacute;ch hợp v&agrave; gắn b&oacute; l&acirc;u d&agrave;i tại qu&ecirc; nh&agrave;, từ chương tr&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo; t&ocirc;i đ&atilde; nộp hồ sơ phỏng vấn c&aacute;ch đ&acirc;y hơn 2 tuần để xin việc tại vị tr&iacute; lễ t&acirc;n của C&ocirc;ng ty CP Th&agrave;nh Đ&ocirc;ng Ninh Thuận. Dẫu biết xin việc trong bối cảnh hiện nay rất kh&oacute; khăn, nhưng mong rằng, C&ocirc;ng ty sớm hồi &acirc;m để t&ocirc;i y&ecirc;n t&acirc;m, sẵn s&agrave;ng cho c&ocirc;ng việc mới tại qu&ecirc; nh&agrave;, chị Hạnh cho biết. C&ograve;n chị Ph&uacute; Thị Ya Nghi&ecirc;m (thị trấn Phước D&acirc;n, huyện Ninh Phước) chia sẻ: Qua k&ecirc;nh giới thiệu v&agrave; t&igrave;m hiểu m&ocirc; h&igrave;nh &ldquo;ATM việc l&agrave;m&rdquo;, t&ocirc;i đ&atilde; nộp hồ sơ, phỏng vấn v&agrave;o cuối th&aacute;ng 8 vừa rồi ở vị tr&iacute; kế to&aacute;n của C&ocirc;ng ty CP Đầu tư Tất Gia Ph&aacute;t, đồng thời C&ocirc;ng ty hứa sẽ chủ động li&ecirc;n hệ để t&ocirc;i sớm đi l&agrave;m. Tin rằng, tại m&ocirc;i trường mới t&ocirc;i sẽ t&igrave;m được vị tr&iacute; c&ocirc;ng việc ph&ugrave; hợp với năng lực của bản th&acirc;n v&agrave; l&agrave; một trong những nh&acirc;n vi&ecirc;n n&ograve;ng cốt của C&ocirc;ng ty, c&oacute; cơ hội ph&aacute;t triển.</p>\r\n', NULL, NULL),
(20, 4, 'Cựu cố vấn Tổng thống Mỹ chia sẻ ‘bí kíp” phản ứng nhanh với Covid-19 của Anh, Mỹ, Israel', 'VHO- “Chúng ta muốn hái quả, nhưng quả ngọt phải là kết quả của những hạt giống đã được gieo trồng từ nhiều thập kỷ trước. Phải qua rất nhiều thế hệ, chúng ta mới có thể thu về “nguyên liệu\" cần thiết để tạo ra những phát minh khoa học đột phá nhằm kiểm soát Covid-19 hay những bệnh nguy hiểm khác\", GS Ezekiel Emanuel chia sẻ tại chương trình Đối thoại Khoa học do Quỹ VinFuture tổ chức trực tuyến. ', 'images/anh_kinhte_20.jpg', '<p><strong>Th&agrave;nh quả khoa học chỉ đến khi c&oacute; nền tảng nghi&ecirc;n cứu l&acirc;u d&agrave;i</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/45528/85128vin1.jpg\" /></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><strong>Chương tr&igrave;nh Đối thoại Khoa học do Quỹ VinFuture được tổ chức trực tuyến</strong></p>\r\n\r\n<p>Quỹ VinFuture vừa ch&iacute;nh thức khởi động chuỗi chương tr&igrave;nh Đối thoại Khoa học h&agrave;ng th&aacute;ng, bắt đầu từ th&aacute;ng 9 đến th&aacute;ng 12 năm 2021 tr&ecirc;n quy m&ocirc; to&agrave;n cầu. Mục ti&ecirc;u của hoạt động n&agrave;y l&agrave; nhằmđưa khoa học &ndash; c&ocirc;ng nghệ đến gần hơn với đại ch&uacute;ng, g&oacute;p phần n&acirc;ng cao nhận thức về vai tr&ograve; của khoa học đối với c&aacute;c Mục ti&ecirc;u Ph&aacute;t triển Bền vững, từ đ&oacute; th&uacute;c đẩy c&aacute;c nghi&ecirc;n cứu, s&aacute;ng kiến đột ph&aacute; trong cộng đồng.</p>\r\n\r\n<p>Buổi đối thoại đầu ti&ecirc;n c&oacute; chủ đề &ldquo;Khoa học đang thay đổi sức khỏe to&agrave;n cầu như thế n&agrave;o?&rdquo;với sự c&oacute; mặt của c&aacute;c nh&agrave; khoa học h&agrave;ng đầu thế giới trong lĩnh vực quản l&yacute; y tế như GS Ezekiel Emanuel (Hiệu ph&oacute; chuy&ecirc;n tr&aacute;ch về c&aacute;c vấn đề to&agrave;n cầu tại Đại học Pennsylvania, Mỹ), GS Alta Schutte (Trưởng nh&oacute;m nghi&ecirc;n cứu về Tim mạch, Mạch m&aacute;u v&agrave; Chuyển h&oacute;a tại Khoa Y học v&agrave; Sức khỏe, Đại học New South Wales, Australia)... &nbsp;Buổi thảo luận đ&atilde; đưa ra rất nhiều &yacute; tưởng quan trọng nhằmđảm bảo sức khỏe to&agrave;n d&acirc;n trong v&agrave; sau đại dịch Covid-19.</p>\r\n\r\n<p>Theo GS Emanuel, kinh nghiệm từ c&aacute;c nước ph&aacute;t triển mang tới nhiều b&agrave;i học trong vấn đề n&agrave;y. Mỹ v&agrave; Anh l&agrave; hai v&iacute; dụ điển h&igrave;nh cho thấy tầm quan trọng của khoa học cơ bản trong việc nghi&ecirc;n cứu v&agrave; nhanh ch&oacute;ng đưa v&agrave;o sản xuất vắc-xin ph&ograve;ng chống Covid.</p>\r\n\r\n<p>Vị GS hiện&nbsp;l&agrave; cố vấn đặc biệt cho Tổng gi&aacute;m đốc Tổ chức Y tế Thế giới v&agrave; từng l&agrave; th&agrave;nh vi&ecirc;n của đội đặc nhiệm chống Covid-19 của Tổng thống Mỹ đương nhiệm Joe Biden, lấy v&iacute; dụ, ở Mỹ, những nghi&ecirc;n cứu đầu ti&ecirc;n về vắc xin mRNA bắt đầu từ &iacute;t nhất hơn ba thập kỷ trước. Tuy nhi&ecirc;n, chỉ đến khi Covid-19 b&ugrave;ng ph&aacute;t, n&oacute; mới c&oacute; thể được ph&aacute;t triển trong thời gian kỷ lục trong khoảng 10 th&aacute;ng. Những đổi mới n&agrave;y kh&ocirc;ng tự nhi&ecirc;n m&agrave; c&oacute; m&agrave; dựa tr&ecirc;n nền tảng của cả một hệ sinh th&aacute;i bao gồm c&aacute;c học viện, c&aacute;c nh&agrave; nghi&ecirc;n cứu, c&aacute;c c&ocirc;ng ty, c&aacute;c nh&agrave; đầu tư mạo hiểm, c&aacute;c cơ quan quản l&yacute;. Hệ sinh th&aacute;i đ&oacute; đ&atilde; lớn l&ecirc;n trong nhiều thập kỷ v&agrave; c&oacute; thể nhanh ch&oacute;ng đạt được tiến bộ khi t&igrave;nh huống xảy ra.</p>\r\n\r\n<p>Tương tự, &ocirc;ng cũng chỉ ra những th&agrave;nh c&ocirc;ng của nước Anh khi c&oacute; thể đưa ra c&aacute;c bằng chứng khoa học v&agrave; ph&aacute;p đồ điều trị hiệu quả chỉ trong v&ograve;ng 100 ng&agrave;y kể từ khi Covid-19 xuất hiện tại quốc gia n&agrave;y.&nbsp;</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta muốn h&aacute;i quả, nhưng quả ngọt phải l&agrave; kết quả của những hạt giống đ&atilde; được gieo trồng từ nhiều thập kỷ trước. Phải qua rất nhiều thế hệ, ch&uacute;ng ta mới c&oacute; thể thu về tất cả những nguy&ecirc;n liệu cần thiết để tạo ra những ph&aacute;t minh khoa học đột ph&aacute; nhằm kiểm so&aacute;t Covid-19 hay những bệnh nguy hiểm kh&aacute;c&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, GS. Emanuel cũng chia sẻ, mặc d&ugrave; đi nhanh trong việc sản xuất vắc xin ph&ograve;ng&nbsp;Covid-19 nhờ th&agrave;nh quả từ nền tảng nghi&ecirc;n cứu, nhưng ch&iacute;nh ng&agrave;nh y tế Mỹ lại tỏ ra l&uacute;ng t&uacute;ng, thiếu hợp t&aacute;c đồng bộ trong cuộc chiến chống đại dịch. &ldquo;Năng lực thực chiến l&agrave; tối quan trọng. Ch&uacute;ng ta cần một chiến lược b&agrave;i bản, kh&ocirc;ng chỉ với những khủng hoảng l&acirc;m thời như CCovid-19, m&agrave; cả c&aacute;c vấn đề sức khoẻ cộng đồng. Hệ thống quản l&yacute; y tế của tất cả c&aacute;c quốc gia đều cần phải được chuẩn bị tốt hơn, bất kể l&agrave; nước gi&agrave;u hay c&aacute;c nước &iacute;t nguồn lực&rdquo;, &ocirc;ng nhận định.</p>\r\n\r\n<p>Vấn đề mấu chốt, theo &ocirc;ng, ch&iacute;nh l&agrave; một hệ thống gi&aacute;m s&aacute;t bệnh tật chủ động, chặt chẽ ở cấp quốc gia cũng như quy m&ocirc; to&agrave;n cầu.</p>\r\n\r\n<p>&ldquo;Israel c&oacute; lẽ l&agrave; v&iacute; dụ điển h&igrave;nh nhất của việc quản l&yacute; dữ liệu c&ocirc;ng d&acirc;n một c&aacute;ch c&oacute; hệ thống. C&ograve;n g&igrave; c&oacute; thể tốt hơn thế? Nh&agrave; chức tr&aacute;ch sẽ nắm được từng người d&acirc;n đang phản ứng với vắc-xin như thế n&agrave;o, điều g&igrave; xảy ra khi họ kh&ocirc;ng được ti&ecirc;m, ai đang bị bệnh, v.v&rdquo;, &ocirc;ng n&oacute;i.&nbsp;</p>\r\n\r\n<p><strong>Khoa học dữ liệu sẽ gi&uacute;p đ&aacute;nh bại những nguy cơ như Covid-19 trong tương lai</strong></p>\r\n\r\n<p>Đồng quan điểm, GS Alta Schutte khẳng định,việc nghi&ecirc;n cứu v&agrave; ph&aacute;t triển khoa học dữ liệu sẽ gi&uacute;p tăng cường khả năng tiếp cận với dịch vụ chăm s&oacute;c sức khỏe của người d&acirc;n, đặc biệt ở c&aacute;c nước ngh&egrave;o, đồng thời gi&uacute;p họ n&acirc;ng cao kiến thức để bảo vệ ch&iacute;nh m&igrave;nh. Nguồn dữ liệu c&oacute; thể tới từ c&aacute;c thiết bị đeo tay đang được sử dụng phổ biến, tạo ra một lượng lớn dữ liệu như th&acirc;n nhiệt v&agrave; c&aacute;c phản ứng của cơ thể cho ph&eacute;p ph&aacute;t hiện c&aacute;c cơn sốt.&nbsp;</p>\r\n\r\n<p>&ldquo;Vai tr&ograve; của khoa học dữ liệu sẽ v&ocirc; c&ugrave;ng to lớn, kể cả l&agrave; dữ liệu c&oacute; sẵn, để Ch&iacute;nh phủ v&agrave; Bộ Y tế c&oacute; thể đưa ra quyết định do dữ liệu thay đổi li&ecirc;n tục&rdquo;, vị GS th&agrave;nh vi&ecirc;n của Viện Sức khỏe To&agrave;n cầu George, đồng thời l&agrave; th&agrave;nh vi&ecirc;n của Hội đồng Sơ khảo Giải thưởng VinFuture, nhận x&eacute;t.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng ta đang ở thời đại kh&ocirc;ng gian mới, thời đại kh&ocirc;ng gian thứ hai, v&agrave; con người đang chạy đua để đặt bước ch&acirc;n đầu ti&ecirc;n l&ecirc;n sao Hỏa. T&ocirc;i hy vọng rằng, cũng như việc chinh phục Mặt Trăng, ch&uacute;ng ta sẽ chứng kiến những &#39;tia lửa&#39; tương tự để cho ra đời những c&ocirc;ng nghệ v&agrave; đổi mới y tế trong tương lai&rdquo;, gi&aacute;o sư Schutte đ&atilde; đưa ra kết luận trong buổi Đối thoại khoa học đầu ti&ecirc;n do Quỹ VinFuture tổ chức.&nbsp;</p>\r\n\r\n<p>Tiếp nối sự th&agrave;nh c&ocirc;ng của cuộc Đối thoại đầu ti&ecirc;n với chủ đề sức khỏe, trong những kỳ tới, chuỗi sự kiện đối thoại khoa học trực tuyến của VinFuture sẽ tiếp tục b&agrave;n luận về vai tr&ograve; của khoa học gắn với c&aacute;c Mục ti&ecirc;u Ph&aacute;t triển Bền vững kh&aacute;c của Li&ecirc;n Hợp Quốc như m&ocirc;i trường bền vững, th&agrave;nh phố th&ocirc;ng minh, b&igrave;nh đẳng giới v&agrave; gi&aacute;o dục chất lượng.&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng qua hoạt động n&agrave;y, VinFuture kỳ vọng c&aacute;c nh&agrave; khoa học v&agrave; chuy&ecirc;n gia nghi&ecirc;n cứu c&oacute; tầm ảnh hưởng tr&ecirc;n thế giới sẽ truyền cảm hứng cho mọi th&agrave;nh phần trong x&atilde; hội, đặc biệt l&agrave; giới trẻ - thế hệ l&atilde;nh đạo tương lai của nh&acirc;n loại, theo đuổi những nghi&ecirc;n cứu, s&aacute;ng kiến nhằm giải quyết những vấn đề cấp thiết mang t&iacute;nh to&agrave;n cầu. Đồng thời, chuỗi sự kiện n&agrave;y cũng tạo điều kiện để c&aacute;c nh&agrave; khoa học chia sẻ những đ&oacute;ng g&oacute;p, cống hiến vĩ đại với đại ch&uacute;ng bằng ng&ocirc;n ngữ b&igrave;nh d&acirc;n, dễ hiểu, gi&uacute;p khoa học trở n&ecirc;n gần gũi v&agrave; trở th&agrave;nhmột phần kh&ocirc;ng thể t&aacute;ch rời của cuộc sống, g&oacute;p phần lan tỏa những gi&aacute; trị tốt đẹp của khoa học phụng sự nh&acirc;n loại trong cộng đồng.</p>\r\n', NULL, NULL),
(21, 7, 'Hoa Kỳ chấp nhận khách du lịch được tiêm phối hợp vắc xin', 'VHO- Trung tâm Kiểm soát và Phòng ngừa Dịch bệnh Hoa Kỳ (CDC) ngày 15.10 (giờ địa phương) cho biết, họ sẽ cho phép du khách quốc tế tiêm phối hợp 2 loại vắc xin khác nhau được phép nhập cảnh. Động thái nằm trong những nỗ lực của nước này nhằm thu hút khách du lịch từ Canada và một số nơi khác.', 'images/anh-quocte1.jpg', '<p style=\"text-align:center\">&Ocirc;ng Brian Higgins,&nbsp;đại diện cho một quận dọc theo bi&ecirc;n giới Canada&nbsp;đ&atilde;&nbsp;đặt c&acirc;u hỏi với CDC về việc,&nbsp;c&oacute; n&ecirc;n chấp nhận&nbsp;việc ti&ecirc;m chủng hỗn hợp hay kh&ocirc;ng. Trong&nbsp;đ&oacute;, c&oacute; việc kết hợp vắc&nbsp;xin mRNA&nbsp;với vắc xin của&nbsp;AstraZeneca</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46375/87077EF18F3A9-92E6-4CA1-978E-16C566D14C5B.jpeg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>H&agrave;nh kh&aacute;ch&nbsp;đeo khẩu trang bảo hộ&nbsp;tại s&acirc;n bay ở Denver, Colorado, Hoa Kỳ&nbsp;(Ảnh: Reuters)</strong></p>\r\n\r\n<p>L&yacute; giải&nbsp;về&nbsp;vấn&nbsp;đề n&agrave;y,&nbsp;ph&aacute;t ng&ocirc;n của CDC:&nbsp;&ldquo;Mặc d&ugrave;&nbsp;CDC&nbsp;kh&ocirc;ng khuyến kh&iacute;ch việc ti&ecirc;m&nbsp;phối hợp vắc&nbsp;xin&nbsp;như một giải ph&aacute;p ch&iacute;nh nhưng ch&uacute;ng&nbsp;t&ocirc;i nhận thấyviệc n&agrave;y ng&agrave;y c&agrave;ng phổ biến&nbsp;ở c&aacute;c&nbsp;quốc gia. Do&nbsp;đ&oacute;,&nbsp;ti&ecirc;m trộn n&ecirc;n&nbsp;được c&ocirc;ng nhận trong hồ sơ vắc&nbsp;xin&rdquo;.</p>\r\n\r\n<p>CDC Hoa Kỳ cũng nhấn mạnh lại:&nbsp;&ldquo;Những loại vắc xin n&agrave;o&nbsp;đ&atilde;&nbsp;được Cơ quan Quản l&yacute; Thực phẩm v&agrave; Dược phẩm&nbsp;FDA cho ph&eacute;p sử dụng, cũng như những loại&nbsp;được&nbsp;WHO&nbsp;c&ocirc;ng nhận&nbsp;sẽ&nbsp;được&nbsp;chấp thuận&nbsp;để nhập cảnh v&agrave;o&nbsp;đất nước,&nbsp;bao gồm cả vắc xin&nbsp;AstraZeneca&rdquo;.</p>\r\n\r\n<p>Về hướng dẫn,&nbsp;đơn&nbsp;vị n&agrave;y&nbsp;sẽ trả lời c&aacute;c c&acirc;u hỏi kh&aacute;c cũng như ban h&agrave;nh c&aacute;ch thức quản l&yacute;&nbsp;du kh&aacute;ch h&agrave;ng kh&ocirc;ng quốc tế trước ng&agrave;y 25.10.</p>\r\n\r\n<p>B&ecirc;n cạnh&nbsp;đ&oacute;, Hoa Kỳ cũng sẽ dỡ&nbsp;bỏ hạn chế&nbsp;đi lại với người nước ngo&agrave;i&nbsp;được ti&ecirc;m chủng&nbsp;đầy&nbsp;đủ vắc xin&nbsp;Covid-19 từ ng&agrave;y 8.11.&nbsp;Ph&aacute;t ng&ocirc;n&nbsp;vi&ecirc;n của Nh&agrave; Trắng,&nbsp;Kevin Munoz&nbsp;đ&atilde;&nbsp;x&aacute;c nhận, quy&nbsp;định mới sẽ&nbsp;được thực hiện nghi&ecirc;m ngặt v&agrave;&nbsp;đồng bộ. Phạm vi&nbsp;&aacute;p dụng sẽ bao gồm cả&nbsp;kh&aacute;ch nhập cảnh bằng&nbsp;đường bộ.</p>\r\n\r\n<p>Theo&nbsp;kế hoạch&nbsp;cụ thể,&nbsp;người kh&ocirc;ng mang&nbsp;quốc tịch&nbsp;Hoa Kỳ&nbsp;khi&nbsp;đến bằng&nbsp;đường h&agrave;ng&nbsp;kh&ocirc;ng sẽ phải&nbsp;đảm bảo&nbsp;đủ&nbsp;điều kiện,&nbsp;được&nbsp;ti&ecirc;m&nbsp;đủ 2 mũi vắc xin v&agrave; c&oacute; kết quả&nbsp;x&eacute;t nghiệm&nbsp;&acirc;m t&iacute;nh.&nbsp;H&agrave;nh kh&aacute;ch&nbsp;đường bộ&nbsp;sẽ&nbsp;&aacute;p dụng quy&nbsp;định tương tự về ti&ecirc;m chủng nhưng&nbsp;kh&ocirc;ng cần phải c&oacute;&nbsp;kết quả x&eacute;t nghiệm.</p>\r\n\r\n<p>Trước th&ocirc;ng tin tr&ecirc;n,&nbsp;Đại sứ Thụy&nbsp;Điển&nbsp;tại Mỹ&nbsp;Karin Olofsdotter&nbsp;đ&atilde; hoan ngh&ecirc;nh quyết&nbsp;định mở cửa v&agrave;&nbsp;gọi&nbsp;đ&acirc;y l&agrave;&nbsp;&ldquo;th&ocirc;ng tin&nbsp;đ&aacute;ng&nbsp;vui mừng&rdquo;.</p>\r\n\r\n<p>Nick Calio, Gi&aacute;m&nbsp;đốc&nbsp;Điều h&agrave;nh Tập&nbsp;đo&agrave;n Thương mại C&ocirc;ng&nbsp;nghiệp&nbsp;Airlines for America nhận&nbsp;định:&nbsp;&ldquo;Mở cửa lại ho&agrave;n to&agrave;n c&aacute;c chuyến bay quốc tế l&agrave; quyết&nbsp;định quan trọng&nbsp;để phục hồi lại nền&nbsp;du lịch,&nbsp;kinh tế to&agrave;n cầu, hỗ trợ h&agrave;ng triệu việc l&agrave;m cho người&nbsp;Hoa Kỳ v&agrave; nước ngo&agrave;i&rdquo;.</p>\r\n\r\n<p>Hoa Kỳ hiện&nbsp;đ&atilde; ti&ecirc;m&nbsp;vắc&nbsp;xin&nbsp;được cho 64,8% d&acirc;n số. Trong&nbsp;đ&oacute;, 55,9%&nbsp;đ&atilde; ti&ecirc;m chủng&nbsp;đầy&nbsp;đủ. Với những nỗ lực&nbsp;đẩy nhanh chiến dịch&nbsp;ti&ecirc;m chủng, Hoa Kỳ&nbsp;đang cố gắng nới lỏng c&aacute;c hạn chế&nbsp;đi lại vốn&nbsp;đ&atilde;&nbsp;được&nbsp;cựu Tổng thống Mỹ Donald Trump&nbsp;&aacute;p dụng với h&agrave;nh kh&aacute;ch Trung Quốc&nbsp;đầu năm&nbsp;2020. Danh s&aacute;ch hạn chế sau&nbsp;đ&oacute;&nbsp;đ&atilde; mở rộng tới h&agrave;ng chục quốc gia&nbsp;kh&aacute;c.</p>\r\n', NULL, NULL);
INSERT INTO `tinbai` (`id`, `idTheLoai`, `TieuDe`, `TrichDan`, `anhtrichdan`, `NoiDung`, `NgayDang`, `TacGia`) VALUES
(22, 7, 'Chiến lược “tín hiệu đèn giao thông” của New Zealand', 'VHO-Hình mẫu chống dịch “Zero Covid-19” New Zealand đang thể hiện rõ bước chuyển sang “sống chung với virus”, với kế hoạch vận hành chiến lược ứng phó mới bớt cứng rắn hơn, ngay khi độ phủ vắc xin ngừa Covid-19 đạt khoảng 90% dân số vào tháng 12 tới.', 'images/anh-quocte2.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46630/87660tiem-chung-new-zealand.jpeg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>New Zealand th&uacute;c đẩy ti&ecirc;m chủng để thực hiện chiến lược ứng ph&oacute; mới với Covid-19 Ảnh: NEW YORK TIMES</strong></p>\r\n\r\n<p>Từng l&agrave; &ldquo;th&agrave;nh tr&igrave;&rdquo; vững chắc kh&ocirc;ng Covid-19 trong suốt nhiều th&aacute;ng đầu năm 2021, New Zealand dường như &ldquo;l&atilde;ng qu&ecirc;n&rdquo; chiến dịch ti&ecirc;m chủng vắc xin ngừa Covid-19 cho phần đ&ocirc;ng d&acirc;n số. Đến giữa th&aacute;ng 8, nước n&agrave;y mới chỉ c&oacute; khoảng 20% d&acirc;n số được ti&ecirc;m ph&ograve;ng đầy đủ. V&agrave; khi biến thể Delta x&acirc;m nhập, khiến chiến lược &ldquo;kh&ocirc;ng virus&rdquo; của New Zealand trở n&ecirc;n bất khả thi, nước n&agrave;y đ&atilde; ch&uacute; trọng hơn tới gi&aacute; trị của &ldquo;tấm khi&ecirc;n&rdquo; vắc xin. Thủ tướng New Zealand Jacinda Ardern cho biết: &ldquo;Ch&uacute;ng t&ocirc;i c&oacute; một c&ocirc;ng cụ mới mang &yacute; nghĩa khi Covid-19 đ&atilde; thay đổi. Ch&uacute;ng t&ocirc;i cũng c&oacute; thể thay đổi. Thay v&igrave; tiếp tục phong tỏa, ch&uacute;ng t&ocirc;i c&oacute; thể tiến về ph&iacute;a trước một c&aacute;ch an to&agrave;n v&agrave; tự tin. C&ocirc;ng cụ đ&oacute; ch&iacute;nh l&agrave; vắc xin&rdquo;.</p>\r\n\r\n<p>Để th&uacute;c đẩy tỉ lệ bao phủ vắc xin ngừa Covid-19, c&aacute;c địa phương tại New Zealand đ&atilde; đưa ra nhiều phương thức khuyến kh&iacute;ch người d&acirc;n tham gia ti&ecirc;m chủng. Trong khi ở th&agrave;nh phố Hamilton, kh&aacute;ch h&agrave;ng của chuỗi McDonald&rsquo;s c&oacute; thể nhận được một ưu đ&atilde;i đặc biệt l&agrave; một mũi ti&ecirc;m vắc xin Pfizer c&ugrave;ng với combo b&aacute;nh m&igrave; kẹp thịt, th&igrave; tại Christchurch, người đi ti&ecirc;m sẽ được mời m&oacute;n c&aacute; v&agrave; khoai t&acirc;y chi&ecirc;n. Nhiều nơi kh&aacute;c lại kh&iacute;ch lệ người d&acirc;n ti&ecirc;m chủng bằng những m&oacute;n qu&agrave; như tặng s&ocirc;-c&ocirc;-la, phiếu mua h&agrave;ng si&ecirc;u thị... Ngo&agrave;i ra, tại những v&ugrave;ng hẻo l&aacute;nh, New Zealand cũng tăng cường c&aacute;c lực lượng chức năng, hỗ trợ người d&acirc;n đến c&aacute;c trung t&acirc;m ti&ecirc;m chủng. Với nhiều nỗ lực tăng tốc ti&ecirc;m ph&ograve;ng, đến ng&agrave;y 21.10, tỉ lệ ti&ecirc;m chủng đủ 2 liều vắc xin cho người từ 12 tuổi trở l&ecirc;n của New Zealand đạt 66% v&agrave; 83% đ&atilde; ti&ecirc;m &iacute;t nhất một liều vắc xin. Nước n&agrave;y đặt mục ti&ecirc;u sẽ ti&ecirc;m chủng đủ 2 liều vắc xin ngừa Covid-19 cho 90% d&acirc;n số v&agrave;o th&aacute;ng 12 tới.</p>\r\n\r\n<p>Thực hiện lộ tr&igrave;nh &ldquo;sống chung&rdquo; với Covid-19, New Zealand cũng đưa ra chiến lược ứng ph&oacute; mới, theo hướng nới lỏng hơn so với hiện h&agrave;nh, &aacute;p dụng khi nước n&agrave;y đạt chạm ngưỡng 90% d&acirc;n số được ti&ecirc;m chủng đủ liều vắc xin. Chiến lược mới sẽ hoạt động tương tự như hệ thống t&iacute;n hiệu của đ&egrave;n giao th&ocirc;ng, bao gồm 3 cấp độ, tương ứng với c&aacute;c mức cảnh b&aacute;o &ldquo;m&agrave;u đỏ&rdquo;, &ldquo;m&agrave;u cam&rdquo; v&agrave; &ldquo;m&agrave;u xanh&rdquo;. Trong đ&oacute;, &ldquo;m&agrave;u đỏ&rdquo; sẽ được &aacute;p dụng khi dịch bệnh b&ugrave;ng ph&aacute;t mạnh mẽ v&agrave; hệ thống y tế chịu &aacute;p lực lớn từ số ca nhập viện. Điểm kh&aacute;c biệt so với quy định hiện h&agrave;nh l&agrave; ngay cả khi cấp độ cảnh b&aacute;o &ldquo;m&agrave;u đỏ&rdquo;, doanh nghiệp vẫn được hoạt động v&agrave; người lao động khi đi l&agrave;m cần c&oacute; chứng nhận ti&ecirc;m chủng, chỉ giới hạn số lượng người xuất hiện trong c&ugrave;ng một thời điểm. Đồng thời, mọi người được khuyến kh&iacute;ch l&agrave;m việc tại nh&agrave;. Ở những địa điểm kh&ocirc;ng y&ecirc;u cầu chứng nhận ti&ecirc;m chủng, như si&ecirc;u thị, cửa h&agrave;ng b&aacute;n lẻ h&agrave;ng h&oacute;a thiết yếu sẽ &aacute;p dụng h&igrave;nh thức giao dịch kh&ocirc;ng tiếp x&uacute;c.</p>\r\n\r\n<p>Trong khi đ&oacute;, đối với mức cảnh b&aacute;o &ldquo;m&agrave;u cam&rdquo;, được &aacute;p dụng khi xuất hiện đợt b&ugrave;ng ph&aacute;t dịch ở quy m&ocirc; hạn chế nhưng c&oacute; nguy cơ l&acirc;y lan cao, c&aacute;c doanh nghiệp sẽ kh&ocirc;ng bị giới hạn hoạt động, nhưng y&ecirc;u cầu người lao động giữ khoảng c&aacute;ch an to&agrave;n. Khi ấy, c&aacute;c trường học được mở cửa nhưng phải tu&acirc;n thủ chặt chẽ c&aacute;c biện ph&aacute;p ph&ograve;ng dịch. V&agrave; ở mức cảnh b&aacute;o &ldquo;m&agrave;u xanh&rdquo;, được ban h&agrave;nh khi xuất hiện một số ca mắc Covid-19 v&agrave; &iacute;t c&oacute; khả năng l&acirc;y lan ra cộng đồng. Ở giai đoạn n&agrave;y, hầu như kh&ocirc;ng c&oacute; hạn chế đối với những người đ&atilde; ti&ecirc;m chủng đầy đủ. Tuy nhi&ecirc;n, tại c&aacute;c địa điểm kh&ocirc;ng y&ecirc;u cầu giấy chứng nhận ti&ecirc;m chủng, người d&acirc;n sẽ phải đeo khẩu trang v&agrave; giới hạn tụ tập ở mức tối đa l&agrave; 100 người tại c&ugrave;ng một thời điểm.</p>\r\n\r\n<p>B&ecirc;n cạnh quy định ph&ograve;ng dịch mới, Ch&iacute;nh phủ New Zealand c&ograve;n đưa ra chương tr&igrave;nh hỗ trợ t&agrave;i ch&iacute;nh cho c&aacute;c doanh nghiệp v&agrave; người lao động bị ảnh hưởng bởi dịch bệnh, với khoản kinh ph&iacute; dự kiến khoảng 470 triệu NZD mỗi tuần. R&otilde; r&agrave;ng, Ch&iacute;nh phủ New Zealand đang cho thấy nỗ lực th&iacute;ch ứng với diễn biến mới của dịch bệnh, nhằm sớm đưa c&aacute;c hoạt động kinh tế - x&atilde; hội của nước n&agrave;y trở lại trạng th&aacute;i b&igrave;nh thường. Việc điều chỉnh chiến lược chống dịch của New Zealand cũng khẳng định sự cần thiết phải nới lỏng c&aacute;c biện ph&aacute;p hạn chế, để giảm thiểu t&aacute;c động ti&ecirc;u cực đến nền kinh tế, cũng như đời sống x&atilde; hội, khi dịch bệnh chưa r&otilde; hồi kết.&nbsp;</p>\r\n', NULL, NULL),
(23, 7, 'Đảo Jeju hướng tới phát triển du lịch không khí thải carbon, rác thải', 'VHO- Để góp phần vào những nỗ lực giải quyết các vấn đề này cũng như tạo dựng một nền tảng du lịch bền vững, chính quyền đảo Jeju đã triển khai các chương trình du lịch xanh thân thiện với môi trường.', 'images/anh-quocte3.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t1000/uploaded/ivpycivo/2021_10_23/jeju_16.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Đảo Jeju ở miền Nam H&agrave;n Quốc. (Nguồn: Republic of Korea/Flickr)</strong></p>\r\n\r\n<p>B&ecirc;n cạnh đại dịch Đ v&agrave; vấn đề biến đổi kh&iacute; hậu, người d&acirc;n tr&ecirc;n khắp thế giới ng&agrave;y c&agrave;ng lo ngại về những vấn đề li&ecirc;n quan đến m&ocirc;i trường.&nbsp;</p>\r\n\r\n<p>Để g&oacute;p phần v&agrave;o những nỗ lực giải quyết c&aacute;c vấn đề n&agrave;y cũng như tạo dựng một nền tảng du lịch bền vững, ch&iacute;nh quyền đảo Jeju (H&agrave;n Quốc) đ&atilde; triển khai c&aacute;c chương tr&igrave;nh du lịch xanh th&acirc;n thiện với m&ocirc;i trường để c&ugrave;ng thế giới hướng tới mục ti&ecirc;u kh&ocirc;ng kh&iacute; thải carbon, trong đ&oacute; c&oacute; chương tr&igrave;nh &quot;E-RUN&quot; (t&ecirc;n viết tắt của &quot;Eco-Run&quot;).</p>\r\n\r\n<p>&quot;E-RUN&quot; l&agrave; một chương tr&igrave;nh du lịch hướng tới trung h&ograve;a kh&iacute; thải carbon, g&oacute;p phần bảo tồn m&ocirc;i trường tự nhi&ecirc;n với những trải nghiệm hết sức độc đ&aacute;o v&agrave; th&uacute; vị. Từ s&acirc;n bay quốc tế Jeju, một trong những cửa ng&otilde; ch&iacute;nh để bắt đầu h&agrave;nh tr&igrave;nh du lịch đảo Jeju, người tham gia chương tr&igrave;nh sẽ trải nghiệm hoạt động đi bộ theo một tuyến đường được chỉ định trước (Yongdam-Dodu-Iho) trong khi thực hiện c&aacute;c sứ mệnh cho c&aacute;c chiến dịch xanh như kh&ocirc;ng sử dụng t&uacute;i nilon, đồ nhựa, c&aacute;c hoạt động lặn biển l&agrave;m sạch đại dương kết hợp đi bộ tr&ecirc;n đường m&ograve;n qua c&aacute;c khu rừng nguy&ecirc;n sinh, trải nghiệm xe điện th&acirc;n thiện m&ocirc;i trường... &quot;E-RUN&quot; năm nay c&oacute; sự tham gia của một số ph&oacute;ng vi&ecirc;n nước ngo&agrave;i thường tr&uacute; tại H&agrave;n Quốc.</p>\r\n\r\n<p>Ngo&agrave;i &quot;E-RUN&quot;, ch&iacute;nh quyền đảo Jeju cũng triển khai chương tr&igrave;nh đi bộ &quot;Jeju Olle&quot;, một cơ hội hiếm c&oacute; để du kh&aacute;ch c&oacute; cơ hội được ngắm nh&igrave;n to&agrave;n bộ sự lộng lẫy của h&ograve;n đảo Jeju với ưu ti&ecirc;n h&agrave;ng đầu l&agrave; sức khỏe v&agrave; sự an to&agrave;n của những người tham gia.</p>\r\n\r\n<p>Với tổng số 23 con đường m&ograve;n chạy quanh đảo, du kh&aacute;ch c&oacute; cơ hội được h&iacute;t thở kh&ocirc;ng kh&iacute; trong l&agrave;nh khi đi bộ len lỏi giữa thi&ecirc;n nhi&ecirc;n với c&aacute;c loại thảm thực vật nguy&ecirc;n sinh. Với chủ đề &quot;We Walk: Apart Together&quot;, chương tr&igrave;nh năm nay được d&agrave;n trải cả về thời gian v&agrave; kh&ocirc;ng gian để đảm bảo an to&agrave;n tối đa cho những người tham gia. Để đảm bảo an to&agrave;n, chương tr&igrave;nh chỉ d&agrave;nh ri&ecirc;ng cho những người đ&atilde; ti&ecirc;m chủng đầy đủ ngừa Covid-19 v&agrave; hạn chế số người.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t1000/uploaded/ivpycivo/2021_10_23/jejuu_1.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Nh&oacute;m ph&oacute;ng vi&ecirc;n nước ngo&agrave;i tham gia E-RUN chụp ảnh lưu niệm c&ugrave;ng cảnh s&aacute;t biển địa phương v&agrave; th&agrave;nh quả sau hơn 30 ph&uacute;t lặn biển dưới c&aacute;i lạnh 4 độ C, ng&agrave;y 20.10.2021. (Ảnh: Anh Nguy&ecirc;n/TTXVN)</strong></p>\r\n\r\n<p>Tiếp đ&oacute; l&agrave; chương tr&igrave;nh lặn biển thu gom r&aacute;c thải. Trong khu&ocirc;n khổ &quot;E-RUN&quot;, người tham gia được trải nghiệm chương tr&igrave;nh lặn biển thu gom r&aacute;c thải gi&uacute;p lấy lại m&ocirc;i trường sạch cho đại dương. Với sự hỗ trợ của lực lượng cảnh s&aacute;t biển v&agrave; c&aacute;c thợ lặn chuy&ecirc;n nghiệp, người tham gia sẽ được chia theo nh&oacute;m c&ugrave;ng với lực lượng cảnh s&aacute;t biển v&agrave; thợ lặn thực hiện nhiệm vụ dọn r&aacute;c thải ở đại dương.</p>\r\n\r\n<p>Cuối c&ugrave;ng l&agrave; Triển l&atilde;m c&aacute;c sản phẩm t&aacute;i chế từ r&aacute;c thải. Tham gia &quot;E-RUN&quot;, người chơi cũng được tận mắt chứng kiến những sản phẩm được l&agrave;m ra từ ch&iacute;nh c&aacute;c loại r&aacute;c thải được thu gom từ đại dương. Triển l&atilde;m mang t&ecirc;n &quot;The Filter&quot; (Bộ lọc) với sự hợp t&aacute;c giữa Trung t&acirc;m Cộng đồng Iho-dong v&agrave; Ủy ban Tự trị Cư d&acirc;n Iho-dong, l&agrave; một dự &aacute;n nhằm biến c&aacute;c loại r&aacute;c thải thu gom từ đại dương th&agrave;nh c&aacute;c sản phẩm văn h&oacute;a của B&atilde;i biển Iho Tewoo, đồng thời cũng l&agrave; một phần trong chiến dịch bảo vệ đại dương của h&ograve;n đảo Jeju.</p>\r\n\r\n<p>Theo Tổ chức Du lịch Jeju, khẩu hiệu của triển l&atilde;m n&agrave;y l&agrave; &quot;Biển ở Jeju l&agrave; s&acirc;n chơi v&agrave; l&agrave; nơi cần được bảo tồn&quot;. Cũng giống như bộ lọc c&oacute; thể l&agrave;m sạch nước v&agrave; bộ lọc của chương tr&igrave;nh chỉnh sửa ảnh c&oacute; thể cho ph&eacute;p chỉnh sửa h&igrave;nh ảnh, Filter nhằm mục đ&iacute;ch &quot;biến đại dương bị &ocirc; nhiễm trở th&agrave;nh một nơi tuyệt đẹp th&ecirc;m một lần nữa.&quot;</p>\r\n\r\n<p>Gi&aacute;m đốc Tổ chức Du lịch Jeju Yang Pil-soo cho biết Triển l&atilde;m Filter l&agrave; một phần trong chiến dịch của đảo Jeju nhằm x&acirc;y dựng nhận thức tốt hơn về tầm quan trọng của m&ocirc;i trường biển bằng c&aacute;ch kết nối nội dung văn h&oacute;a v&agrave; du lịch với r&aacute;c thải ở biển Jeju vốn đ&atilde; trở th&agrave;nh mối lo thường trực (c&ugrave;ng với đại dịch Covid-19) của người d&acirc;n tr&ecirc;n đảo.</p>\r\n\r\n<p>Chương tr&igrave;nh E-RUN năm nay, với sự tham gia của c&aacute;c ph&oacute;ng vi&ecirc;n nước ngo&agrave;i, sẽ l&agrave; một v&iacute; dụ điển h&igrave;nh cho việc t&aacute;i chế t&agrave;i nguy&ecirc;n, nơi c&aacute;c ph&oacute;ng vi&ecirc;n, người d&acirc;n, c&aacute;c nh&oacute;m bảo tồn m&ocirc;i trường tr&ecirc;n đảo v&agrave; c&aacute;c thương hiệu nổi tiếng to&agrave;n cầu c&ugrave;ng hợp t&aacute;c để thu gom r&aacute;c thải v&agrave; l&agrave;m sạch đại dương.</p>\r\n', NULL, NULL),
(24, 7, 'Chuyên gia cảnh báo về làn sóng lây nhiễm nguy hiểm ở Châu Âu', 'VHO- Mặc dù dự báo mùa đông năm nay, các nước châu Âu sẽ có nhiều vắc xin hơn nhưng hiện khu vực này vẫn là nơi duy nhất trên thế giới báo cáo sự gia tăng các ca nhiễm Covid-19. Theo Tổ chức Y tế Thế giới WHO, đây đã là tuần thứ 3 liên tiếp Châu Âu ghi nhân xu hướng gia tăng các ca mắc.', 'images/anh_quocte_24.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46660/87705%E1%BA%A2NH-1-COVID-19-CH%C3%82U-%C3%82U.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Người d&acirc;n biểu t&igrave;nh phản đối c&aacute;c hạn chế của Covid-19 ở Bulgaria (Ảnh: CNN)</strong></p>\r\n\r\n<p><strong>&ldquo;M&ugrave;a đ&ocirc;ng thảm khốc&rdquo;</strong></p>\r\n\r\n<p>Nỗi đau mất người th&acirc;n đang bao phủ khắp Đ&ocirc;ng &Acirc;u v&agrave; Nga khi số ca tử vong ng&agrave;y c&agrave;ng tăng. Hầu hết, c&aacute;c ca bệnh tử vong l&agrave; do sự chần chừ trong việc ti&ecirc;m vắc xin.</p>\r\n\r\n<p>Thứ 5 tuần trước, Latvia đ&atilde; trở th&agrave;nh quốc gia đầu ti&ecirc;n trong Li&ecirc;n minh Ch&acirc;u &Acirc;u bị &aacute;p đặt lệnh cấm vận khi quốc gia n&agrave;y phải vật lộn với t&igrave;nh trạng c&aacute;c ca nhiễm tăng, nhưng tỷ lệ ti&ecirc;m chủng thấp. Theo số liệu thống k&ecirc;, chỉ 56% tổng số người trưởng th&agrave;nh ở đ&acirc;y được ti&ecirc;m chủng đầy đủ. Trong khi đ&oacute;, tỷ lệ trung b&igrave;nh của EU l&ecirc;n tới 74,6%.</p>\r\n\r\n<p>Ph&iacute;a T&acirc;y &Acirc;u hiện cũng đang ghi nhận số ca mắc mới tăng d&ugrave; mức độ bao phủ vắc xin cao. Theo đ&oacute; kể từ th&aacute;ng 5, tỷ lệ mắc Covid-19 của Đức lần đầu ti&ecirc;n tăng l&ecirc;n 100 ca nhiễm mới tr&ecirc;n 100.000 d&acirc;n v&agrave;o thứ 7. Bỉ v&agrave; Ireland cũng đang chứng kiến đ&agrave; l&acirc;y lan khi tỷ lệ mắc bệnh lần lượt l&agrave; 325,76 v&agrave; 432,84 tr&ecirc;n 100.000 người.</p>\r\n\r\n<p>Bộ trưởng Y tế Bỉ Frank Vandenbrouck đ&atilde; trả lời phỏng vấn tr&ecirc;n truyền h&igrave;nh rằng, hơn 85% d&acirc;n số nước n&agrave;y đ&atilde; được chủng ngừa đầy đủ. C&aacute;c ca bệnh nặng phải nhập viện hầu hết đều rơi v&agrave;o những người chưa ti&ecirc;m ph&ograve;ng.</p>\r\n\r\n<p>Chuy&ecirc;n gia sức khỏe to&agrave;n cầu tại Trường Kinh doanh Sa&iuml;d thuộc Đại học Oxford (Anh) n&oacute;i với CNN rằng: &ldquo;Việc c&aacute;c ca bệnh tăng l&agrave; do nới lỏng c&aacute;c hạn chế khi mở cửa nền kinh tế. Thời tiết lạnh dần v&agrave; biến thể si&ecirc;u l&acirc;y nhiễm Delta cũng đang l&agrave;m t&igrave;nh h&igrave;nh trở n&ecirc;n tồi tệ hơn&rdquo;.</p>\r\n\r\n<p>Tỷ lệ ti&ecirc;m chủng của Đ&ocirc;ng v&agrave; T&acirc;y Ch&acirc;u &Acirc;u đang theo 2 hướng ri&ecirc;ng biệt. Nhờ c&oacute; vắc xin, tỷ lệ tử vong của c&aacute;c quốc gia T&acirc;y &Acirc;u phần lớn kh&ocirc;ng tăng so với c&aacute;c nước Đ&ocirc;ng &Acirc;u.</p>\r\n\r\n<p>Từ thứ 2, Romania đ&atilde; &aacute;p dụng lệnh giới nghi&ecirc;m ban đ&ecirc;m v&agrave; bắt buộc phải vượt qua kiểm tra sức khỏe ở hầy hết c&aacute;c địa điểm. Y&ecirc;u cầu được đưa ra khi Romania ghi nhận 19,25 ca tử vong tr&ecirc;n 1 triệu d&acirc;n. Tỷ l&ecirc; n&agrave;y được cho l&agrave; cao nhất thế giới.</p>\r\n\r\n<p>T&igrave;nh trạng khẩn cấp của Romania kh&ocirc;ng đến từ t&igrave;nh trạng thiếu vắc xin. C&aacute;c nước thuộc EU đều c&oacute; quyền tiếp cận với c&aacute;c loại vắc xin đ&atilde; được ph&ecirc; duyệt. Thế nhưng kh&ocirc;ng giống như nhiều quốc gia kh&aacute;c, việc triển khai vắc xin của Romania đ&atilde; bị cản trở bởi t&acirc;m l&yacute; chần chừ ti&ecirc;m. T&iacute;nh đến nay, chỉ c&oacute; 35,6% d&acirc;n số tại đ&acirc;y được ti&ecirc;m đủ 2 liều.</p>\r\n\r\n<p>Nước l&aacute;ng giềng Ukraine cũng rơi v&agrave;o t&igrave;nh cảnh tương tự khi v&agrave;o thứ 5 tuần qua, đất nước ghi nhận kỷ lục 22.415 ca nhiễm. C&aacute;c trường học ở nhiều điểm n&oacute;ng về Covid-19 của Ukraine đ&atilde; phải đ&oacute;ng cửa.</p>\r\n\r\n<p>Tổng thống Volodymyr Zelensky đ&atilde; phải k&ecirc;u gọi người d&acirc;n đi ti&ecirc;m v&agrave; khẳng định, chỉ c&oacute; vắc xin mới khiến t&igrave;nh trạng tốt hơn: &ldquo;Ch&uacute;ng ta chỉ c&oacute; 2 lựa chọn. Ti&ecirc;m chủng hoặc đ&oacute;ng cửa đất nước. Mỗi ng&agrave;y ch&uacute;ng ta đều phải đối mặt với th&aacute;ch thức v&igrave; số ca mắc mới tăng nhanh. T&ocirc;i ho&agrave;n to&agrave;n phản đối việc phải đ&oacute;ng cửa&rdquo;.</p>\r\n\r\n<p>Hay với Nga, giới chức thừa nhận người d&acirc;n của họ đang phải trải qua m&ugrave;a đ&ocirc;ng &ldquo;thảm khốc&rdquo;. Số trường hợp tử vong cao nhất trong 1 ng&agrave;y đ&atilde; từng đạt tới con số 1.028. Người ph&aacute;t ng&ocirc;n của Tổng thống Vladimir Putin, Dmitry Peskov n&oacute;i với c&aacute;c nh&agrave; b&aacute;o: &ldquo;C&ocirc;ng d&acirc;n của ch&uacute;ng ta cần c&oacute; tr&aacute;ch nhiệm hơn v&agrave; đi ti&ecirc;m ph&ograve;ng ngay lập tức&rdquo;.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46660/87704%E1%BA%A3nh-2-Covid-ch%C3%A2u-%C3%A2u.jpeg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>&nbsp;Ti&ecirc;m vắc xin th&ocirc;i l&agrave; kh&ocirc;ng đủ. C&aacute;c chuy&ecirc;n gia y tế đang th&uacute;c giục Ch&iacute;nh phủ Anh &aacute;p đặt th&ecirc;m c&aacute;c lệnh hạn chế như đeo khẩu trang (Ảnh: CNN)</strong></p>\r\n\r\n<p><strong>Một giải ph&aacute;p kh&ocirc;ng thể l&agrave;m n&ecirc;n tất cả</strong></p>\r\n\r\n<p>Sự xuất hiện của vắc xin đ&atilde; thay đổi t&igrave;nh h&igrave;nh ở nhiều quốc gia v&agrave; khiến nhiều người lạc quan hơn. D&ugrave; rằng giữ vai tr&ograve; ti&ecirc;n quyết trong ph&ograve;ng ngừa dịch bệnh nhưng vắc xin vẫn cần đi k&egrave;m với nhiều biện ph&aacute;p bảo vệ bản th&acirc;n kh&aacute;c.</p>\r\n\r\n<p>Vương Quốc Anh hiện đang ghi nhận nhiều ca bệnh hằng ng&agrave;y nhất ở T&acirc;y &Acirc;u sau khi loại bỏ gần như tất cả c&aacute;c hạn chế về gi&atilde;n c&aacute;ch x&atilde; hội. Nhiều chuy&ecirc;n gia y tế v&agrave; c&ocirc;ng đo&agrave;n y tế của Anh đ&atilde; phải cầu xin Ch&iacute;nh phủ &aacute;p đặt lại c&aacute;c biện ph&aacute;p bắt buộc như đeo khẩu trang hoặc ti&ecirc;m vắc xin.</p>\r\n\r\n<p>Bất chấp những lời k&ecirc;u gọi, Ch&iacute;nh phủ Anh vẫn b&aacute;c bỏ c&aacute;c động th&aacute;i ngay cả khi số người nhập viện v&agrave; tử vong gia tăng. Katherine Henderson, Chủ tịch Trường Cao đẳng Chăm s&oacute;c Khẩn cấp Ho&agrave;ng gia Anh chia sẻ với Sky News: &ldquo;Dịch vụ y tế của Anh đang rơi v&agrave;o t&igrave;nh trạng qu&aacute; tải do Covid-19. C&aacute;c khoa cấp cứu tr&ecirc;n to&agrave;n quốc đang phải gồng m&igrave;nh để xử l&yacute; &ldquo;h&agrave;ng d&agrave;i&rdquo; xe cứu thương xếp b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p>Thay v&agrave;o đ&oacute;, Thủ tướng Anh Boris Johnson chỉ k&ecirc;u gọi những người tr&ecirc;n 50 tuổi c&oacute; nguy cơ cao mắc Covid-19 ti&ecirc;m mũi bổ sung nhằm khắc phục sự bảo vệ suy yếu từ vắc xin sau 6 th&aacute;ng.</p>\r\n\r\n<p>Nhiều chuy&ecirc;n gia y tế cho rằng, điều n&agrave;y l&agrave; kh&ocirc;ng đủ khi SARS-CoV-2 ho&agrave;n to&agrave;n c&oacute; thể sinh ra những biến thể mới kh&oacute; lường hơn. Cơ quan An ninh Y tế Vương quốc Anh mới đ&acirc;y đ&atilde; phải &ldquo;lưu &yacute;&rdquo; đến biến thể AY.4.2, biến thể c&oacute; thể l&agrave; &ldquo;hậu duệ&rdquo; của Delta.</p>\r\n\r\n<p>Chuy&ecirc;n gia y tế Drobac cho biết: &ldquo;Nếu chỉ để vắc xin l&agrave;m hết tất cả mọi việc sẽ l&agrave; kh&ocirc;ng đủ. Vắc xin quan trong số 1 nhưng sẽ ra sao đối với những trẻ nhỏ chưa đủ điều kiện về độ tuổi để ti&ecirc;m vắc xin? &ldquo;.</p>\r\n\r\n<p>Mike Ryan, Gi&aacute;m đốc Điều h&agrave;nh Chương tr&igrave;nh khẩn cấp Y tế của WHO nhấn mạnh: &ldquo;Ch&acirc;u &Acirc;u cần thận trọng hơn trong c&aacute;c quyết định ph&ograve;ng, chống dịch bệnh. Ch&uacute;ng ta kh&ocirc;ng biết 2, 3 th&aacute;ng nữa t&igrave;nh h&igrave;nh sẽ ra sao. Song song với việc ti&ecirc;m vắc xin, c&aacute;c biện ph&aacute;p như đeo khẩu trang cần được tiến h&agrave;nh nghi&ecirc;m ngặt&rdquo;.</p>\r\n', NULL, NULL),
(25, 7, 'Châu Á nỗ lực mở cửa du lịch', 'VHO- Ngay khi kiểm soát được dịch bệnh, các quốc gia ở châu Á đã thực hiện các giải pháp có thể nhằm đón khách quốc tế trở lại.', 'images/anh-quocte5.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/45984/86176nepal.jpg\" style=\"height:400px; width:598px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>Nepal nới lỏng hạn chế đ&uacute;ng thời điểm bắt đầu m&ugrave;a leo n&uacute;i v&agrave;o m&ugrave;a thu</strong></p>\r\n\r\n<p>Nepal đ&atilde; bắt đầu nối lại việc cấp thị thực (visa) cho tất cả kh&aacute;ch du lịch đ&atilde; ti&ecirc;m vắc xin ph&ograve;ng Covid-19. Tất cả du kh&aacute;ch tới Nepal vẫn sẽ phải x&eacute;t nghiệm Covid-19 ngay khi nhập cảnh. Với những du kh&aacute;ch chưa ti&ecirc;m chủng vắc xin ngừa Covid-19 th&igrave; phải c&aacute;ch ly trong v&ograve;ng 10 ng&agrave;y. Quyết định n&agrave;y được đưa ra đ&uacute;ng thời điểm bắt đầu m&ugrave;a leo n&uacute;i v&agrave;o m&ugrave;a thu khiến nhiều người hy vọng sẽ mang du kh&aacute;ch trở lại. Người ph&aacute;t ng&ocirc;n Bộ Du lịch Nepal Tara Nath Adhikari cho biết, quyết định nối lại việc cấp thị thực l&agrave; nhằm mở cửa trở lại ng&agrave;nh du lịch, vốn l&agrave; một trong những trụ cột của nền kinh tế Nepal. Hiệp hội Leo n&uacute;i Nepal (NMA) cũng nhận định đ&acirc;y l&agrave; quyết định rất quan trọng, c&oacute; thể gi&uacute;p gia tăng lượng kh&aacute;ch du lịch quốc tế đến nước n&agrave;y.</p>\r\n\r\n<p>Ấn Độ cũng đang dự định cấp miễn ph&iacute; 500.000 thị thực du lịch để chuẩn bị nối lại hoạt động của ng&agrave;nh du lịch sau hơn một năm đ&oacute;ng cửa. Hiện Ấn Độ đang đ&agrave;m ph&aacute;n với c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng quốc tế để l&ecirc;n lịch tr&igrave;nh nối lại c&aacute;c chuyến bay giữa nước n&agrave;y v&agrave; c&aacute;c thị trường ch&iacute;nh ở Bắc Mỹ v&agrave; ch&acirc;u &Acirc;u. Ấn Độ cũng đang t&iacute;nh đến việc x&acirc;y dựng những ch&iacute;nh s&aacute;ch linh hoạt để th&uacute;c đẩy du lịch v&agrave; hợp t&aacute;c với c&aacute;c nước trong khu vực để chuẩn bị kế hoạch thiết lập c&aacute;c h&agrave;nh lang du lịch để th&uacute;c đẩy du lịch an to&agrave;n hậu Covid-19.</p>\r\n\r\n<p>Thủ tướng Campuchia, Samdech Techo Hun Sen đ&atilde; y&ecirc;u cầu Bộ Du lịch nước n&agrave;y c&acirc;n nhắc mở cửa trở lại cho du kh&aacute;ch quốc tế đ&atilde; ti&ecirc;m đủ liều vắc xin ngừa Covid-19 nhập cảnh v&agrave;o Campuchia với thời gian c&aacute;ch ly 7 ng&agrave;y. Theo đ&oacute;, việc kh&ocirc;i phục trở lại ng&agrave;nh c&ocirc;ng nghiệp du lịch ở Campuchia sẽ thực hiện theo 2 bước, đầu ti&ecirc;n l&agrave; hướng tới du lịch nội địa v&agrave; tiếp theo l&agrave; cho ph&eacute;p những du kh&aacute;ch nước ngo&agrave;i đ&atilde; ti&ecirc;m đủ liều vắc xin được nhập cảnh với thời gian c&aacute;ch ly 7 ng&agrave;y. Trong thời gian c&aacute;ch ly, du kh&aacute;ch thuộc diện n&agrave;y sẽ kh&ocirc;ng bị hạn chế lưu tr&uacute; trong kh&aacute;ch sạn, m&agrave; c&oacute; thể đi lại trong khu vực nơi họ đến theo sự sắp xếp của Bộ chủ quản hoặc ch&iacute;nh quyền địa phương. Thủ tướng Campuchia Samdech Techo Hun Sen gợi &yacute; rằng trong kịch bản n&agrave;y, nếu du kh&aacute;ch tới trung t&acirc;m du lịch Siem Reap, trong phạm vi h&agrave;nh ch&iacute;nh của tỉnh, họ c&oacute; thể đi thăm c&aacute;c đền đ&agrave;i nổi tiếng tại đ&acirc;y.</p>\r\n\r\n<p>Chưa c&ocirc;ng bố kế hoạch đ&oacute;n kh&aacute;ch du lịch quốc tế, nhưng Malaysia đ&atilde; c&oacute; kế hoạch &ldquo;phục hồi&rdquo; du lịch nội địa. Bộ trưởng Du lịch, Nghệ thuật v&agrave; Văn h&oacute;a Malaysia Nancy Shukri cho bi&ecirc;́t khu giải tr&iacute; Genting, th&agrave;nh phố cổ Melaka v&agrave; đảo Tioman sẽ tham gia v&agrave;o &ldquo;bong bóng du lịch&rdquo;. B&agrave; cũng cho biết nhiều địa điểm sẽ sớm được th&ecirc;m v&agrave;o &ldquo;bong b&oacute;ng du lịch&rdquo; nội địa. Trước đ&oacute;, Langkawi đ&atilde; trở th&agrave;nh địa điểm đầu ti&ecirc;n đ&oacute;n du kh&aacute;ch tham quan trở lại sau thời gian d&agrave;i Malaysia &aacute;p dụng c&aacute;c biện ph&aacute;p kiểm dịch chặt chẽ. C&aacute;c hoạt động được ph&eacute;p ở Langkawi bao gồm b&atilde;i biển v&agrave; c&aacute;c hoạt động dưới nước, chơi golf, đạp xe, c&acirc;u c&aacute;, du thuyền. Ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c tour du lịch gi&aacute;o dục v&agrave; du lịch sinh th&aacute;i như ngắm chim, th&aacute;m hiểm hang động, leo n&uacute;i, đi bộ xuy&ecirc;n rừng. C&aacute;c bảo t&agrave;ng, ph&ograve;ng trưng b&agrave;y nghệ thuật, trung t&acirc;m di sản văn h&oacute;a, c&ocirc;ng vi&ecirc;n chủ đề v&agrave; trung t&acirc;m giải tr&iacute; cũng được mở cửa đón khách.</p>\r\n\r\n<p>Indonesia cũng xem x&eacute;t việc mở cửa đ&oacute;n du kh&aacute;ch nước ngo&agrave;i đến đảo Bali v&agrave; một số điểm du lịch kh&aacute;c từ th&aacute;ng 10. Ban đầu, Indonesia sẽ ưu ti&ecirc;n tiếp nhận du kh&aacute;ch từ H&agrave;n Quốc, Nhật Bản, Singapore v&agrave; New Zealand trong b&ocirc;́i cảnh mức độ l&acirc;y lan virus SARS-CoV-2 tại c&aacute;c quốc gia n&agrave;y thấp. Dự kiến, đến khi đạt mục ti&ecirc;u ti&ecirc;m mũi đ&acirc;̀u ti&ecirc;n vắc xin ngừa Covid-19 cho 70% người d&acirc;n, nước n&agrave;y sẽ mở cửa cho du khách nước ngoài.</p>\r\n\r\n<p>Th&aacute;i Lan trước đ&oacute; c&ocirc;ng bố kế hoạch từ th&aacute;ng 10 sẽ tiến h&agrave;nh mở cửa đón khách qu&ocirc;́c t&ecirc;́đ&ecirc;́n Bangkok, Chiang Mai, c&ugrave;ng c&aacute;c khu du lịch ở Pattaya, Cha-Am v&agrave; Hua Hin. T&ugrave;y theo đặc th&ugrave; ri&ecirc;ng, mỗi địa phương sẽ triển khai những c&aacute;ch thức đ&oacute;n kh&aacute;ch kh&aacute;c nhau, ph&ugrave; hợp với lợi thế của từng điểm đến. Một số địa điểm kh&aacute;c dự kiến cũng sẽ mở cửa ho&agrave;n to&agrave;n v&agrave;o giữa th&aacute;ng 10, theo m&ocirc; h&igrave;nh &ldquo;bong b&oacute;ng&rdquo; với một số nước l&aacute;ng giềng. Tuy nhi&ecirc;n, nước n&agrave;y cho biết kế hoạch mở cửa trở lại 5 tỉnh n&oacute;i tr&ecirc;n c&oacute; thể sẽ phải tr&igrave; ho&atilde;n đến th&aacute;ng 11 do tỷ lệ ti&ecirc;m chủng ở c&aacute;c địa phương n&agrave;y vẫn chưa đạt mức 70%.&nbsp;</p>\r\n', NULL, NULL),
(26, 6, 'Thủ tướng Hun Sen: Sự giúp đỡ của Việt Nam sẽ mãi mãi được khắc ghi trong lịch sử Campuchia', 'VHO-Sáng 7.1, tại sân vận động Olympic ở thủ đô Phnom Penh, hàng nghìn người dân Campuchia mang cờ hoa rực rỡ cùng đại diện các bộ, ban, ngành Chính phủ hoàng gia tưng bừng kỷ niệm 40 năm Chiến thắng 7.1 (7.1.1979-7.1.2019) lật đổ chế độ diệt chủng Khmer Đỏ.', 'images/anh_sukien_26.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/14818/21152Campuchia-Pol-Pot.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Thủ tướng Hun Sen v&agrave; Chủ tịch Quốc hội Heng Samrin ch&agrave;o mừng c&aacute;c đại biểu dự lễ kỷ niệm. Ảnh: TTXVN</strong></p>\r\n\r\n<p>Tham dự buổi lễ c&oacute; Thủ tướng Ch&iacute;nh phủ Ho&agrave;ng gia Campuchia Samdech Techo Hun Sen c&ugrave;ng Phu nh&acirc;n, Chủ tịch Quốc hội Heng Samrin c&ugrave;ng đại diện c&aacute;c sứ qu&aacute;n, tổ chức quốc tế v&agrave; kh&aacute;ch mời nước ngo&agrave;i. Đại sứ Việt Nam tại Campuchia Vũ Quang Minh cũng c&oacute; mặt tại lễ kỷ niệm long trọng n&agrave;y.</p>\r\n\r\n<p>Trong b&agrave;i diễn văn khai mạc lễ kỷ niệm, Thủ tướng Campuchia Hun Sen nhấn mạnh rằng Chiến thắng 7.1.1979 c&oacute; được l&agrave; dựa tr&ecirc;n sức mạnh tổng hợp của hai nguồn lực: Tinh thần đo&agrave;n kết của người d&acirc;n Campuchia dưới sự l&atilde;nh đạo của Đảng Nh&acirc;n d&acirc;n Campuchia, c&ugrave;ng sự hỗ trợ rất lớn lao, kịp thời v&agrave; hiệu quả của Qu&acirc;n t&igrave;nh nguyện Việt Nam.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/14818/21151TTXVN_0701Campuchia3.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Quang cảnh buổi lễ ở Thủ đ&ocirc; Phnom Penh</strong></p>\r\n\r\n<p>Người đứng đầu Ch&iacute;nh phủ Campuchia khẳng định, nếu kh&ocirc;ng c&oacute; sự tổng h&ograve;a của hai nguồn lực n&agrave;y sẽ kh&ocirc;ng l&agrave;m n&ecirc;n Chiến thắng 7.1.</p>\r\n\r\n<p>Thủ tướng Hun Sencho biết: &ldquo;Thay mặt cho nh&acirc;n d&acirc;n Campuchia, t&ocirc;i xin gửi lời cảm ơn s&acirc;u sắc nhất tới Đảng Cộng sản Việt Nam, Ch&iacute;nh phủ, qu&acirc;n đội v&agrave; nh&acirc;n d&acirc;n Việt Nam đ&atilde; đ&aacute;p lời k&ecirc;u gọi của mặt trận Đo&agrave;n kết D&acirc;n tộc cứu quốc Campuchia trong sự nghiệp giải ph&oacute;ng đất nước v&agrave; nh&acirc;n d&acirc;n Campuchia khỏi chế độ diệt chủng v&agrave; ngăn chặn chế độ n&agrave;y quay trở lại. C&ocirc;ng lao vĩ đại n&agrave;y sẽ m&atilde;i m&atilde;i được khắc ghi trong lịch sử Campuchia&rdquo;.</p>\r\n\r\n<p>Thủ tướng Hun Sen cũng n&ecirc;u r&otilde;, kể từ sau Chiến thắng vĩ đại ng&agrave;y 7.1.1979, đất nước Campuchia kh&ocirc;ng chỉ hồi sinh m&agrave; c&ograve;n ph&aacute;t triển mạnh mẽ, đạt được nhiều th&agrave;nh tựu to lớn trong ph&aacute;t kinh tế-x&atilde; hội trong suốt 40 năm qua.</p>\r\n\r\n<p>Đặc biệt, m&ocirc;i trường h&ograve;a b&igrave;nh, ổn định ch&iacute;nh trị trong khoảng 2 thập kỷ qua đ&atilde; mang lại &quot;thời điểm v&agrave;ng&quot; m&agrave; Campuchia chưa từng c&oacute; trước đ&acirc;y để đạt được những th&agrave;nh tựu hết sức đ&aacute;ng tự h&agrave;o. Campuchia nằm trong nh&oacute;m quốc gia tăng trưởng kinh tế cao của thế giới, đạt mức trung b&igrave;nh khoảng 7%/năm.</p>\r\n\r\n<p>Sau b&agrave;i ph&aacute;t biểu của Thủ tướng Hun Sen l&agrave; những m&agrave;n đồng diễn, m&uacute;a h&aacute;t mang đậm t&iacute;nh truyền thống của đất nước Ch&ugrave;a th&aacute;p tr&ecirc;n s&acirc;n vận động Olympic; c&aacute;c khối diễu h&agrave;nh rực rỡ đại diện cho c&aacute;c bộ, ban, ng&agrave;nh của Campuchia.</p>\r\n', NULL, NULL),
(27, 6, 'Trên 8 đài truyền hình Hàn Quốc tới Hà Nội đưa tin Hội nghị thượng đỉnh Mỹ - Triều', 'VHO-  Chiều qua 21.2, gần 20 phóng viên truyền hình đã chờ sẵn tại cổng sau Nhà khách Chính phủ (phố Lê Lai) - nơi ông Kim Chang Son, đặc phái viên của nhà lãnh đạo Triều Tiên được cho là đang lưu trú tại đây để ghi lại những hình ảnh kịp thời nhất.', 'images/anh-sukien2.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/16088/24157IMG_20190221_151706.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>Ph&oacute;ng vi&ecirc;n c&aacute;c đ&agrave;i truyền h&igrave;nh nước ngo&agrave;i lu&ocirc;n trong tư thế sẵn s&agrave;ng</strong></p>\r\n\r\n<p>Eun Sung Lee, trợ l&yacute; quay phim Đ&agrave;i truyền h&igrave;nh &ldquo;Yonhap News TV&rdquo; (H&agrave;n Quốc) cho biết, nh&oacute;m truyền h&igrave;nh của anh gồm 3 người, ngo&agrave;i ra c&ograve;n c&oacute; c&aacute;c ph&oacute;ng vi&ecirc;n điện tử... tổng cộng l&ecirc;n tới 7 người. K&ecirc;nh của anh l&agrave; 1 trong hơn 8 k&ecirc;nh&nbsp;truyền h&igrave;nh H&agrave;n Quốc đ&atilde; c&oacute; mặt tại Việt Nam, nh&oacute;m của Lee đến H&agrave; Nội từ ng&agrave;y 16 - 23.2; sau đ&oacute;, ng&agrave;y 24.2 sẽ c&oacute; một nh&oacute;m kh&aacute;c tiếp tục đưa tin. &ldquo;Đ&acirc;y l&agrave; lần đầu ti&ecirc;n t&ocirc;i tới Việt Nam, trước đ&oacute; t&ocirc;i kh&ocirc;ng biết g&igrave; về đất nước c&aacute;c bạn. Ch&uacute;ng t&ocirc;i ở kh&aacute;ch sạn Suji v&agrave; di chuyển bằng xe của kh&aacute;ch sạn. T&ocirc;i rất th&iacute;ch m&oacute;n phở v&agrave; cơm mỳ ốp trứng. Đất nước c&aacute;c bạn thật thanh b&igrave;nh&rdquo;, Lee chia sẻ.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/16088/24156IMG_20190221_151348.jpg\" style=\"height:525px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>Gun Jae Oh (tr&aacute;i), Eun Sung Lee (giữa) v&agrave; một đồng nghiệp của K&ecirc;nh truyền h&igrave;nh Yonhap News TV</strong></p>\r\n\r\n<p>Trưởng nh&oacute;m của Eun Sung Lee l&agrave; Gun Jae Oh, đ&acirc;y l&agrave; lần thứ hai anh sang Việt Nam. Anh cho biết, kh&ocirc;ng c&oacute; thời gian để tham quan H&agrave; Nội, tuy nhi&ecirc;n, theo cảm nhận người Việt Nam giống người H&agrave;n Quốc l&agrave; vui vẻ, nhiệt t&igrave;nh, nồng ấm. Giống Lee, người trưởng nh&oacute;m cũng th&iacute;ch phở, c&ograve;n cơm l&agrave; m&oacute;n cơm sườn. Anh cho biết, từ ng&agrave;y sang Việt Nam, anh l&agrave;m việc từ 6h s&aacute;ng, v&agrave; chờ đợi ở c&aacute;c kh&aacute;ch sạn, h&ocirc;m đầu ti&ecirc;n l&agrave; Metropole, từ ng&agrave;y 20.2 l&agrave; chờ đợi ở Nh&agrave; kh&aacute;ch Ch&iacute;nh phủ. C&ocirc;ng việc của anh l&agrave; quay cảnh c&ocirc;ng t&aacute;c chuẩn bị cho cuộc họp, cảnh c&aacute;c quan chức từ kh&aacute;ch sạn đi ra v&agrave; l&ecirc;n &ocirc; t&ocirc;.</p>\r\n\r\n<p>C&ograve;n với Bonwon Koo, ph&oacute;ng vi&ecirc;n Đ&agrave;i truyền h&igrave;nh MBC sẽ ở H&agrave; Nội tới ng&agrave;y 1.3 để đưa tin về sự kiện v&agrave; những c&ocirc;ng việc li&ecirc;n quan. Nh&oacute;m của Bonwon Koo c&oacute; 12 người, v&agrave; anh cho biết, cuộc họp thượng đỉnh Mỹ - Triều l&agrave; một sự kiện lớn, thu h&uacute;t sự quan t&acirc;m của người d&acirc;n H&agrave;n Quốc cũng như thế giới, do đ&oacute; bản th&acirc;n v&agrave; đồng nghiệp phải cố gắng l&agrave;m sao cho tốt.</p>\r\n', NULL, NULL),
(28, 6, 'Hội nghị Thượng đỉnh ở Hà Nội là một thành công', 'VHO- Cố vấn an ninh Hoa Kỳ John Bolton đã có buổi phỏng vấn trực tiếp với hãng tin Fox News đưa ra ý kiến cho rằng, dù không ký được thỏa thuận nào, Hội nghị Thượng đỉnh Mỹ - Triều Tiên được tổ chức tại Việt Nam vẫn không phải một sự thất bại. Đồng thời, cố vấn Nhà Trắng cũng đề cao vai trò của Việt Nam trong việc đóng góp vào cuộc gặp gỡ giữa hai nhà lãnh đạo, tạo ra một môi trường hòa bình và hữu nghị.', 'images/anh-sukien3.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/16426/25018Co%CC%82%CC%81-va%CC%82%CC%81n-an-ninh-My%CC%83--Ho%CC%A3%CC%82i-nghi%CC%A3-Thu%CC%9Bo%CC%9B%CC%A3ng-%C4%91i%CC%89nh-o%CC%9B%CC%89-Ha%CC%80-No%CC%A3%CC%82i-la%CC%80-mo%CC%A3%CC%82t-tha%CC%80nh-co%CC%82ng-.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>Cố vấn an ninh quốc gia Hoa Kỳ John Bolton. Ảnh: WASHINGTON POST</strong></p>\r\n\r\n<p>&Ocirc;ng John Bolton cho rằng, nghĩa vụ của Hoa Kỳ ch&iacute;nh l&agrave; bảo vệ v&agrave; th&uacute;c đẩy lợi &iacute;ch an ninh quốc gia, b&ecirc;n cạnh đ&oacute; cuộc gặp gỡ n&agrave;y đ&atilde; mở ra nhiều cơ hội cho cả ba b&ecirc;n (trong đ&oacute; c&oacute; Việt Nam) trong tiến tr&igrave;nh h&ograve;a b&igrave;nh thế giới. Trước đ&oacute;, v&agrave;o ng&agrave;y 1.3, Tổng thống Mỹ Donald Trump cũng đ&atilde; đăng tải d&ograve;ng trạng th&aacute;i tr&ecirc;n trang c&aacute; nh&acirc;n Twitter của m&igrave;nh, chia sẻ: &ldquo;Thật tốt khi được trở về từ Việt Nam, một đất nước v&ocirc; c&ugrave;ng tuyệt vời. Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; những cuộc đ&agrave;m ph&aacute;n nhiều &yacute; nghĩa c&ugrave;ng l&atilde;nh đạo Kim Jong Un. Ch&uacute;ng t&ocirc;i hiểu những điều họ muốn v&agrave; họ cũng biết ch&uacute;ng t&ocirc;i c&oacute; thể cung cấp những g&igrave;. Một mối quan hệ tốt đẹp. H&atilde;y chờ xem điều g&igrave; sẽ xảy ra&rdquo;.</p>\r\n\r\n<p>V&agrave;o ng&agrave;y 3.3, Nh&agrave; Trắng cũng đ&atilde; cho đăng tải d&ograve;ng trạng th&aacute;i tr&ecirc;n trang chủ Twitter của m&igrave;nh, đề cao t&igrave;nh hữu nghị v&agrave; hợp t&aacute;c giữa Việt Nam v&agrave; Hoa Kỳ: &ldquo;Mối quan hệ thương mai giữa Mỹ v&agrave; Việt Nam đ&atilde; tạo ra h&agrave;ng tỉ USD lợi nhuận cho ng&agrave;nh h&agrave;ng h&oacute;a v&agrave; dịch vụ Mỹ, hỗ trợ h&agrave;ng ng&agrave;n việc l&agrave;m v&agrave; mở ra một thị trường mới cho n&ocirc;ng d&acirc;n Mỹ&rdquo;. B&igrave;nh luận n&agrave;y được đưa ra chỉ v&agrave;i ng&agrave;y sau khi Tổng thống Donald Trump trở về từ cuộc gặp mặt với nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong Un tại Hội nghị Thượng đỉnh Mỹ - Triều được tổ chức tại H&agrave; Nội. Đ&acirc;y cũng l&agrave; một trong những yếu tố nhằm khẳng định sự th&agrave;nh c&ocirc;ng của kỳ hội nghị cũng như vai tr&ograve; của Việt Nam như một đối t&aacute;c đ&aacute;ng tin cậy của những nền c&ocirc;ng nghiệp h&agrave;ng đầu thế giới. Theo truyền th&ocirc;ng quốc tế, Tổng thống Donald Trump cũng đ&aacute;nh gia cao c&aacute;c hợp đồng thương mại của Việt Nam v&agrave; Hoa Kỳ, đồng thời ghi nhận mức th&acirc;m hụt thương mại của Mỹ đối với c&aacute;c nước Đ&ocirc;ng Nam &Aacute; đang giảm dần.</p>\r\n\r\n<p>V&agrave;o chiều ng&agrave;y 2.3, Chủ tịch Đảng Lao động Triều Ti&ecirc;n, Chủ tịch Uỷ ban Quốc vụ Triều Ti&ecirc;n Kim Jong Un v&agrave; đo&agrave;n đại biểu cấp cao cũng đ&atilde; trở về nước, kết th&uacute;c tốt đẹp chuyến thăm hữu nghị ch&iacute;nh thức đến Việt Nam. Theo truyền th&ocirc;ng quốc tế, trước khi trở về nước, nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n Kim Jong Un cũng đ&atilde; d&agrave;nh thời gian vào lăng viếng Chủ tịch Hồ Ch&iacute; Minh.</p>\r\n', NULL, NULL),
(29, 6, 'Ông Joe Biden đắc cử Tổng thống thứ 46 của nước Mỹ', 'VHO- Các hãng thông tấn lớn của Mỹ như CNN, Reuters, NBC, CBS, Fox News đã đồng loạt đưa tin ông Joe Biden sẽ là Tổng thống thứ 46 của nước Mỹ sau khi ông chiến thắng tại Pennsylvania để có 284 phiếu đại cử tri. Ông sau đó giành thêm Nevada, nâng tổng số phiếu lên 290.', 'images/anh_sukien_29.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/35034/61840Biden.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>&Ocirc;ng Joe Biden</strong></p>\r\n\r\n<p>Ứng cử vi&ecirc;n đảng D&acirc;n chủ Joe Biden đ&atilde; đ&aacute;nh bại đương kim Tổng thống Mỹ Donald Trump để gi&agrave;nh hơn 270 phiếu đại cử tri trong cuộc bầu cử tổng thống năm 2020, qua đ&oacute; sẽ trở th&agrave;nh tổng thống thứ 46 của nước n&agrave;y. Theo đ&oacute;, ứng cử vi&ecirc;n của Đảng D&acirc;n chủ Joe Biden đ&atilde; gi&agrave;nh được hơn 270 phiếu đại cử tri tr&ecirc;n tổng số 538 phiếu đại cử tri to&agrave;n quốc. Theo luật định, &ocirc;ng Joe Biden sẽ tuy&ecirc;n thệ nhậm chức v&agrave;o ng&agrave;y 20.1.2021 v&agrave; ch&iacute;nh thức trở th&agrave;nh Tổng thống thứ 46 của nước Mỹ.</p>\r\n\r\n<p>Chiến thắng n&agrave;y cũng gi&uacute;p thượng nghị sĩ Kamala Harris trở th&agrave;nh nữ Ph&oacute; Tổng thống da m&agrave;u đầu ti&ecirc;n của Mỹ v&agrave; khiến Trump trở th&agrave;nh tổng thống đương nhiệm đầu ti&ecirc;n thất bại trong nỗ lực t&aacute;i tranh cử, kể từ sau George H.W. Bush v&agrave;o năm 1992.</p>\r\n\r\n<p>Tổng thống đắc cử Joe Biden đ&atilde; nhanh ch&oacute;ng gửi lời cảm ơn sự ủng hộ của người d&acirc;n Mỹ d&agrave;nh cho &ocirc;ng v&agrave; Ph&oacute; Tổng thống đắc cử Harris, cũng như k&ecirc;u gọi họ đo&agrave;n kết trong thời điểm khủng hoảng n&agrave;y. &quot;Khi chiến dịch tranh cử kết th&uacute;c, đ&atilde; đến l&uacute;c tr&uacute;t bỏ bực dọc v&agrave; chỉ tr&iacute;ch gay gắt để x&iacute;ch lại gần nhau. Đ&atilde; đến l&uacute;c nước Mỹ đo&agrave;n kết v&agrave; h&agrave;n gắn. Ch&uacute;ng ta l&agrave; nước Mỹ. Kh&ocirc;ng c&oacute; g&igrave; ch&uacute;ng ta kh&ocirc;ng thể l&agrave;m nếu s&aacute;t c&aacute;nh c&ugrave;ng nhau&quot;, Biden cho hay.</p>\r\n\r\n<p>Ngay sau khi h&agrave;ng loạt phương tiện truyền th&ocirc;ng đưa tin ứng cử vi&ecirc;n Biden đ&atilde; gi&agrave;nh qu&aacute; 270 phiếu đại cử tri v&agrave; sẽ trở th&agrave;nh tổng thống thứ 46 của mỹ, đương kim Tổng thống Donald Trump từ chối c&ocirc;ng nhận chiến thắng n&agrave;y, đồng thời khẳng định sẽ triển khai c&aacute;c th&aacute;ch thức ph&aacute;p l&yacute;. Nếu kết quả cuộc bầu cử n&agrave;y được x&aacute;c nhận, &ocirc;ng Trump sẽ trở th&agrave;nh tổng thống tại nhiệm 1 nhiệm kỳ đầu ti&ecirc;n tại Mỹ trong v&ograve;ng 25 năm qua.</p>\r\n\r\n<p>Trong cuộc tổng tuyển cử năm nay, cử tri Mỹ b&ecirc;n cạnh việc bầu tổng thống v&agrave; ph&oacute; tổng thống, to&agrave;n bộ 435 ghế tại Hạ viện, 35/100 ghế tại Thượng viện, 11 vị tr&iacute; thống đốc bang c&ugrave;ng khoảng 5.000 ghế trong c&aacute;c cơ quan lập ph&aacute;p cấp bang tr&ecirc;n to&agrave;n quốc cũng được bầu lại.</p>\r\n\r\n<p>Ngay sau khi &ocirc;ng được truyền th&ocirc;ng đồng loạt xướng t&ecirc;n l&agrave; Tổng thống thứ 46 của Mỹ, l&atilde;nh đạo nhiều nước tr&ecirc;n thế giới đ&atilde; ch&uacute;c mừng &ocirc;ng Joe Biden v&agrave; b&agrave;&nbsp;Kamala Harris.</p>\r\n\r\n<p>L&atilde;nh đạo nước l&aacute;ng giềng với Mỹ, Thủ tướng Canada Justin Trudeau nhanh ch&oacute;ng ch&uacute;c mừng Biden v&agrave; Kamala Harris, nữ ph&oacute; tổng thống da m&agrave;u đầu ti&ecirc;n của Mỹ. &quot;Hai nước ch&uacute;ng ta l&agrave; bạn b&egrave;, đối t&aacute;c v&agrave; đồng minh th&acirc;n thiết. Quan hệ của ch&uacute;ng ta độc nhất v&ocirc; nhị tr&ecirc;n trường quốc tế. T&ocirc;i thực sự mong chờ được c&ugrave;ng l&agrave;m việc v&agrave; x&acirc;y dựng điều đ&oacute; c&ugrave;ng cả hai&quot;, Trudeau đăng tr&ecirc;n Twitter.</p>\r\n\r\n<p>Thủ tướng Đức Angela Merkel cũng gửi lời ch&uacute;c mừng Tổng thống đắc cử Biden v&agrave; ph&oacute; tướng Harris tr&ecirc;n Twitter. &quot;T&ocirc;i mong đợi v&agrave;o sự hợp t&aacute;c trong tương lai với Tổng thống Biden. T&igrave;nh hữu nghị xuy&ecirc;n Đại T&acirc;y Dương của ch&uacute;ng ta l&agrave; kh&ocirc;ng thể thay thế, nếu ch&uacute;ng ta l&agrave;m chủ được những th&aacute;ch thức lớn của thời đại&quot;, b&agrave; cho biết trong một th&ocirc;ng b&aacute;o.</p>\r\n\r\n<p>Thủ tướng Anh Boris Johnson ch&uacute;c mừng cả Biden v&igrave; đắc cử v&agrave; Ph&oacute; tướng Harris v&igrave; &quot;th&agrave;nh tựu lịch sử&quot; của b&agrave;. &quot;Mỹ l&agrave; đồng minh quan trọng nhất của ch&uacute;ng t&ocirc;i. T&ocirc;i mong muốn hợp t&aacute;c chặt chẽ về những vấn đề ưu ti&ecirc;n chung của hai b&ecirc;n, từ biến đổi kh&iacute; hậu đến thương mại v&agrave; an ninh&quot;, &ocirc;ng viết tr&ecirc;n Twitter.</p>\r\n\r\n<p>Tổng thống Ph&aacute;p Emmanuel Macron ch&uacute;c mừng Biden v&agrave; lưu &yacute; về những th&aacute;ch thức hai nước cần đối mặt. &quot;Người Mỹ đ&atilde; lựa chọn Tổng thống của họ. Ch&uacute;c mừng Joe Biden v&agrave; Kamala Harris. Ch&uacute;ng ta c&oacute; rất nhiều việc phải l&agrave;m để vượt qua những th&aacute;ch thức hiện nay. H&atilde;y hợp t&aacute;c c&ugrave;ng nhau!&quot;, &ocirc;ng viết.</p>\r\n\r\n<p>Thủ tướng T&acirc;y Ban Nha Pedro Sanchez viết: &quot;Người d&acirc;n Mỹ đ&atilde; lựa chọn Tổng thống thứ 46 của họ. Ch&uacute;c mừng Joe Biden v&agrave; Kamala Harris. Ch&uacute;ng t&ocirc;i xin gửi lời ch&uacute;c may mắn v&agrave; mọi điều tốt đẹp nhất, đồng thời mong đợi hợp t&aacute;c để giải quyết những th&aacute;ch thức ph&iacute;a trước&quot;.</p>\r\n\r\n<p>Thủ tướng Nhật Bản Yoshihide Suga ch&uacute;c mừng Joe Biden gi&agrave;nh chiến thắng tại Nh&agrave; Trắng, cam kết l&agrave;m việc với đảng D&acirc;n chủ để tăng cường hơn nữa mối quan hệ của c&aacute;c quốc gia.</p>\r\n', NULL, NULL),
(30, 6, 'Thử nghiệm thành công mạng 5G phục vụ thương mại', 'VHO- VNPT vừa chính thức thử nghiệm thành công mạng VinaPhone 5G tại 2 thành phố Hà Nội và TP Hồ Chí Minh. Kết quả thử nghiệm mạng 5G đạt hơn 2,2Gbps (Video speedtest) nhanh gấp 10 lần so với mạng 4G và có độ trễ lý tưởng gần như bằng 0.', 'images/anh_sukien_6.jpg', '<p>Để c&oacute; kết quả n&agrave;y, VNPT đ&atilde; hợp t&aacute;c với c&aacute;c nh&agrave; sản xuất thiết bị 5G h&agrave;ng đầu thế giới để&nbsp; k&iacute;ch hoạt c&aacute;c t&iacute;nh năng ti&ecirc;n tiến nhất, tối ưu ho&aacute; cấu tr&uacute;c mạng lưới cho mạng 5G Vinaphone. Tốc độ vượt trội n&agrave;y của mạng 5G VinaPhone đ&atilde; được ứng dụng thử nghiệm trong c&aacute;c dịch vụ y&ecirc;u cầu tốc độ dịch vụ băng rộng cao, c&aacute;c dịch vụ đ&ograve;i hỏi độ trễ dịch vụ thấp như: C&aacute;c ứng dụng tương t&aacute;c thực tế ảo (AR/VR), tr&ograve; chơi trực tuyến sử dụng băng th&ocirc;ng si&ecirc;u tốc độ Cloud Game, video 8K&hellip; đem lại trải nghiệm cao nhất cho người d&ugrave;ng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/29385/49977vnpt.jpg\" /></p>\r\n\r\n<p>Với việc t&iacute;ch hợp mạng 5G thử nghiệm th&agrave;nh c&ocirc;ng v&agrave;o cấu tr&uacute;c mạng hiện hữu, VNPT đ&atilde; sẵn s&agrave;ng về mặt kỹ thuật, c&ocirc;ng nghệ v&agrave; cấu tr&uacute;c mạng lưới cho việc triển khai mạng 5G thương mại. B&ecirc;n cạnh việc nghi&ecirc;n cứu ứng dụng &nbsp;5G trong c&aacute;c lĩnh vực giải tr&iacute;, sản xuất kinh doanh, giao th&ocirc;ng vận tải, y tế, gi&aacute;o dục...VNPT cũng đ&aacute;nh gi&aacute; khả năng khai th&aacute;c 5G để phục vụ nhu cầu Internet của c&aacute;c hộ gia đ&igrave;nh thay thế hệ thống c&aacute;p quang. Điều n&agrave;y đặc biệt c&oacute; &yacute; nghĩa trong việc đưa Internet đến v&ugrave;ng s&acirc;u v&ugrave;ng xa, khu vực kh&oacute; khăn, g&oacute;p phần tạo dựng nền tảng để thực hiện chuyển đổi số đến tận th&ocirc;n, x&oacute;m, l&agrave;ng bản ở c&aacute;c v&ugrave;ng miền.</p>\r\n\r\n<p>Đại diện VNPT cho biết, mạng VinaPhone 5G sẽ kh&ocirc;ng dừng lại ở việc cung cấp dữ liệu si&ecirc;u tốc độ m&agrave; c&ograve;n đem đến hệ sinh th&aacute;i số cho người d&ugrave;ng v&agrave; c&aacute;c nền tảng ch&iacute;nh phủ điện tử, đ&ocirc; thị th&ocirc;ng minh, g&oacute;p phần khẳng định vai tr&ograve; đồng h&agrave;nh c&ugrave;ng Ch&iacute;nh phủ trong c&ocirc;ng cuộc x&acirc;y dựng Ch&iacute;nh phủ điện tử Việt Nam v&agrave; thể hiện tr&ograve; ti&ecirc;n phong trong cung cấp c&aacute;c dịch vụ di động thế hệ mới tại Việt Nam.</p>\r\n', NULL, NULL),
(31, 5, 'Đội hình tiêu biểu EURO 2020', 'VHO- VCK EURO 2020 đã kết thúc với chức vô địch thuộc về tuyển Italia sau khi đoàn quân áo thiên thanh đánh bại chủ nhà tuyển Anh trong trận chung kết. Sau khi EURO 2020 xác định được các danh hiệu tập thể và cá nhân thì đội hình tiêu biểu của giải cũng đã Liên đoàn bóng đá châu Âu (UEFA) công bố.', 'images/anh_thethao_31.jpg', '<p>Mười một c&aacute;i t&ecirc;n trong đội h&igrave;nh n&agrave;y đều l&agrave; những cầu thủ đ&atilde; thi đấu ấn tượng hơn c&aacute;c đồng nghiệp kh&aacute;c ở vị tr&iacute; của m&igrave;nh, trong đ&oacute; 2 đội b&oacute;ng lọt v&agrave;o trận chung kết l&agrave; Italia v&agrave; Anh đ&oacute;ng g&oacute;p số cầu thủ nhiều nhất.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/42479/78468uefa.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Đội h&igrave;nh ti&ecirc;u biểu EURO 2020 của UEFA</strong>&nbsp;</p>\r\n\r\n<p>Cụ thể nh&agrave; v&ocirc; địch tuyển Italia v&agrave; &aacute; qu&acirc;n c&ugrave;ng c&oacute; 3 cầu thủ, c&aacute;c vị tr&iacute; c&ograve;n lại thuộc về Đan Mạch (2 suất), Ph&aacute;p (1) v&agrave; Thụy Sỹ (1), Bồ Đ&agrave;o Nha (1).</p>\r\n\r\n<p>Trong đội h&igrave;nh xuất sắc nhất của UEFA, Donnarumma được lựa chọn cho vị tr&iacute; trấn giữ khung th&agrave;nh. Thủ m&ocirc;n đang chơi cho AC Milan nhận danh hiệu Găng tay v&agrave;ng v&agrave; Cầu thủ xuất sắc nhất EURO 2020. Donnarumma g&oacute;p c&ocirc;ng lớn gi&uacute;p Italia v&ocirc; địch, đặc biệt l&agrave; việc sắm vai người h&ugrave;ng trong 2 loạt lu&acirc;n lưu với T&acirc;y Ban Nha v&agrave; Anh. Ở h&agrave;ng tứ vệ, Maguire (Anh) v&agrave; Chiellini (Italia) l&agrave; 2 trung vệ xuất sắc nhất tại giải đấu năm nay, 2 hậu vệ c&aacute;nh lần lượt l&agrave; Luke Shaw (Anh) v&agrave; Joakim Maehle (Đan Mạch).</p>\r\n\r\n<p>Ở tuyến giữa, UEFA chọn cặp tiền vệ trung t&acirc;m l&agrave; Xhaka (Thụy Sĩ) v&agrave; Pogba (Ph&aacute;p), trong khi Dolberg (Đan Mạch), Chiesa (Italia) v&agrave; Sterling (Anh) g&oacute;p mặt ở vị tr&iacute; tiền vệ tấn c&ocirc;ng. Cầu thủ chơi cao nhất trong đội h&igrave;nh n&agrave;y l&agrave; Ronaldo. D&ugrave; tuyển Bồ Đ&agrave;o Nha&nbsp;dừng bước tại v&ograve;ng 1/16 đội nhưng CR7 ghi được 5 b&agrave;n thắng v&agrave; gi&agrave;nh danh hiệu Vua ph&aacute; lưới.</p>\r\n', NULL, NULL);
INSERT INTO `tinbai` (`id`, `idTheLoai`, `TieuDe`, `TrichDan`, `anhtrichdan`, `NoiDung`, `NgayDang`, `TacGia`) VALUES
(32, 5, 'Nhà vô địch EURO Donnarumma chính thức gia nhập Paris Saint-Germain', 'VHO- Chia tay AC Milan, nhà vô địch EURO 2020 Gianluigi Donnarumma sẽ thi đấu cho Paris Saint-Germain theo bản hợp đồng có thời hạn 5 năm (đến tháng 6.2026).', 'images/anh_thethao_32.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/42567/78650donnarumma-2026.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Donnarumma sẽ thi đấu cho Paris Saint-Germain đến th&aacute;ng 6.2026. (Nguồn: psg.fr)</strong></p>\r\n\r\n<p>Kh&ocirc;ng l&acirc;u sau khi gi&uacute;p Italy l&ecirc;n ng&ocirc;i EURO 2020, thủ th&agrave;nh Gianluigi Donnarumma đ&atilde; ch&iacute;nh thức gia nhập Paris Saint-Germain theo dạng chuyển nhượng tự do từ AC Milan.</p>\r\n\r\n<p>Theo th&ocirc;ng b&aacute;o đăng tr&ecirc;n trang chủ, PSG cho biết Gianluigi Donnarumma sẽ thi đấu cho đội b&oacute;ng theo bản hợp đồng c&oacute; thời hạn 5 năm (đến th&aacute;ng 6.2026).</p>\r\n\r\n<p>PSG kh&ocirc;ng tiết lộ mức lương d&agrave;nh cho thủ th&agrave;nh 22 tuổi n&agrave;y, nhưng theo tuyền th&ocirc;ng th&igrave; số tiền m&agrave; Donnarumma sẽ hưởng lương 12 triệu euro/1 m&ugrave;a.</p>\r\n\r\n<p>&quot;T&ocirc;i rất hạnh ph&uacute;c được l&agrave; một phần của c&acirc;u lạc bộ tuyệt vời như Paris Saint-Germain,&quot; thủ th&agrave;nh Gianluigi Donnarumma chia sẻ.</p>\r\n\r\n<p>&quot;T&ocirc;i cảm thấy đ&atilde; sẵn s&agrave;ng đ&oacute;n nhận thử th&aacute;ch mới v&agrave; tiếp tục ph&aacute;t triển tại đ&acirc;y. Với Paris, t&ocirc;i muốn gi&agrave;nh nhiều vinh quang nhất c&oacute; thể v&agrave; mang niềm vui tới cho người h&acirc;m mộ.&quot;</p>\r\n\r\n<p>Donnarumma ra mắt Milan ở tuổi 16 năm 2015 v&agrave; đ&atilde; c&oacute; 215 lần ra s&acirc;n ở Serie A. Tuy nhi&ecirc;n sua 8 năm gắn b&oacute; (t&iacute;nh từ đội trẻ), thủ th&agrave;nh n&agrave;y đ&atilde; quyết định chia tay Rossoneri để t&igrave;m bến đỗ mới trong sự nghiệp.</p>\r\n\r\n<p>Trong 5 m&ugrave;a giải ở đội 1, Donnarumma đ&atilde; c&ugrave;ng AC Milan gi&agrave;nh được 1 danh hiệu Si&ecirc;u c&uacute;p Italia năm 2016 v&agrave; được bầu chọn l&agrave; thủ m&ocirc;n xuất sắc nhất Serie A m&ugrave;a vừa qua.</p>\r\n\r\n<p>Tại EURO 2020, thủ th&agrave;nh 22 tuổi n&agrave;y đ&atilde; thi đấu ấn tượng để gi&uacute;p đội tuyển Italy gi&agrave;nh chức v&ocirc; địch. Ngo&agrave;i ra, Donnarumma c&ograve;n được b&igrave;nh chọn l&agrave; Cầu thủ xuất sắc nhất giải đấu.</p>\r\n\r\n<p>Gia nhập Paris Saint-Germain, Gianluigi Donnarumma sẽ phải cạnh tranh vị tr&iacute; số 1 với thủ th&agrave;nh Keylor Navas.</p>\r\n\r\n<p>Trước Donnarumma, đội chủ s&acirc;n C&ocirc;ng vi&ecirc;n c&aacute;c Ho&agrave;ng tử cũng đ&atilde; đưa về hai t&acirc;n binh kh&aacute;c l&agrave; Achraf Hakimi v&agrave; Sergio Ramos trong kỳ chuyển nhượng m&ugrave;a H&egrave; n&agrave;y.</p>\r\n', NULL, NULL),
(33, 5, 'Tây Ban Nha là ứng viên số một giành huy chương vàng bóng đá nam', 'VHO- Sở hữu đội hình với nhiều tuyển thủ vừa dự EURO 2020, đội tuyển U23 Tây Ban Nha được xem là ứng cử viên số một cho tấm huy chương vàng môn bóng đá nam tại Olympic Tokyo 2020.\r\n', 'images/anh_thethao_33.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t1000/uploaded/ivpycivo/2021_07_20/taybannhaeuro20201406.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Đội tuyển T&acirc;y Ban Nha. (Nguồn: Getty Images)</strong></p>\r\n\r\n<p>Sở hữu đội h&igrave;nh với nhiều tuyển thủ vừa dự EURO 2020 c&ugrave;ng c&aacute;c ng&ocirc;i sao qu&aacute; tuổi d&agrave;y dặn kinh nghiệm như Marco Asensio v&agrave; Dani Ceballos, đội tuyển U23 T&acirc;y Ban Nha được xem l&agrave; ứng cử vi&ecirc;n số một cho tấm huy chương v&agrave;ng m&ocirc;n b&oacute;ng đ&aacute; nam tại Olympic Tokyo 2020.&nbsp;</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của nh&agrave; c&aacute;i Bwin, tỷ lệ cược v&ocirc; địch gi&agrave;nh cho đội tuyển U23 T&acirc;y Ban Nha l&agrave; 2/1 (đặt 1 ăn 2), xếp tr&ecirc;n đương kim v&ocirc; địch U23 Brazil (11/4), U23 Ph&aacute;p (9/2), U23 Đức (8/1), U23 Argentina (8/1) v&agrave; chủ nh&agrave; U23 Nhật Bản (14/1). Cũng coi đội tuyển U23 T&acirc;y Ban Nha l&agrave; ứng cử vi&ecirc;n số một cho ng&ocirc;i v&ocirc; địch c&ograve;n c&oacute; nh&agrave; c&aacute;i William Hills (15/8), Paddy Power, Bet Fair (c&ugrave;ng 23/10) v&agrave; Bet Victor (9/4).</p>\r\n\r\n<p>Trong khi đ&oacute;, với tỷ lệ l&agrave; 250/1 ở hầu hết c&aacute;c nh&agrave; c&aacute;i, đội tuyển U23 Saudi Arabia v&agrave; đội tuyển U23 Nam Phi bị đ&aacute;nh gi&aacute; l&agrave; hai đội b&oacute;ng yếu nhất ở m&ocirc;n b&oacute;ng đ&aacute; nam tại kỳ Thế vận hội n&agrave;y.</p>\r\n', NULL, NULL),
(34, 5, 'Cristiano Ronaldo lập kỷ lục mới tại đấu trường Champions League', 'VHO- Với pha lập công vào lưới Atalanta, Cristiano Ronaldo đã vượt qua Lionel Messi (37 trận) để trở thành cầu thủ ghi bàn vào lưới nhiều đối thủ nhất tại Champions League.', 'images/anh_thethao_34.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"https://cdnimg.vietnamplus.vn/t825/Uploaded/mzdic/2021_10_21/ronaldo-ky-luc-champions-league-2110.jpg\" style=\"height:464px; width:825px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Ronaldo l&agrave; cầu thủ ghi b&agrave;n v&agrave;o lưới nhiều đội nhất ở Champions League. (Nguồn: Getty Images)</strong></p>\r\n\r\n<p style=\"text-align:center\">Cristiano Ronaldo đ&atilde; trở th&agrave;nh người h&ugrave;ng với pha lập c&ocirc;ng quyết định gi&uacute;p Manchester United 3-2 trước Atalanta tại v&ograve;ng bảng Champions League.</p>\r\n\r\n<p style=\"text-align:center\">Ph&uacute;t 81, Luke Shaw c&oacute; pha tạt b&oacute;ng như đặt để Ronaldo bật cao đ&aacute;nh đầu ghi b&agrave;n ấn định chiến thắng ngược d&ograve;ng kh&ocirc;ng tưởng cho Quỷ đỏ.</p>\r\n\r\n<p style=\"text-align:center\">Với pha lập c&ocirc;ng n&agrave;y, Ronaldo kh&ocirc;ng chỉ gi&uacute;p M.U giữ 3 điểm ở lại Old Trafford m&agrave; c&ograve;n lập n&ecirc;n kỷ lục mới tại Champions League.</p>\r\n\r\n<p style=\"text-align:center\">Ronaldo biến Atalanta th&agrave;nh đội b&oacute;ng thứ 38 m&agrave; anh chọc thủng lưới tại đấu trường danh gi&aacute; nhất ch&acirc;u &Acirc;u cấp c&acirc;u lạc bộ.</p>\r\n\r\n<p style=\"text-align:center\">Th&agrave;nh t&iacute;ch n&agrave;y gi&uacute;p CR7 vượt qua Lionel Messi (37 trận) để trở th&agrave;nh cầu thủ ghi b&agrave;n v&agrave;o lưới nhiều đối thủ nhất tại Champions League.</p>\r\n\r\n<p style=\"text-align:center\">Ở trận thắng Villarreal ở lượt trận trước đ&oacute;, Cristiano Ronaldo cũng đ&atilde; vượt qua Iker Casillas để trở th&agrave;nh cầu thủ ra s&acirc;n nhiều nhất tại đấu trường danh gi&aacute; n&agrave;y.</p>\r\n\r\n<p style=\"text-align:center\">Chia sẻ sau khi ghi b&agrave;n thắng quan trọng, tr&ecirc;n trang Twitter c&aacute; nh&acirc;n, si&ecirc;u sao Cristiano Ronaldo viết: &quot;Đ&uacute;ng! Nh&agrave; h&aacute;t của những giấc mơ đang ch&aacute;y! Ch&uacute;ng t&ocirc;i đ&atilde; sống lại! Man United l&agrave; vậy v&agrave; ch&uacute;ng t&ocirc;i kh&ocirc;ng bao giờ bỏ cuộc! Đ&acirc;y l&agrave; Old Trafford!&quot;</p>\r\n\r\n<p style=\"text-align:center\"><br />\r\nỞ trận đấu n&agrave;y, Manchester United đ&atilde; trải qua hiệp 1 đầy thất vọng v&agrave; để cho Atalanta vượt l&ecirc;n dẫn trước 2-0 nhờ c&aacute;c b&agrave;n thắng của Mario Pasalic v&agrave; Merih Demiral.</p>\r\n\r\n<p style=\"text-align:center\">Tuy nhi&ecirc;n, sang hiệp 2, Ronaldo v&agrave; đồng đội đ&atilde; trở lại đầy mạnh mẽ để ghi ba b&agrave;n v&agrave;o lưới Atalanta, qua đ&oacute; ngược d&ograve;ng th&agrave;nh c&ocirc;ng 3-2.</p>\r\n\r\n<p style=\"text-align:center\">Marcus Rashford v&agrave; Harry Maguire thay nhau lập c&ocirc;ng sau hai t&igrave;nh huống kiến tạo của Bruno, trước khi Ronaldo bật cao đ&aacute;nh đấu ấn định chiến thắng.</p>\r\n\r\n<p style=\"text-align:center\">Với chiến thắng n&agrave;y, Manchester United đ&atilde; c&oacute; trong tay 6 điểm, vượt qua ch&iacute;nh Atalanta để chiếm ng&ocirc;i đầu bảng F.</p>\r\n', NULL, NULL),
(35, 5, 'Chuẩn bị cho khán giả vào SVĐ Mỹ Đình: Phòng chống dịch là ưu tiên hàng đầu', 'VHO-Chiều nay, Thứ trưởng Bộ VHTTDL Hoàng Đạo Cương đã có buổi làm việc với Tổng cục TDTT, Liên đoàn Bóng đá Việt Nam (VFF) cùng các bộ, ban ngành, các cơ quan chức năng của TP Hà Nội về kế hoạch tổ chức các trận đấu tháng 11 vòng loại cuối FIFA World Cup 2022 tại Hà Nội.', 'images/anh_thethao_35.jpg', '<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46658/87702To%C3%A0n-c%E1%BA%A3nh-bu%E1%BB%95i-l%C3%A0m-vi%E1%BB%87c.JPG\" style=\"height:339px; width:600px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>To&agrave;n cảnh buổi l&agrave;m việc</strong></p>\r\n\r\n<p style=\"text-align:center\">Tại buổi l&agrave;m việc, &ocirc;ng L&ecirc; Ho&agrave;i Anh, Tổng Thư k&yacute; VFF đ&atilde; b&aacute;o c&aacute;o về tiến độ c&ocirc;ng t&aacute;c chuẩn bị cụ thể cho hai trận đấu giữa ĐTQG Việt Nam gặp ĐTQG Nhật Bản&nbsp;(ng&agrave;y 11.11) v&agrave; trận ĐTQG Việt Nam gặp ĐTQG Ả rập X&ecirc; &uacute;t (ng&agrave;y 16.11) tr&ecirc;n SVĐ&nbsp;Quốc gia Mỹ Đ&igrave;nh.&nbsp; Theo đ&oacute;, hai trận đấu n&agrave;y, UBND TP H&agrave; Nội đ&atilde; đồng &yacute;, thống nhất cho ph&eacute;p kh&aacute;n giả v&agrave;o s&acirc;n với khoảng 12.000 người. Hiện VFF đ&atilde; ho&agrave;n thiện c&aacute;c thủ tục từ ph&iacute;a trong nước để c&aacute;c đội tuyển nước ngo&agrave;i ho&agrave;n tất thủ tục nhập cảnh Việt Nam. Đại diện Cục L&atilde;nh sự (Bộ Ngoại giao) cho biết đ&atilde; nhận được văn bản v&agrave; sẽ trả lời sớm nhất để hỗ trợ việc nhập cảnh của c&aacute;c cầu thủ, th&agrave;nh vi&ecirc;n theo đo&agrave;n của hai đội tuyển n&agrave;y. &Ocirc;ng Ho&agrave;i Anh cũng cho biết VFF cũng đ&atilde; c&oacute; kế hoạch phối hợp với c&aacute;c cơ quan kh&aacute;c để đảm bảo trận đấu an to&agrave;n, trong đ&oacute;, yếu tố ph&ograve;ng chống dịch được ưu ti&ecirc;n h&agrave;ng đầu.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46658/877171.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Thứ trưởng Ho&agrave;ng Đạo Cương ph&aacute;t biểu chỉ đạo</strong></p>\r\n\r\n<p style=\"text-align:center\">Tại cuộc họp, đại diện Bộ Y tế, Bộ C&ocirc;ng an,Bộ Quốc ph&ograve;ng, &nbsp;Bộ GTVT, Sở VHTT H&agrave; Nội, C&ocirc;ng an TP H&agrave; Nội&hellip; cũng đ&atilde; n&ecirc;u &yacute; kiến, trong đ&oacute; nhất tr&iacute; cao việc tổ chức hai trận đấu, đ&aacute;p ứng l&ograve;ng mong mỏi của người h&acirc;m mộ. Tuy nhi&ecirc;n, cần c&oacute; những biện ph&aacute;p ngăn ngừa, kh&ocirc;ng để c&aacute;c sự kiện n&agrave;y trở th&agrave;nh điểm l&acirc;y nhiễm dịch. Đồng thời, cần phải l&agrave;m r&otilde; việc v&agrave;o s&acirc;n, ra về, chỗ ngồi, khoảng c&aacute;ch 5k của kh&aacute;n giả như thế&nbsp; n&agrave;o. Phải x&acirc;y dựng kịch bản cho kh&aacute;n giả đến s&acirc;n, từ đ&oacute; bố tr&iacute; c&aacute;c lực lượng an ninh, y tế. X&acirc;y dựng kịch bản vị tr&iacute;, c&aacute;c v&ograve;ng kiểm so&aacute;t, so&aacute;t v&eacute; như thế n&agrave;o cho ph&ugrave; hợp&hellip; B&ecirc;n cạnh đ&oacute;, c&aacute;c cơ quan chức năng cũng đề nghị l&agrave;m r&otilde; việc mua v&eacute;, đặt v&eacute; của kh&aacute;n giả. Theo VFF, việc b&aacute;n v&eacute; sẽ thực hiện trực tuyến,VFF sẽ tiến h&agrave;nh&nbsp;trả v&eacute; th&ocirc;ng qua&nbsp;dịch vụ Bưu điện Việt Nam (VN Post) theo địa chỉ kh&aacute;ch h&agrave;ng đ&atilde; đăng k&yacute;. Kh&aacute;n giả nhận v&eacute; cần phải c&oacute; CMND/Thẻ căn cước c&ocirc;ng d&acirc;n gốc để đối chiếu. C&ugrave;ng với đ&oacute;,&nbsp;kh&aacute;n giả v&agrave;o s&acirc;n sẽ phải ti&ecirc;m đủ hai mũi vaccin ngừa Covid 19 (mũi 2 đ&atilde; qua 14 ng&agrave;y) v&agrave; test Covid 19 c&oacute; kết quả &acirc;m t&iacute;nh trước 72 giờ trước giờ thi đấu...</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46658/877162.jpg\" style=\"height:399px; width:581px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Đại diện Tổng cục TDTT v&agrave; VFF ph&aacute;t biểu tại buổi họp</strong></p>\r\n\r\n<p style=\"text-align:center\">Kết luận cuộc họp, Thứ trưởng Ho&agrave;ng Đạo Cương cho biết, đ&acirc;y l&agrave; c&aacute;c hoạt động thể thao sau khi dịch bệnh thuy&ecirc;n giảm sau đợt b&ugrave;ng ph&aacute;t vừa qua. Ch&iacute;nh v&igrave; thế, b&ecirc;n cạnh l&agrave;m sao để trận đấu đạt y&ecirc;u cầu về chuy&ecirc;n m&ocirc;n th&igrave; cần phải đảm bảo hạn chế thấp nhất về dịch bệnh, đảm bảo an ninh trật tự. Tổng cục TDTT, VFF cần x&acirc;y dựng kế hoạch cụ thể, chi tiết, thực hiện cụ thể như thế n&agrave;o, từ mua v&eacute;, v&agrave;o s&acirc;n, thi đấu, ra về&hellip; Đặc biệt, cần tuy&ecirc;n truyền s&acirc;u rộng để người d&acirc;n biết r&otilde; v&agrave; thực hiện c&aacute;c biện ph&aacute;p, quy tr&igrave;nh ph&ograve;ng dịch khi đến dự kh&aacute;n trận đấu. &ldquo;Li&ecirc;n quan đến y tế, VFF l&agrave;m việc với Sở y tế H&agrave; Nội để triển khai chi tiết, kỹ lưỡng v&agrave; sẽ c&oacute; buổi kiểm tra việc tổ chức.&rdquo;, Thứ trưởng Ho&agrave;ng Đạo Cương nhấn mạnh.</p>\r\n', NULL, NULL),
(36, 1, 'Tổng thống Sierra Leone thăm Nhà triển lãm Việt Nam tại EXPO 2020 Dubai', 'VHO- Tối 14.10 (giờ địa phương), Tổng thống Cộng hòa Sierra Leone Julius Maada Bio đã tới thăm Nhà Triển lãm Việt Nam tại EXPO 2020 Dubai. Ông Trần Nhất Hoàng, Phó tổng đại diện Việt Nam tại EXPO 2020 Dubai, Phó Cục trưởng Cục Hợp tác quốc tế (Bộ VHTTL) và toàn thể cán bộ nhân viên Nhà Triển lãm Việt Nam đã tiếp đón trọng thể Tổng thống Sierra Leone và đoàn tuỳ tùng.', 'images/anh_vanhoa_36.jpg', '<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87033Anh-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tổng thống Sierra Leone d&agrave;nh nhiều thời gian tại khu 10 m&agrave;n h&igrave;nh TV giới thiệu 10 điều bạn cần biết về Việt Nam. Theo &ocirc;ng Trần Nhất Ho&agrave;ng, Tổng thống Julius Maada Bio đ&atilde; b&agrave;y tỏ sự ngưỡng mộ khi thấy h&igrave;nh ảnh Chủ tịch Hồ Ch&iacute; Minh đọc bản Tuy&ecirc;n ng&ocirc;n Độc lập khai sinh ra nước Việt Nam D&acirc;n chủ Cộng h&ograve;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87034Anh-2.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nghệ sĩ Ngọc Anh biểu diễn s&aacute;o tr&uacute;c v&agrave; một số nhạc cụ d&acirc;n tộc Việt Nam như đ&agrave;n K&rsquo;long put v&agrave; T&rsquo;rưng cho Tổng thống Sierra Leone v&agrave; đo&agrave;n tuỳ t&ugrave;ng</strong></p>\r\n\r\n<p>Tại EXPO 2020 Dubai, Nh&agrave; Triển l&atilde;m Việt Nam tổ chức tr&igrave;nh diễn &acirc;m nhạc truyền thống v&agrave; rối nước hằng ng&agrave;y. C&aacute;c nghệ sĩ c&ograve;n d&agrave;nh thời gian để giao lưu, hỗ trợ kh&aacute;n giả chơi thử một số nhạc cụ truyền thống của Việt Nam như cồng chi&ecirc;ng, đ&agrave;n đ&aacute;, đ&agrave;n T&rsquo;rưng, K&rsquo;long put&hellip; Hoạt động trải nghiệm n&agrave;y nhận được sự tham gia nhiệt t&igrave;nh của kh&aacute;ch tham quan. Trước đ&oacute;, Tổng Thư k&yacute; Tổ chức Ph&aacute;p ngữ, b&agrave; Louise Mushikiwabo cũng từng c&oacute; chuyến thăm Nh&agrave; Triển l&atilde;m Việt Nam. B&agrave; b&agrave;y tỏ sự th&iacute;ch th&uacute; khi lần đầu ti&ecirc;n được chơi đ&agrave;n K&rsquo;long put của Việt Nam.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87032Anh-3.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87035Anh-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87036Anh-5.jpg\" style=\"height:553px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&oacute; tổng đại diện Trần Nhất Ho&agrave;ng giới thiệu với Tổng thống Julius Maada Bio sản phẩm gạo ST25 của Việt Nam. Việt Nam v&agrave; Sierra Leone c&oacute; nhiều tiềm năng trong hợp t&aacute;c n&ocirc;ng nghiệp. Sierra Leone coi Việt Nam l&agrave; một trong những đối t&aacute;c h&agrave;ng đầu v&agrave; mong muốn đẩy mạnh hợp t&aacute;c song phương trong lĩnh vực thuỷ sản, sản xuất l&uacute;a gạo v&agrave; cử c&aacute;c chuy&ecirc;n gia Việt Nam sang Sierra Leone.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87037Anh-6.jpg\" style=\"height:543px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87038Anh-7.jpg\" style=\"height:522px; width:800px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tổng thống Sierra Leone nghe thuyết minh về c&aacute;c sản phẩm &ldquo;Make in Vietnam&rdquo; như vệ tinh, xe đạp tre&hellip; Nh&agrave; Triển l&atilde;m Việt Nam tại EXPO 2020 Dubai do Bộ VHTTDL tổ chức v&agrave; vận h&agrave;nh. Tại lần thứ 7 tham dự Triển l&atilde;m Thế giới, b&ecirc;n cạnh tiếp tục quảng b&aacute; c&aacute;c gi&aacute; trị văn h&oacute;a truyền thống, Nh&agrave; Triển l&atilde;m c&ograve;n t&iacute;ch cực giới thiệu với bạn b&egrave; quốc tế một số th&agrave;nh tựu khoa học c&ocirc;ng nghệ của Việt Nam ph&ugrave; hợp với ti&ecirc;u ch&iacute; ph&aacute;t triển bền vững m&agrave; EXPO 2020 đặt ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87039Anh-8.jpg\" style=\"height:543px; width:800px\" /></p>\r\n\r\n<p>Tổng thống Julius Maada Bio hiện đang c&oacute; mặt tại Dubai để tham dự Ng&agrave;y Quốc gia của Sierra Leone tại EXPO 2020 Dubai (14.10). &Ocirc;ng đ&atilde; c&oacute; cuộc gặp với Ph&oacute; Tổng thống, Thủ tướng UAE v&agrave; Quốc Vương Dubai, ng&agrave;i Seikh Mohammed bi Rashid Al Maktoum.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46360/87040Anh-9.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trước khi rời Nh&agrave; Triển l&atilde;m Việt Nam, Tổng thống Sierra Leone cho biết &ldquo;Việt Nam rất đẹp&rdquo; v&agrave; mong muốn c&oacute; cơ hội được đến thăm Việt Nam.</p>\r\n', NULL, NULL),
(37, 1, 'Tiếng đàn bầu Việt Nam ở EXPO 2020 Dubai', 'VHO- Lần đầu tiên đàn bầu Việt Nam hòa tấu cùng ban nhạc truyền thống Trung Đông và ngôi sao nổi tiếng người Ả-rập, nam ca sĩ Aseel Abu Baker trong Đêm nhạc Jalsat, tại Triển lãm thế giới EXPO 2020 Dubai (UAE).', 'images/anh_vanhoa_37.png', '<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46293/8685503.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại s&acirc;n khấu ngo&agrave;i trời lớn nhất trong khu tổ hợp EXPO 2020 Dubai, một trong những chương tr&igrave;nh nghệ thuật h&agrave;ng đầu của EXPO 2020 đ&atilde; diễn ra với sự g&oacute;p mặt của NSƯT đ&agrave;n bầu Việt Nam Lệ Giang.</p>\r\n\r\n<p>Đ&ecirc;m nhạc Jalsat l&agrave; loạt chương tr&igrave;nh tổ chức h&agrave;ng th&aacute;ng, từ th&aacute;ng 10.2021 đến th&aacute;ng 3.2022 trong khu&ocirc;n khổ EXPO 2020. Chương tr&igrave;nh nhận được rất nhiều sự quan t&acirc;m của truyền th&ocirc;ng v&agrave; người d&acirc;n c&aacute;c nước V&ugrave;ng Vịnh. C&aacute;c nh&agrave; tổ chức tham vọng kh&ocirc;ng chỉ giới thiệu một phần di sản &acirc;m nhạc của UAE m&agrave; c&ograve;n đem lại cho người xem một trải nghiệm độc nhất v&ocirc; nhị khi đưa c&aacute;c nhạc cụ d&acirc;n tộc của một số quốc gia tr&ecirc;n thế giới c&ugrave;ng tr&igrave;nh diễn với ban nhạc truyền thống Trung Đ&ocirc;ng v&agrave; c&aacute;c nghệ sĩ t&ecirc;n tuổi trong khu vực.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46293/8685602.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&agrave;n bầu Việt Nam l&agrave; nhạc cụ duy nhất được mời tham gia ngay trong chương tr&igrave;nh Đ&ecirc;m nhạc Jalsat số đầu ti&ecirc;n. Xuất hiện trong t&agrave; &aacute;o d&agrave;i m&agrave;u v&agrave;ng rực rỡ, NSƯT Lệ Giang gần như ngay lập tức trở th&agrave;nh t&acirc;m điểm của s&acirc;n khấu. &Acirc;m thanh đ&agrave;n bầu đ&atilde; mở đầu tiết mục một c&aacute;ch đầy tự tin, dẫn dắt người nghe v&agrave;o giai điệu s&ocirc;i động của ca kh&uacute;c Gedar Gedar do nam ca sĩ Aseel Abu Baker tr&igrave;nh b&agrave;y. Sự đi&ecirc;u luyện của nghệ sỹ Việt Nam được thể hiện r&otilde; trong những ph&uacute;t ngẫu hứng duy&ecirc;n d&aacute;ng v&agrave; đầy h&ograve;a hợp với giọng h&aacute;t ấm &aacute;p của danh ca người Ả-rập X&ecirc;-&uacute;t.</p>\r\n\r\n<p>Aseel Abu Baker l&agrave; ng&ocirc;i sao rất được ưa th&iacute;ch tại khu vực V&ugrave;ng Vịnh. Cha của anh, cố nghệ sĩ Abu Bakr Salem Balfaqih l&agrave; một huyền thoại, được coi l&agrave; &ldquo;cha đẻ của d&ograve;ng nhạc Ả-rập đương đại&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"Đàn bầu Việt Nam là nhạc cụ duy nhất được mời tham gia ngay trong chương trình Đêm nhạc Jalsat số đầu tiên. Xuất hiện trong tà áo dài màu vàng rực rỡ, NSƯT Lệ Giang gần như ngay lập tức trở thành tâm điểm của sân khấu. Âm thanh đàn bầu đã mở đầu tiết mục một cách đầy tự tin, dẫn dắt người nghe vào giai điệu sôi động của ca khúc Gedar Gedar do nam ca sĩ Aseel Abu Baker trình bày. Sự điêu luyện của nghệ sỹ Việt Nam được thể hiện rõ trong những phút ngẫu hứng duyên dáng và đầy hòa hợp với giọng hát ấm áp của danh ca người Ả-rập Xê-út.  Aseel Abu Baker là ngôi sao rất được ưa thích tại khu vực Vùng Vịnh. Cha của anh, cố nghệ sĩ Abu Bakr Salem Balfaqih là một huyền thoại, được coi là “cha đẻ của dòng nhạc Ả-rập đương đại”.\" /><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46293/8685704.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện đang giảng dạy tại Học viện &Acirc;m nhạc quốc gia Việt Nam, NSƯT Lệ Giang đ&atilde; c&oacute; hơn 30 năm gắn b&oacute; với c&acirc;y đ&agrave;n bầu. C&ocirc; cũng l&agrave; nghệ sĩ hiếm hoi từng đồng h&agrave;nh với Nh&agrave; Triển l&atilde;m Việt Nam tại c&aacute;c kỳ EXPO trước đ&acirc;y như Thượng Hải, Trung Quốc (2010), Yeosu, H&agrave;n Quốc (2012), Milano, &Yacute; (2015), Astana, Kazakhstan (2017) v&agrave; giờ đ&acirc;y l&agrave; EXPO 2020 Dubai.</p>\r\n\r\n<p>Trong hơn 30 năm chơi đ&agrave;n bầu, NSƯT Lệ Giang từng biểu diễn tại hơn 80 quốc gia v&agrave; d&agrave;nh được nhiều giải thưởng, danh hiệu cao qu&yacute;. Chị cũng li&ecirc;n tục tham gia c&aacute;c chương tr&igrave;nh biểu diễn tr&ecirc;n truyền h&igrave;nh trong v&agrave; ngo&agrave;i nước nhằm giới thiệu về c&acirc;y đ&agrave;n bầu Việt Nam.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46293/8685901.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i về buổi biểu diễn đầy ấn tượng lần n&agrave;y, NSƯT Lệ Giang chia sẻ: &ldquo;T&ocirc;i rất cảm k&iacute;ch khi được ban tổ chức ch&agrave;o đ&oacute;n nồng nhiệt, c&aacute;c nghệ sĩ th&acirc;n thiện, ấm &aacute;p v&agrave; đặc biệt l&agrave; được &ldquo;đối thoại&rdquo; v&agrave; &ldquo;phi&ecirc;u&rdquo; c&ugrave;ng ca sĩ nổi tiếng Aseel Abu Baker. Sử dụng những nhạc cụ ti&ecirc;u biểu của mỗi d&acirc;n tộc, ch&uacute;ng t&ocirc;i đ&atilde; c&ugrave;ng ho&agrave; tấu n&ecirc;n giai điệu bạn b&egrave;, những thanh &acirc;m của t&igrave;nh hữu nghị, kết nối v&agrave; đến gần nhau hơn&quot;.</p>\r\n\r\n<p>Đ&ecirc;m nhạc Jalsat với ban nhạc truyền thống gồm những nhạc c&ocirc;ng nổi tiếng ở Ả-rập đến từ nhiều nước V&ugrave;ng Vịnh kh&aacute;c nhau. C&aacute;c th&agrave;nh vi&ecirc;n ban nhạc từng cộng t&aacute;c với c&aacute;c nghệ sĩ hạng A, thường xuy&ecirc;n tr&igrave;nh diễn tr&ecirc;n c&aacute;c k&ecirc;nh truyền h&igrave;nh lớn v&agrave; tham gia c&aacute;c sự kiện &acirc;m nhạc h&agrave;ng đầu khu vực. Phần lớn trong số họ đ&atilde; được UAE trao &ldquo;visa văn h&oacute;a&rdquo; 10 năm v&igrave; danh tiếng v&agrave; những cống hiến cho nghệ thuật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46293/8685805.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave; một trong những sự kiện triển l&atilde;m phi thương mại lớn nhất thế giới, EXPO 2020 Dubai lần đầu ti&ecirc;n diễn ra tại khu vực Trung Đ&ocirc;ng đ&atilde; thu h&uacute;t sự tham gia của hơn 190 quốc gia v&agrave; c&aacute;c tổ chức quốc tế. Ng&agrave;y 1.10 vừa qua, Nh&agrave; Triển l&atilde;m Việt Nam đ&atilde; ch&iacute;nh thức mở cửa đ&oacute;n kh&aacute;ch. Trong lần thứ 7 tham dự Triển l&atilde;m Thế giới, b&ecirc;n cạnh việc tiếp tục quảng b&aacute; v&agrave; t&ocirc;n vinh c&aacute;c tinh h&oacute;a văn h&oacute;a truyền thống d&acirc;n tộc, Nh&agrave; Triển l&atilde;m Việt Nam c&ograve;n giới thiệu một số tiến bộ của Việt Nam trong lĩnh vực khoa học c&ocirc;ng nghệ theo hướng bền vững, th&acirc;n thiện m&ocirc;i trường.</p>\r\n\r\n<p>Trong suốt 6 th&aacute;ng tại EXPO 2020, Nh&agrave; Triển l&atilde;m Việt Nam sẽ tổ chức v&agrave; tham gia nhiều hoạt động tr&ecirc;n c&aacute;c lĩnh vực kh&aacute;c nhau, nhưng được mong chờ nhất sẽ l&agrave; Ng&agrave;y Quốc gia (30.12.2021) nơi Việt Nam thật sự trở th&agrave;nh t&acirc;m điểm của to&agrave;n bộ sự kiện.</p>\r\n\r\n<p>Để thưởng thức tiết mục của NSƯT Lệ Giang v&agrave; cập nhật c&aacute;c th&ocirc;ng tin, hoạt động của Nh&agrave; Triển l&atilde;m Việt Nam, bạn đọc c&oacute; thể truy cập trang web ch&iacute;nh thức của Nh&agrave; Triển l&atilde;m tại&nbsp;<a href=\"http://www.vietnamexpo2020dubai.com/\">www.vietnamexpo2020dubai.com</a>&nbsp;hoặc theo d&otilde;i c&aacute;c t&agrave;i khoản của Nh&agrave; Triển l&atilde;m Việt Nam tr&ecirc;n Facebook, Instgram v&agrave; Youtube.</p>\r\n', NULL, NULL),
(38, 1, 'Thông qua 5 dự án tu bổ, bảo tồn các di tích quan trọng của di sản Huế', 'VHO- HĐND tỉnh Thừa Thiên Huế khóa 13 (nhiệm kỳ 2021-2026) vừa thông qua 15 nghị quyết quan trọng, liên quan đến kế hoạch phát triển kinh tế- xã hội của địa phương này. Trong đó, đáng lưu ý có 5 dự án bảo tồn, tu bổ và phát huy giá trị tổng thể di tích Huế, với kinh phí khoảng 460 tỷ đồng.', 'images/anh_hue_38.jpg', '<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46363/87050Dan-Nam-Giao.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đ&agrave;n Nam Giao triều Nguyễn dự kiến sẽ được bảo tồn phần c&ograve;n lại với kinh ph&iacute; hơn 40,3 tỷ đồng (ảnh: Cổng v&agrave;o di t&iacute;ch Đ&agrave;n Nam Giao)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cụ thể, 5 dự &aacute;n bảo tồn, tu bổ di t&iacute;ch quan trọng được th&ocirc;ng qua tại kỳ họp chuy&ecirc;n đề lần thứ 2 của HĐND tỉnh Thừa Thi&ecirc;n Huế kh&oacute;a 13, gồm: Dự &aacute;n tu bổ, phục hồi v&agrave; t&ocirc;n tạo điện Cần Ch&aacute;nh (Ho&agrave;ng th&agrave;nh Huế) với kinh ph&iacute; gần 200 tỷ đồng; dự &aacute;n bảo tồn, tu bổ di t&iacute;ch Đ&agrave;n Nam Giao (phần c&ograve;n lại) với kinh ph&iacute; hơn 40,3 tỷ đồng; dự &aacute;n bảo tồn th&iacute;ch nghi di t&iacute;ch Quốc Tử Gi&aacute;m sau khi Bảo t&agrave;ng Lịch sử tỉnh ho&agrave;n th&agrave;nh việc di dời đến địa điểm mới, với kinh ph&iacute; hơn 60,5 tỷ đồng; dự &aacute;n bảo tồn, tu bổ v&agrave; ph&aacute;t huy di t&iacute;ch lăng Thiệu Trị giai đoạn 3 c&oacute; kinh ph&iacute; hơn 60,5 tỷ đồng v&agrave; dự &aacute;n bảo quản, tu bổ, phục hồi di t&iacute;ch lăng Tự Đức (phần c&ograve;n lại) với kinh ph&iacute; gần 100 tỷ đồng.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; những dự &aacute;n c&oacute; t&iacute;nh chất đặc biệt, quy tr&igrave;nh lập c&aacute;c thủ tục đầu tư dự &aacute;n thực hiện nhiều bước theo quy định của Luật X&acirc;y dựng, Luật Đầu tư c&ocirc;ng v&agrave; Luật Di sản văn ho&aacute;. Để dự &aacute;n sớm triển khai thi c&ocirc;ng trong năm 2021, c&aacute;c đại biểu HĐND tỉnh đề nghị UBND tỉnh quan t&acirc;m chỉ đạo chủ đầu tư khẩn trương ho&agrave;n chỉnh c&aacute;c thủ tục ph&aacute;p l&yacute; li&ecirc;n quan, tập trung nghi&ecirc;n cứu, sưu tầm tư liệu, tham vấn &yacute; kiến của c&aacute;c chuy&ecirc;n gia về mặt kiển tr&uacute;c, mỹ thuật, khảo cổ... trong qu&aacute; tr&igrave;nh lập dự &aacute;n để triển khai c&oacute; hiệu quả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46363/87049Di-tich-Hue.jpg\" style=\"height:372px; width:600px\" /></p>\r\n\r\n<p><strong>Chương tr&igrave;nh t&aacute;i hiện lễ Nguy&ecirc;n Đ&aacute;n trong Ho&agrave;ng cung Huế phục vụ du kh&aacute;ch hồi đầu năm 2021</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng tại kỳ họp lần n&agrave;y, HĐND tỉnh Thừa Thi&ecirc;n Huế cũng đồng &yacute; việc tạm ứng kinh ph&iacute; hoạt động chi thường xuy&ecirc;n năm 2021 cho Trung t&acirc;m Bảo tồn Di t&iacute;ch Cố đ&ocirc; Huế với số tiền 65 tỷ đồng, nhằm kịp thời chi trả lương v&agrave; thực hiện c&aacute;c nhiệm vụ cấp thiết trong năm nay. Do ảnh hưởng của dịch bệnh Covid-19, nguồn thu từ b&aacute;n v&eacute; tham quan di sản Huế từ đầu năm đến hết th&aacute;ng 7 vừa qua chỉ đạt 19,4 tỷ đồng; dự kiến, trong c&aacute;c th&aacute;ng cuối năm, nguồn thu tăng th&ecirc;m khoảng 600 triệu đồng (đạt 16,7% so với dự to&aacute;n). &nbsp;</p>\r\n\r\n<p>Theo UBND tỉnh Thừa Thi&ecirc;n Huế, từ khi ảnh hưởng bởi dịch bệnh, Trung t&acirc;m Bảo tồn Di t&iacute;ch Cố đ&ocirc; Huế sắp xếp c&ocirc;ng việc hợp l&yacute; cho c&aacute;n bộ, cắt c&aacute;c khoản chi chưa cần thiết, hạn chế việc mua sắm, sửa chữa t&agrave;i sản&hellip; th&igrave; nhu cầu kinh ph&iacute; tối thiểu để thực hiện c&aacute;c nhiệm vụ của Trung t&acirc;m trong năm 2021 l&agrave; 85 tỷ đồng. Trong khi nguồn thu chỉ đạt khoảng 20 tỷ đồng, n&ecirc;n cần được tạm ứng ng&acirc;n s&aacute;ch tỉnh 65 tỷ đồng để đảm bảo hoạt động. Trong năm 2022 - 2023, Trung t&acirc;m Bảo tồn Di t&iacute;ch Cố đ&ocirc; Huế c&oacute; tr&aacute;ch nhiệm sử dụng nguồn thu ph&iacute; tham quan v&agrave; c&aacute;c nguồn thu hợp ph&aacute;p kh&aacute;c để ho&agrave;n trả đầy đủ ng&acirc;n s&aacute;ch tỉnh số tiền đ&atilde; tạm ứng.</p>\r\n', NULL, NULL),
(39, 1, 'Xây dựng chương trình nghệ thuật truyền thống phục vụ phố đi bộ Hoàng thành Huế', 'VHO- UBND TP. Huế và Trung tâm Bảo tồn Di tích Cố đô Huế đang phối hợp xây dựng đề án tổ chức phố đi bộ ở Hoàng thành tại đường Lê Huân và đường 23 tháng 8 (xung quanh Đại Nội Huế).', 'images/anh_vanhoa_39.jpg', '<p>Phố đi bộ Ho&agrave;ng th&agrave;nh sẽ t&aacute;i hiện kh&ocirc;ng gian về một Huế xưa để du kh&aacute;ch thưởng thức c&aacute;c loại h&igrave;nh diễn xướng, trải nghiệm tr&ograve; chơi d&acirc;n gian, c&aacute;c nghề truyền thống, thưởng thức ẩm thực Huế&hellip; Nhằm n&acirc;ng cao chất lượng cho phố đi bộ, Nh&agrave; h&aacute;t Nghệ thuật truyền thống Cung đ&igrave;nh Huế (thuộc Trung t&acirc;m Bảo tồn Di t&iacute;ch Cố đ&ocirc; Huế) cũng x&acirc;y dựng c&aacute;c chương tr&igrave;nh biểu diễn đặc sắc dựa tr&ecirc;n nền tảng chất liệu truyền thống. Chương tr&igrave;nh biểu diễn được x&acirc;y dựng c&ocirc;ng phu, gồm c&aacute;c loại h&igrave;nh nghệ thuật: Ca Huế, tuồng Huế, m&uacute;a h&aacute;t cung đ&igrave;nh&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46361/87042NT2.jpg\" /></p>\r\n\r\n<p><strong>C&aacute;c nghệ sĩ Nh&agrave; h&aacute;t Nghệ thuật truyền thống Cung đ&igrave;nh Huế x&acirc;y dựng chương tr&igrave;nh&nbsp;biểu diễn nhằm phục vụ phố đi bộ Ho&agrave;ng th&agrave;nh</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong chiều ng&agrave;y 14.10, tại Nh&agrave; h&aacute;t Duyệt Thị Đường (Đại Nội Huế), Nh&agrave; h&aacute;t Nghệ thuật truyền thống Cung đ&igrave;nh Huế đ&atilde; tổ chức buổi biểu diễn b&aacute;o c&aacute;o về nội dung chương tr&igrave;nh n&oacute;i tr&ecirc;n. Với c&aacute;c tiết mục: hoạt cảnh&nbsp;<em>Mi&ecirc;n man nhớ Huế</em>, tr&iacute;ch đoạn tuồng cổ&nbsp;<em>Ph&agrave;n L&ecirc; Ba đại chiến thiết bạc</em>&nbsp;(trong vở tuồng&nbsp;<em>Đ&agrave;ng Chinh T&acirc;y</em>), h&ograve;a tấu &acirc;m nhạc&nbsp;<em>X&acirc;y dựng Kinh đ&ocirc;</em>&nbsp;v&agrave; m&uacute;a T<em>hanh tr&agrave; hiến quả</em>&hellip; Chương tr&igrave;nh nghệ thuật truyền thống n&agrave;y sẽ được xem x&eacute;t, d&agrave;n dựng ph&ugrave; hợp khi thực hiện biểu diễn ở kh&ocirc;ng gian ngo&agrave;i trời, hướng đến phục vụ du kh&aacute;ch tại phố đi bộ Ho&agrave;ng th&agrave;nh. Dự kiến phố đi bộ Ho&agrave;ng th&agrave;nh Huế sẽ được đưa v&agrave;o hoạt động v&agrave;o đầu năm 2022.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46361/87041NT-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Một nghệ sĩ h&oacute;a h&oacute;a trang&nbsp;biểu diễn tr&iacute;ch đoạn tuổng cổ</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay, tại TP. Huế đ&atilde; c&oacute; phố đi bộ (thường gọi l&agrave; phố T&acirc;y) ở c&aacute;c tuyến đường V&otilde; Thị S&aacute;u- Chu Văn An- Phạm Ngũ L&atilde;o, với c&aacute;c dịch vụ theo xu hướng hiện đại. Thế n&ecirc;n, việc x&acirc;y dựng phố đi bộ Ho&agrave;ng th&agrave;nh ở khu vực xung quanh Đại Nội Huế, được xem x&eacute;t v&agrave; c&acirc;n nhắc nhằm ph&ugrave; hợp với kh&ocirc;ng gian kiến tr&uacute;c, cảnh quan di sản văn h&oacute;a. Việc lựa chọn khai th&aacute;c dịch vụ tr&ecirc;n nền tảng truyền thống được ủng hộ.</p>\r\n\r\n<p>Theo kế hoạch của UBND TP. Huế, ngo&agrave;i việc x&acirc;y dựng phố đi bộ ở Ho&agrave;ng th&agrave;nh trở th&agrave;nh điểm đến hấp dẫn phục vụ du kh&aacute;ch, th&igrave; địa phương n&agrave;y cũng đang đẩy nhanh việc x&acirc;y dựng v&agrave; khai th&aacute;c chợ đ&ecirc;m Đ&ocirc;ng Ba. Khu chợ n&agrave;y sẽ l&agrave; nơi mua sắm của du kh&aacute;ch v&agrave; cộng đồng về đ&ecirc;m, k&egrave;m theo c&aacute;c dịch vụ về ẩm thực đặc trưng của Huế. Địa phương n&agrave;y cũng l&ecirc;n kế hoạch &ldquo;đ&oacute;ng cầu&rdquo; Trường Tiền v&agrave;o buổi tối c&aacute;c ng&agrave;y cuối tuần để l&agrave;m tuyến đường đi bộ, kết nối phố T&acirc;y v&agrave; chợ đ&ecirc;m Đ&ocirc;ng Ba, phố đi bộ Ho&agrave;ng th&agrave;nh.</p>\r\n', NULL, NULL),
(40, 1, 'Lai Châu sẵn sàng tổ chức Ngày hội Văn hóa dân tộc Mông lần thứ III', 'VHO- Dự kiến, Ngày hội Văn hóa dân tộc Mông toàn quốc lần thứ III sẽ được tổ chức từ ngày 24 - 26.12.2021 tại TP. Lai Châu. Đây là dịp để các tỉnh tham gia học tập, trao đổi kinh nghiệm, nâng cao nhận thức, trách nhiệm của các cấp, ngành và đồng bào các dân tộc trong việc giữ gìn bản sắc văn hóa dân tộc Mông, góp phần xây dựng và phát triển văn hóa, con người Việt Nam.', 'images/anh-vanhoa5.jpg', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46522/87396mong2.jpg\" style=\"height:385px; width:578px\" /></p>\r\n\r\n<p>&nbsp;&nbsp;<strong>Ng&agrave;y hội Văn h&oacute;a d&acirc;n tộc M&ocirc;ng to&agrave;n quốc lần thứ II tại H&agrave; Giang</strong></p>\r\n\r\n<p>Ng&agrave;y 20.10, Ban Tổ chức Ng&agrave;y hội Văn h&oacute;a d&acirc;n tộc M&ocirc;ng to&agrave;n quốc lần thứ III tại tỉnh Lai Ch&acirc;u, năm 2021 đ&atilde; tổ chức họp về c&ocirc;ng t&aacute;c&nbsp;tổ chức Ng&agrave;y hội.</p>\r\n\r\n<p>Theo đ&oacute;, Ng&agrave;y hội Văn h&oacute;a d&acirc;n tộc M&ocirc;ng to&agrave;n quốc lần thứ III tại tỉnh Lai Ch&acirc;u, năm 2021&nbsp;do Bộ&nbsp;VHTTDL phối hợp với UBND tỉnh Lai Ch&acirc;u&nbsp;tổ chức. Dự kiến, Ng&agrave;y hội sẽ&nbsp;diễn ra&nbsp;trong 3 ng&agrave;y&nbsp;(từ ng&agrave;y&nbsp;24 - 26.12.2021) tại TP. Lai Ch&acirc;u với sự tham gia của 14 tỉnh c&oacute; đồng b&agrave;o d&acirc;n tộc M&ocirc;ng sinh&nbsp;sống.</p>\r\n\r\n<p>Với chủ đề &ldquo;Bảo tồn, ph&aacute;t huy bản sắc văn h&oacute;a d&acirc;n tộc M&ocirc;ng b&igrave;nh đẳng, đo&agrave;n kết, hội nhập v&agrave; ph&aacute;t triển&rdquo; gồm&nbsp;nhiều nội dung như&nbsp;Li&ecirc;n hoan văn nghệ quần ch&uacute;ng, tr&igrave;nh diễn trang phục truyền thống d&acirc;n tộc M&ocirc;ng; tr&igrave;nh diễn, giới thiệu tr&iacute;ch đoạn lễ hội, nghi thức sinh hoạt văn h&oacute;a d&acirc;n tộc M&ocirc;ng; kh&ocirc;ng gian trưng b&agrave;y giới thiệu, quảng b&aacute; sản phẩm văn h&oacute;a v&agrave; du lịch của 14 tỉnh tham gia Ng&agrave;y hội; thi gi&atilde; b&aacute;nh giầy; triển l&atilde;m &ldquo;Đặc trưng văn h&oacute;a d&acirc;n tộc M&ocirc;ng trong cộng đồng văn h&oacute;a c&aacute;c d&acirc;n tộc Việt Nam&rdquo;; hoạt động thể thao quần ch&uacute;ng d&acirc;n tộc M&ocirc;ng; hoạt động Du lịch; kh&ocirc;ng gian trưng b&agrave;y, quảng b&aacute; sản phẩm văn h&oacute;a, du lịch, OCOP tỉnh Lai Ch&acirc;u&hellip;Ng&agrave;y hội l&agrave; dịp để c&aacute;c tỉnh tham gia học tập, trao đổi kinh nghiệm, n&acirc;ng cao nhận thức, tr&aacute;ch nhiệm của c&aacute;c cấp, ng&agrave;nh v&agrave; đồng b&agrave;o c&aacute;c d&acirc;n tộc trong việc giữ g&igrave;n bản sắc văn h&oacute;a d&acirc;n tộc M&ocirc;ng, g&oacute;p phần x&acirc;y dựng v&agrave; ph&aacute;t triển văn h&oacute;a, con người Việt Nam. Đồng thời,&nbsp;l&agrave; dịp để giới thiệu, quảng b&aacute; c&aacute;c gi&aacute; trị văn h&oacute;a truyền thống của d&acirc;n tộc M&ocirc;ng tới bạn b&egrave; trong nước v&agrave; quốc tế, g&oacute;p phần ph&aacute;t triển du lịch, kinh tế-x&atilde; hội.</p>\r\n\r\n<p>Đến nay, Sở VHTTDL đ&atilde; tham mưu cho UBND tỉnh Lai Ch&acirc;u gửi th&ocirc;ng b&aacute;o đến c&aacute;c tỉnh tham gia Ng&agrave;y hội,&nbsp;giao nhiệm vụ cho c&aacute;c tiểu ban nội dung, tuy&ecirc;n truyền kh&aacute;nh tiết v&agrave; thi đua khen thưởng,&nbsp;hậu cần, lễ t&acirc;n, an ninh, chăm s&oacute;c sức khỏe v&agrave; triển khai c&aacute;c hoạt động chuẩn bị cho Ng&agrave;y hội,&nbsp;đảm bảo an to&agrave;n trong c&ocirc;ng t&aacute;c ph&ograve;ng, chống dịch Covid-19.</p>\r\n\r\n<p>Theo Ban Tổ chức, c&ocirc;ng t&aacute;c&nbsp;chuẩn bị đang được tiến h&agrave;nh khẩn trương,&nbsp;chu đ&aacute;o, sẵn s&agrave;ng để Ng&agrave;y hội diễn ra một c&aacute;ch trang trọng v&agrave; lan tỏa được bản sắc văn h&oacute;a tốt đẹp của đồng b&agrave;o M&ocirc;ng tr&ecirc;n cả nước, lan tỏa được tiềm năng, thế mạnh, vẻ đẹp của miền đất, con người Lai Ch&acirc;u tới bạn b&egrave; trong nước v&agrave; quốc tế.</p>\r\n\r\n<p>Ng&agrave;y hội Văn h&oacute;a d&acirc;n tộc M&ocirc;ng to&agrave;n quốc lần thứ III, năm 2021 c&ograve;n&nbsp;l&agrave; hoạt động c&oacute; &yacute; nghĩa rất quan trọng đối với tỉnh Lai Ch&acirc;u v&agrave; c&aacute;c địa phương tham gia, g&oacute;p phần k&iacute;ch cầu du lịch, quảng b&aacute; h&igrave;nh ảnh sau thời gian d&agrave;i nhiều hoạt động bị ảnh hưởng do dịch bệnh Covid-19.&nbsp;</p>\r\n', NULL, NULL),
(41, 3, 'Khánh Hòa: Ứng dụng công nghệ số vào hoạt động nghệ thuật', 'VHO - Do tình hình dịch bệnh Covid-19 kéo dài, nhiều hoạt động văn hóa, nghệ thuật trên địa bàn tỉnh Khánh Hòa gặp nhiều khó khăn. Để ứng phó với dịch bệnh, Sở VHTT Khánh Hòa đã và đang ứng dụng công nghệ số, xây dựng các chương trình nghệ thuật trực tuyến để phục vụ người dân trên không gian mạng.', 'images/anh_xahoi_41.jpg', '<p style=\"text-align:center\">&Ocirc;ng Nguyễn Thanh H&agrave; &ndash; Gi&aacute;m đốc Sở VHTT, cho biết: Từ cuối th&aacute;ng 4 đến nay, khi dịch Covid-19 b&ugrave;ng ph&aacute;t, ng&agrave;nh Văn h&oacute;a tỉnh chịu ảnh hưởng lớn, nhiều hoạt động lễ hội, sự kiện văn h&oacute;a, văn nghệ tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh Kh&aacute;nh H&ograve;a phải dừng hoạt động để đảm bảo an to&agrave;n sức khỏe cho cộng đồng. Để th&aacute;o gỡ phần n&agrave;o kh&oacute; khăn, th&iacute;ch ứng với t&igrave;nh h&igrave;nh dịch bệnh nhiều hoạt động văn h&oacute;a, nghệ thuật của c&aacute;c đơn vị trực thuộc Sở phải chuyển sang h&igrave;nh thức tổ chức &ldquo;nh&agrave; h&aacute;t online&rdquo;, &ldquo;s&acirc;n khấu trực tuyến&rdquo; th&ocirc;ng qua c&aacute;c k&ecirc;nh th&ocirc;ng tin fanpage hoặc t&agrave;i khoản YouTube.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/xuanhuong/0_01_18.jpg\" /></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\"><strong>Biểu diễn tại khu c&aacute;ch ly&nbsp;tập trung phục vụ người mắc bệnh Covid-19, v&agrave; đội ngũ y b&aacute;c sĩ tuyến đầu chống dịch</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&nbsp;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">B&ecirc;n cạnh đ&oacute;, c&aacute;c hoạt động văn h&oacute;a, lễ hội phải thu nhỏ quy m&ocirc;, dung lượng tổ chức với nhiều c&aacute;ch thức kh&aacute;c nhau. Một số lễ hội quan trọng chỉ được tổ chức phần lễ, kh&ocirc;ng tổ chức phần hội; nhiều hoạt động văn h&oacute;a tập trung đ&ocirc;ng người th&igrave; dừng tổ chức, ho&atilde;n tổ chức. Một số chương tr&igrave;nh văn h&oacute;a, văn nghệ đ&atilde; được d&agrave;n dựng nhưng chưa tổ chức biểu diễn, ng&agrave;nh Văn h&oacute;a tỉnh phối hợp với b&aacute;o đ&agrave;i ghi h&igrave;nh v&agrave; ph&aacute;t tr&ecirc;n s&oacute;ng truyền h&igrave;nh, mạng x&atilde; hội, c&aacute;c k&ecirc;nh th&ocirc;ng tin số.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Hiện nay, Nh&agrave; h&aacute;t Nghệ thuật truyền thống, Đo&agrave;n Ca m&uacute;a nhạc Hải Đăng hay Trung t&acirc;m Văn h&oacute;a - Điện ảnh đều c&oacute; fanpage hoặc t&agrave;i khoản YouTube nhưng cũng mới chỉ dừng lại ở việc giới thiệu thời gian, địa điểm biểu diễn hoặc đăng một số tiết mục, chương tr&igrave;nh nghệ thuật của đơn vị.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&nbsp;Một số kh&aacute;n giả y&ecirc;u nghệ thuật, cho rằng: Việc c&aacute;c k&ecirc;nh online, fanpage của những đơn vị nghệ thuật chưa thu h&uacute;t được kh&aacute;n giả đ&oacute; l&agrave; do nội dung chưa phong ph&uacute;; kh&aacute;n giả vẫn chưa quen với việc tương t&aacute;c v&agrave; hưởng thụ nghệ thuật tr&ecirc;n kh&ocirc;ng gian mạng; sự xuất hiện của c&aacute;c chương tr&igrave;nh gameshow, giải tr&iacute; khiến cho việc l&ecirc;n mạng xem kịch hay biểu diễn nghệ thuật bị xao l&atilde;ng.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Để th&aacute;o gỡ được những vướng mắc đ&oacute;, c&aacute;c đơn vị văn h&oacute;a nghệ thuật (Sở VHTT tỉnh Kh&aacute;nh H&ograve;a) cần x&acirc;y dựng kế hoạch, chương tr&igrave;nh biểu diễn online một c&aacute;ch b&agrave;i bản, c&oacute; đầu tư, tr&ecirc;n cơ sở đ&oacute; dần định h&igrave;nh phong c&aacute;ch v&agrave; x&acirc;y dựng thương hiệu cho m&igrave;nh. Mỗi đơn vị n&ecirc;n mạnh dạn thay đổi tư duy mới tổ chức biểu diễn tr&ecirc;n kh&ocirc;ng gian mạng. L&agrave;m được như thế mới mong c&oacute; được những chương tr&igrave;nh ph&ugrave; hợp với thị hiếu kh&aacute;n giả trong bối cảnh mới, kh&aacute;n giả mới.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\"><strong>Biểu diễn tại khu c&aacute;ch ly&nbsp;tập trung phục vụ người mắc bệnh Covid-19, v&agrave; đội ngũ y b&aacute;c sĩ tuyến đầu chống dịch</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&nbsp;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">B&ecirc;n cạnh đ&oacute;, c&aacute;c hoạt động văn h&oacute;a, lễ hội phải thu nhỏ quy m&ocirc;, dung lượng tổ chức với nhiều c&aacute;ch thức kh&aacute;c nhau. Một số lễ hội quan trọng chỉ được tổ chức phần lễ, kh&ocirc;ng tổ chức phần hội; nhiều hoạt động văn h&oacute;a tập trung đ&ocirc;ng người th&igrave; dừng tổ chức, ho&atilde;n tổ chức. Một số chương tr&igrave;nh văn h&oacute;a, văn nghệ đ&atilde; được d&agrave;n dựng nhưng chưa tổ chức biểu diễn, ng&agrave;nh Văn h&oacute;a tỉnh phối hợp với b&aacute;o đ&agrave;i ghi h&igrave;nh v&agrave; ph&aacute;t tr&ecirc;n s&oacute;ng truyền h&igrave;nh, mạng x&atilde; hội, c&aacute;c k&ecirc;nh th&ocirc;ng tin số.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Hiện nay, Nh&agrave; h&aacute;t Nghệ thuật truyền thống, Đo&agrave;n Ca m&uacute;a nhạc Hải Đăng hay Trung t&acirc;m Văn h&oacute;a - Điện ảnh đều c&oacute; fanpage hoặc t&agrave;i khoản YouTube nhưng cũng mới chỉ dừng lại ở việc giới thiệu thời gian, địa điểm biểu diễn hoặc đăng một số tiết mục, chương tr&igrave;nh nghệ thuật của đơn vị.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&nbsp;Một số kh&aacute;n giả y&ecirc;u nghệ thuật, cho rằng: Việc c&aacute;c k&ecirc;nh online, fanpage của những đơn vị nghệ thuật chưa thu h&uacute;t được kh&aacute;n giả đ&oacute; l&agrave; do nội dung chưa phong ph&uacute;; kh&aacute;n giả vẫn chưa quen với việc tương t&aacute;c v&agrave; hưởng thụ nghệ thuật tr&ecirc;n kh&ocirc;ng gian mạng; sự xuất hiện của c&aacute;c chương tr&igrave;nh gameshow, giải tr&iacute; khiến cho việc l&ecirc;n mạng xem kịch hay biểu diễn nghệ thuật bị xao l&atilde;ng.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">Để th&aacute;o gỡ được những vướng mắc đ&oacute;, c&aacute;c đơn vị văn h&oacute;a nghệ thuật (Sở VHTT tỉnh Kh&aacute;nh H&ograve;a) cần x&acirc;y dựng kế hoạch, chương tr&igrave;nh biểu diễn online một c&aacute;ch b&agrave;i bản, c&oacute; đầu tư, tr&ecirc;n cơ sở đ&oacute; dần định h&igrave;nh phong c&aacute;ch v&agrave; x&acirc;y dựng thương hiệu cho m&igrave;nh. Mỗi đơn vị n&ecirc;n mạnh dạn thay đổi tư duy mới tổ chức biểu diễn tr&ecirc;n kh&ocirc;ng gian mạng. L&agrave;m được như thế mới mong c&oacute; được những chương tr&igrave;nh ph&ugrave; hợp với thị hiếu kh&aacute;n giả trong bối cảnh mới, kh&aacute;n giả mới.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\"><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/xuanhuong/0_01_19.jpg\" /></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\"><strong>Trong điều kiện dịch bệnh kh&oacute; khăn, nhưng c&aacute;c nghệ sĩ vẫn miệt m&agrave;i biểu diễn</strong></span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:center\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&nbsp;</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">&Ocirc;ng Nguyễn Thanh H&agrave;, cho biết: Hiện nay, t&igrave;nh h&igrave;nh dịch Covid-19 ở Kh&aacute;nh H&ograve;a đ&atilde; cơ bản được kiểm so&aacute;t, UBND tỉnh Kh&aacute;nh H&ograve;a đang triển khai giai đoạn 2 kế hoạch th&iacute;ch ứng an to&agrave;n trong ph&ograve;ng, chống dịch. Sở VHTT đang cho triển khai x&acirc;y dựng một số chương tr&igrave;nh văn h&oacute;a, nghệ thuật phục vụ nhu cầu của nh&acirc;n d&acirc;n trong to&agrave;n tỉnh. Cụ thể Sở đang x&acirc;y dựng Chương tr&igrave;nh nghệ thuật ch&agrave;o năm mới 2022. &ldquo;D&ugrave; c&ograve;n nhiều kh&oacute; khăn nhưng tập thể Sở VHTT sẽ nỗ lực để đảm bảo c&aacute;c hoạt động văn h&oacute;a, nghệ thuật, thể thao sớm ổn định trở lại&rdquo;, &ocirc;ng Nguyễn Thanh H&agrave; cho biết.</span></span></p>\r\n\r\n<p style=\"margin-left:0in; margin-right:0in; text-align:justify\"><span style=\"color:#333333\"><span style=\"background-color:#ffffff\">B&ecirc;n cạnh đ&oacute;, để động vi&ecirc;n kh&iacute;ch lệ tinh thần lực lượng tuyến đầu ph&ograve;ng, chống dịch Covid-19, Sở VHTT đ&atilde; đề nghị Đo&agrave;n Ca m&uacute;a nhạc Hải Đăng th&agrave;nh lập đội văn nghệ xung k&iacute;ch với 21 ca sĩ, diễn vi&ecirc;n thực hiện biểu diễn ở c&aacute;c bệnh viện d&atilde; chiến, khu c&aacute;ch ly y tế tập trung, g&oacute;p phần cổ vũ, động vi&ecirc;n bệnh nh&acirc;n, đội ngũ y, b&aacute;c sĩ nơi tuyến đầu chống dịch.</span></span></p>\r\n', NULL, NULL);
INSERT INTO `tinbai` (`id`, `idTheLoai`, `TieuDe`, `TrichDan`, `anhtrichdan`, `NoiDung`, `NgayDang`, `TacGia`) VALUES
(42, 3, 'Quảng Nam: Mưa lũ làm 5.373 ngôi nhà bị ngập, 2.5035 người dân phải sơ tán', 'VHO-  Mưa lớn kéo dài những ngày cuối tuần qua đã gây ngập lụt tại nhiều địa phương của Quảng Nam,  làm 5.373 nhà bị ngập và hơn 2.500 nguời dân phải sơ tán. Thiệt hại về giao thông, sản xuất nông nghiệp,…khá nặng nề. Trong khi đó, dự báo từ ngày 26.10, ở khu vực Trung Bộ và Tây Nguyên có khả năng xảy ra một đợt mưa to đến rất to, Quảng Nam vừa khắc phục hậu quả mưa lũ, vừa tiếp tục triển khai các biện pháp chủ động ứng phó với vùng áp thấp và mưa lớn.', 'images/anh_xahoi_42.jpg', '<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46647/87682ha-25.jpg\" /></p>\r\n\r\n<p><strong>Nhiều v&ugrave;ng ở Quảng Nam bị ngập s&acirc;u do mưa lũ</strong></p>\r\n\r\n<p>Ng&agrave;y 25.10, Ban chỉ huy Ph&ograve;ng chống thi&ecirc;n tai v&agrave; T&igrave;m kiếm cứu nạn (PCTT v&agrave; TKCN) tỉnh Quảng Nam đ&atilde; c&oacute; b&aacute;o c&aacute;o nhanh c&ocirc;ng t&aacute;c triển khai ứng ph&oacute; với mưa lớn tr&ecirc;n địa b&agrave;n tỉnh Quảng Nam những ng&agrave;y qua.&nbsp;<br />\r\nTừ đ&ecirc;m ng&agrave;y 22 đến chiều ng&agrave;y 24.10, &nbsp;c&aacute;c địa phương trong tỉnh Quảng Nam đ&atilde; c&oacute; mưa to đến mưa rất to. Mực nước tr&ecirc;n s&ocirc;ng Tam Kỳ &nbsp;dao động ở mức tr&ecirc;n b&aacute;o động 3; tr&ecirc;n lưu vực s&ocirc;ng Thu Bồn đang dao động ở mức dưới b&aacute;o động 2,&hellip;<br />\r\nT&iacute;nh đến 17 giờ chiều ng&agrave;y 24.10, to&agrave;n tỉnh Quảng Nam sơ t&aacute;n 779 hộ với 2.535 khẩu. Trong đ&oacute;. TP. Tam Kỳ 22 hộ/57 khẩu; huyện Ph&uacute; Ninh sơ t&aacute;n xen gh&eacute;p 533 hộ/1803 khẩu, tập trung 8 hộ/62 nh&acirc;n khẩu; huyện Quế Sơn sơ t&aacute;n xen gh&eacute;p 204 hộ/546 khẩu, di dời tập trung 12 hộ/17 khẩu; huyện Bắc Tr&agrave; My 14 hộ/50 nh&acirc;n khẩu.<br />\r\nMưa lũ l&agrave;m 5.373 nh&agrave; với 20.219 khẩu tr&ecirc;n địa b&agrave;n tỉnh bị ngập. Trong đ&oacute;, huyện N&uacute;i Th&agrave;nh 1592 nh&agrave; bị ngập từ 0,5-0,7m; TP. Tam Kỳ 1235 nh&agrave; bị ngập từ 0,3-1,2m; TP. Hội An 785 nh&agrave; bị ngập s&acirc;u từ 0,1-0,5m; huyện Thăng B&igrave;nh 710 nh&agrave; bị ngập từ 0,3-1,2m.&nbsp;<br />\r\nTại huyện Ph&uacute; Ninh, một số khu d&acirc;n cư tại c&aacute;c x&atilde; Tam An (2 th&ocirc;n khoảng 400hộ), x&atilde; Tam Đ&agrave;n (2 th&ocirc;n khoảng 500 hộ), x&atilde; Tam Th&agrave;nh (2 th&ocirc;n khoảng 150 hộ) ngập s&acirc;u từ 1,5-2,0m. Địa phương đang theo d&otilde;i v&agrave; chuẩn bị lực lượng, phương tiện để chủ động di dời, ứng cứu d&acirc;n. Ngo&agrave;i ra c&ograve;n c&oacute; 1 người d&acirc;n ở huyện Ph&uacute; Ninh nghi bị mất t&iacute;ch tại cầu &Ocirc;ng Hiền tr&ecirc;n quốc lộ 1A, gần ng&atilde; 3 Kỳ L&yacute; ( x&atilde; Tam Đ&agrave;n).&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46647/87683ha-25b.jpg\" style=\"height:371px; width:600px\" /></p>\r\n\r\n<p><strong>TP. Hội An bị ngập s&acirc;u từ 0,1-0,5m.</strong></p>\r\n\r\n<p>Về nh&agrave; ở, ở huyện Nam Tr&agrave; My c&oacute; 03 nh&agrave; sạt lở đất v&agrave;o khu vực nh&agrave; bếp tại l&agrave;ng Tak pỏ, th&ocirc;n 1; 03 nh&agrave; c&oacute; nguy cơ đ&atilde; cho di dời người v&agrave; t&agrave;i sản đến nơi an to&agrave;n; Huyện N&uacute;i Th&agrave;nh c&oacute; 02 nh&agrave; bị sạt lở đất v&agrave;o nh&agrave;; tại huyện Ph&uacute; Ninh lở đất l&agrave;m sập nh&agrave; dưới của 02 hộ gia đ&igrave;nh.</p>\r\n\r\n<p>Hơn 97 ha hoa m&agrave;u, rau m&agrave;u bị thiệt hại do mưa lũ ( (Thăng B&igrave;nh 44ha; Quế Sơn 2,65ha; N&ocirc;ng Sơn 0,5 ha, N&uacute;iTh&agrave;nh 05 ha; Bắc Tr&agrave; My: 50ha); hơn 150ha diện t&iacute;ch đất nu&ocirc;i trồng thủy sản bị sạt lở v&agrave; 2,5 ha ao nu&ocirc;i trồng thủy sản bị ngập ở huyện N&uacute;i Th&agrave;nh; một số trang trại chăn nu&ocirc;i tr&ecirc;n địa b&agrave;n huyện Ph&uacute; Ninh bị ngập, cuốn tr&ocirc;i; 02 t&agrave;u, 03 chiếc ghe m&aacute;y bị ch&igrave;m; 01 t&agrave;u bị thủng nước ở huyện N&uacute;i Th&agrave;nh.&nbsp;</p>\r\n\r\n<p>Về giao th&ocirc;ng: Tuyến đường QL 40b đoạn Km 103+100 qua địa b&agrave;n huyện miền n&uacute;i Nam Tr&agrave; My bị sạt lở, đất đ&aacute; tr&agrave;n ra đường; hầu hết c&aacute;c tuyến đường về x&atilde;, th&ocirc;n đều bị sạt lở với khối lượng lớn.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46647/87681quoc-lo-25.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Nhiều vị tr&iacute; tr&ecirc;n tuyến Quốc lộ 1A đoạn qua địa phận Quảng Nam bị ngập, nước tr&agrave;n qua mặt đường</strong></p>\r\n\r\n<p>Tại huyện miền n&uacute;i Bắc Tr&agrave; My, một số tuyến đường bị sạt lở, giao th&ocirc;ng bị chia cắt (tuyến Tr&agrave; Đốc đi Tr&agrave; Bui; tuyến Tr&agrave; Gi&aacute;c đi Tr&agrave; Ka); tuyến đường đi Th&ocirc;n 5 (cũ) x&atilde; Tr&agrave; K&oacute;t, khối lượng thực tế đang được c&aacute;c địa phương kiểm tra. Tuyến đường ĐX từ th&ocirc;n 4 đi th&ocirc;n 6 x&atilde; Tr&agrave; Bui c&oacute; một số điểm sạt lở (khoản 2000m3), Tại N&oacute;c &ocirc;ng Phong th&ocirc;n 1 xảy ra sạt lở đất đ&aacute; ra đường b&ecirc; t&ocirc;ng (khoản 600m3), hiện lực lượng đ&atilde; khắc phục tạm để xe m&aacute;y lưu th&ocirc;ng v&agrave; nh&acirc;n d&acirc;n đi lại. Tuyến đường BTNT th&ocirc;n 3 x&atilde; Tr&agrave; Ka mưa lớn l&agrave;m x&oacute;i lở 03 điểm với hơn 1000m3.<br />\r\nHuyện T&acirc;y Giang: tuyến đường ĐT606 bị sạt lở g&acirc;y &aacute;ch tắc giao th&ocirc;ng đi c&aacute;c x&atilde; v&ugrave;ng cao của huyện nhiều giờ; một số tuyến đường ĐH, tuyến đường x&atilde; bị sạt lở nhỏ.<br />\r\nTại huyện N&uacute;i Th&agrave;nh, sạt lở 2 tuyến giao th&ocirc;ng ĐT 617, 618 bị sạt lở taluy dương ước khổi lượng 500 m3, gảy 5 trụ điện hiện đ&atilde; khắc phục điện; đường 618 c&ograve;n phần đất bị sạt lở; chỉ mới khắc phục xong đường ĐT 617.<br />\r\nNgo&agrave;i ra bị sạt lở tại đường Đ&agrave;o đoạn từ ng&atilde; 3 ĐH8 đến nh&agrave; văn h&oacute;a th&ocirc;n Danh Sơn, x&atilde; Tam Sơn; Tuyến ĐH3 (đoạn từ x&atilde; Đ&egrave;o Ba V&iacute; đến UBND x&atilde;) v&agrave; ĐH 8 (tuyến từ UBND x&atilde; Tam Sơn đến đều Phường Tổng v&agrave; tuyến từ UBND x&atilde; Tam Sơn đến đ&egrave;o Thầu Đ&acirc;u) mưa lớn đ&atilde; l&agrave;m hự hỏng nhiều đoạn, hầu hết c&aacute;c mương, cống rảnh bị bồi lối ho&agrave;n to&agrave;n, g&acirc;y kh&oacute; khăn cho việc đi lại; &hellip;<br />\r\nHuyện Thăng B&igrave;nh, huyện Ph&uacute; Ninh, giao th&ocirc;ng địa phương bị sạt lở, &aacute;ch tắc nhiều điểm, &nbsp;hư hỏng một số tuyến giao th&ocirc;n n&ocirc;ng th&ocirc;n , nhiều v&ugrave;ng bị ngập nước v&agrave; c&ocirc; lập ,&hellip;<br />\r\nHuyện Quế Sơn, sạt lỡ taluy dương tuyến đường ĐT 611, với chiều d&agrave;i 20 m, khối lượng sạt lở đất khoảng 1.000 m3; Sạt lở taluy &acirc;m c&aacute;c tuyến đường ĐH01.QS, tuyến đường GTNT, &nbsp;ĐH15.QS,&hellip;Huyện N&ocirc;ng Sơn, một số tuyến đường, giao th&ocirc;ng bị ngập, chia cắt.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/EasyDNNNews/thumbs/46647/87684ntm-25.jpg\" /></p>\r\n\r\n<p><strong>Người d&acirc;n huyện Nam Tr&agrave; My tham gia dọn dẹp, khắc phục kh&ocirc;i phục lại tuyến giao th&ocirc;ng&nbsp;</strong></p>\r\n\r\n<p>Theo Sở Giao th&ocirc;ng vận tải tỉnh Quảng Nam, &nbsp;khu gian đường sắt giữa 2 ga N&uacute;i Th&agrave;nh, tỉnh Quảng Nam v&agrave; Tr&igrave; B&igrave;nh, tỉnh Quảng Ng&atilde;i bị sạt lở, nền đường sắt bị kho&eacute;t s&acirc;u với chiều d&agrave;i đoạn bị hư hỏng l&agrave; 40m. C&aacute;c đơn vị chức năng đ&atilde; khắc phục đoạn đường sắt bị hư hỏng v&agrave; th&ocirc;ng tuyến l&uacute;c 21 giờ 00 ng&agrave;y 24.10.&nbsp;</p>\r\n\r\n<p>T&igrave;nh h&igrave;nh mưa lũ c&ograve;n diễn biến phức tạp, đề nghị c&aacute;c tổ chức, c&aacute; nh&acirc;n khi di chuyển phải chủ động, c&oacute; phương &aacute;n ph&ograve;ng tr&aacute;nh tai nạn do lũ lụt, sạt lở đất bất ngờ g&acirc;y ra.</p>\r\n\r\n<p>Dự b&aacute;o, trong ng&agrave;y h&ocirc;m nay, 25.10,c&aacute;c địa phương trong tỉnh mưa giảm dần về diện mưa v&agrave; lượng mưa; phổ biến chỉ c&ograve;n c&oacute; mưa, mưa vừa, lượng mưa từ 10 - 30mm, ri&ecirc;ng một số địa phương ph&iacute;a Nam c&oacute; nơi mưa to; tổng lượng mưa từ 30 - 50mm, c&oacute; nơi tr&ecirc;n 50mm.<br />\r\nBộ Chỉ huy Bộ đội bi&ecirc;n ph&ograve;ng tỉnh cho biết, đến 17 giờ chiều qua, ng&agrave;y 24.10, tổng số t&agrave;u c&aacute; đang hoạt động xa bờ tr&ecirc;n biển l&agrave; 35 t&agrave;u/1.272 lao động, trong đ&oacute; khu vực Ho&agrave;ng Sa 07 t&agrave;u/124 lao động (khu vực T&acirc;y Nam Ho&agrave;ng Sa); khu vực Trường Sa 28 t&agrave;u/1.148 lao động (c&aacute;c t&agrave;u nằm trong khu vực an to&agrave;n).</p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, NULL),
(43, 3, 'Khánh Hòa: Khẩn trương ứng phó với áp thấp nhiệt đới mạnh lên thành bão số 9', 'VHO - Ngày 25.10, UBND tỉnh Khánh Hòa ban hành Công điện khẩn số 09/CĐ-UBND yêu cầu các Sở, ngành, nhân dân và các đơn vị chủ động ứng phó với ATNĐ, bão (cơn bão số 9) và mưu lũ trên địa bàn tỉnh.', 'images/anh_xahoi_43.jpg', '<p style=\"text-align:center\">Theo đ&oacute;, UBND tỉnh Kh&aacute;nh H&ograve;a đề nghị Bộ Chỉ huy Bộ đội Bi&ecirc;n ph&ograve;ng Kh&aacute;nh H&ograve;a tiếp tục tổ chức kiểm đếm, giữ th&ocirc;ng tin li&ecirc;n lạc v&agrave; kịp thời th&ocirc;ng b&aacute;o cho chủ c&aacute;c phương tiện, thuyền trưởng c&aacute;c t&agrave;u, thuyền đang hoạt động tr&ecirc;n biển biết vị tr&iacute;, hướng di chuyển v&agrave; diễn biến của v&ugrave;ng &aacute;p thấp để chủ động tho&aacute;t ra hoặc kh&ocirc;ng di chuyển v&agrave;o v&ugrave;ng nguy hiểm. UBND tỉnh cũng y&ecirc;u cầu Ban Chỉ huy PCTT v&agrave; TKCN tỉnh phối hợp tổ chức hướng dẫn việc neo đậu, sắp xếp t&agrave;u thuyền tại c&aacute;c khu tr&aacute;nh tr&uacute;; quản l&yacute; chặt chẽ việc ra khơi của c&aacute;c t&agrave;u thuyền.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/xuanhuong/0_01_16.jpg\" /></p>\r\n\r\n<p><strong>T&agrave;u, thuyền tr&ecirc;n v&ugrave;ng biển tỉnh Kh&aacute;nh H&ograve;a khẩn trương t&igrave;m nơi tr&aacute;nh tr&uacute; bảo đảm an to&agrave;n</strong></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, UBND tỉnh Kh&aacute;nh H&ograve;a đề nghị Ban Chỉ huy PCTT v&agrave; TKCN c&aacute;c huyện, thị x&atilde;, th&agrave;nh phố chỉ đạo c&aacute;c x&atilde;, phường kiểm tra, r&agrave; so&aacute;t c&aacute;c khu vực xung yếu, chủ động th&ocirc;ng tin, cảnh b&aacute;o đến người d&acirc;n khi c&oacute; mưa lớn xảy ra; chủ động bố tr&iacute; lực lượng sẵn s&agrave;ng triển khai sơ t&aacute;n d&acirc;n ra khỏi c&aacute;c khu vực xung yếu, nguy hiểm, trong đ&oacute; cần kết hợp biện ph&aacute;p sơ t&aacute;n d&acirc;n vừa đảm bảo an to&agrave;n trong thi&ecirc;n tai v&agrave; ph&ograve;ng chống dịch bệnh Covid-19.</p>\r\n\r\n<p>Để đảm bảo an to&agrave;n cho học sinh, ng&agrave;y 25.10, Sở GDĐT tỉnh Kh&aacute;nh H&ograve;a đ&atilde; c&oacute; văn bản khẩn gửi đến c&aacute;c ph&ograve;ng GDĐT v&agrave; c&aacute;c đơn vị trực thuộc sở về việc chủ động ứng ph&oacute; với &aacute;p thấp nhiệt đới, b&atilde;o (cơn b&atilde;o số 9).</p>\r\n\r\n<p>Theo đ&oacute;, Sở GDĐT y&ecirc;u cầu c&aacute;c đơn vị trường học cho học sinh (HS) tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh Kh&aacute;nh H&ograve;a nghỉ học trực tiếp v&agrave;o 2 ng&agrave;y 26, 27.10 v&agrave; chuyển sang dạy học trực tuyến. Thường xuy&ecirc;n theo d&otilde;i diễn biến t&igrave;nh h&igrave;nh b&atilde;o, mưa lũ để chủ động điều chỉnh thời gian cho HS nghỉ học; kịp thời phối hợp với c&aacute;c lực lượng cứu hộ tại địa phương trong c&ocirc;ng t&aacute;c ứng ph&oacute; sự cố thi&ecirc;n tai v&agrave; t&igrave;m kiếm cứu nạn tại đơn vị. Chủ động c&aacute;c biện ph&aacute;p ph&ograve;ng tr&aacute;nh mưa ngập, l&ecirc;n phương &aacute;n bảo đảm an to&agrave;n, giảm thiểu thiệt hại về cơ sở vật chất, c&ocirc;ng tr&igrave;nh trường học, t&agrave;i liệu, s&aacute;ch vở v&agrave; dụng cụ học tập&hellip;</p>\r\n\r\n<p>Trước đ&oacute;, Trung t&acirc;m Dự b&aacute;o kh&iacute; tượng thủy văn quốc gia, cho biết: Hồi 1 h ng&agrave;y 25.10 vị tr&iacute; t&acirc;m &aacute;p thấp nhiệt đới c&oacute; khả năng mạnh l&ecirc;n th&agrave;nh b&atilde;o số 9 ở v&agrave;o khoảng 10,5 độ Vĩ Bắc; 115,2 độ Kinh Đ&ocirc;ng, c&aacute;ch đảo Song Tử T&acirc;y khoảng 140 km về ph&iacute;a Đ&ocirc;ng Nam. Sức gi&oacute; mạnh nhất v&ugrave;ng gần t&acirc;m &aacute;p thấp nhiệt đới (ATNĐ) mạnh cấp 6 (40- 50km/giờ), giật cấp 8. B&aacute;n k&iacute;nh gi&oacute; mạnh cấp 6, giật cấp 8 khoảng 80 km t&iacute;nh từ t&acirc;m ATNĐ.</p>\r\n\r\n<p><img alt=\"\" src=\"http://vanhoaonline.com.vn/Portals/0/xuanhuong/0_01_17.jpg\" /></p>\r\n\r\n<p><strong>Ngư d&acirc;n đang đ&aacute;nh bắt c&aacute; tr&ecirc;n biển, nhanh ch&oacute;ng v&agrave;o bờ tr&aacute;nh tr&uacute; b&atilde;o số 9</strong></p>\r\n\r\n<p>Trong 24 giờ tới, ATNĐ di chuyển chủ yếu theo hướng T&acirc;y T&acirc;y Bắc, mỗi giờ đi được khoảng 15km v&agrave; c&oacute; khả năng mạnh l&ecirc;n th&agrave;nh b&atilde;o. Đến 1 h 00 ng&agrave;y 26.10, vị tr&iacute; t&acirc;m b&atilde;o ở khoảng 11,5 độ Vĩ Bắc; 111,9 độ Kinh Đ&ocirc;ng, tr&ecirc;n v&ugrave;ng biển ph&iacute;a T&acirc;y Bắc quần đảo Trường Sa. Sức gi&oacute; mạnh nhất v&ugrave;ng gần t&acirc;m b&atilde;o mạnh cấp 8 (60-75km/giờ), giật cấp 10, cảnh b&aacute;o cấp độ rủi ro thi&ecirc;n tai cấp 3. Trước diễn biến ATNĐ khả năng mạnh l&ecirc;n th&agrave;nh b&atilde;o, di chuyển nhanh, nguy cơ ảnh hưởng trực tiếp đến tỉnh Kh&aacute;nh H&ograve;a từ ng&agrave;y 26.10.</p>\r\n', NULL, NULL),
(44, 3, 'Quảng Nam: Xử phạt 7,5 triệu đồng đối với một cá nhân đăng thông tin sai về quỹ vắc xin', 'VHO- Sở Thông tin và Truyền thông tỉnh Quảng Nam vừa quyết định xử phạt hành chính đối với cá nhân cung cấp thông tin sai sự thật về quỹ vắc xin Covid-19 lên mạng xã hội', 'images/anh_xahoi_44.jpg', '<p><img alt=\"\" src=\"http://baovanhoa.vn/Portals/0/EasyDNNNews/thumbs/46372/87070b8777b37c8c4019a58d5-(1).jpg\" style=\"height:400px; width:321px\" /></p>\r\n\r\n<p><strong>B&agrave;i viết c&oacute; nội dung sai sự thật tr&ecirc;n t&agrave;i khoản Facebook của &ocirc;ng Thoại. Ảnh: Sở TT&amp;TT</strong></p>\r\n\r\n<p>Sở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tỉnh Quảng Nam cho biết đ&atilde; ban h&agrave;nh Quyết định số 04/QĐ-XPVPHC về xử phạt vi phạm h&agrave;nh ch&iacute;nh đối với &ocirc;ng Đinh Hữu Thoại ( SN 1973, hiện tr&uacute; tại thị trấn Ti&ecirc;n Kỳ, huyện Ti&ecirc;n Phước, Quảng Nam) v&igrave; c&oacute; h&agrave;nh vi cung cấp nội dung th&ocirc;ng tin sai sự thật tr&ecirc;n mạng x&atilde; hội, g&acirc;y bức x&uacute;c dư luận.<br />\r\nTrước đ&oacute;, ng&agrave;y 18.6.2021, c&aacute; nh&acirc;n n&agrave;y sử dụng t&agrave;i khoản facebook c&oacute; t&ecirc;n &ldquo;Đinh Hữu Thoại&rdquo; (ID: 1849592060, c&oacute; t&iacute;ch xanh) cung cấp th&ocirc;ng tin sai sự thật, với nội dung:<br />\r\n&ldquo;QUỸ VẮC XIN L&Agrave; QUỸ LỪA ĐẢO!&nbsp;<br />\r\nKẺ LỪA ĐẢO TH&Igrave; MANG TIỀN ĐI GỬI NH&Agrave; BĂNG LẤY L&Atilde;I.&nbsp;<br />\r\nKẺ BỊ LỪA TH&Igrave; NGẬM BỒ H&Ograve;N L&Agrave;M NGỌT!&rdquo;&nbsp;<br />\r\nK&egrave;m theo đ&oacute; l&agrave; ảnh chụp b&agrave;i viết tr&ecirc;n trang th&ocirc;ng tin về dịch bệnh vi&ecirc;m đường h&ocirc; hấp cấp covid-19 của Bộ Y tế c&oacute; nội dung:<br />\r\n&ldquo;Thủ tướng: Quỹ vắc xin COVID-19 l&agrave; quỹ của sự nh&acirc;n &aacute;i, tinh thần đo&agrave;n kết v&agrave; tr&aacute;i tim kết nối tr&aacute;i tim.<br />\r\nTrong b&agrave;i ph&aacute;t biểu quan trọng như &ldquo;lời hiệu triệu&rdquo; huy động mọi nguồn lực cho chiến lược vắc xin ph&ograve;ng COVID-19, Thủ tướng Ch&iacute;nh phủ Phạm Minh Ch&iacute;nh khẳng định, Đảng, Nh&agrave; nước, Ch&iacute;nh phủ hiểu v&agrave; tr&acirc;n trọng mọi đ&oacute;ng g&oacute;p của người d&acirc;n, doanh nghiệp kh&ocirc;ng kể &iacute;t hay nhiều, g&oacute;p v&agrave;o Qũy vắc xin ph&ograve;ng, chống COVID-19 - Qũy của sự nh&acirc;n &aacute;i, của tinh thần đo&agrave;n kết, của niềm tin, của tr&aacute;i tim kết nối tr&aacute;i tim&rdquo;.<br />\r\nQuy định tại điểm a, khoản 1, điều 101 Nghị định 15/2020/NĐ-CP ng&agrave;y 03/02/2020 của Ch&iacute;nh phủ quy định xử phạt vi phạm h&agrave;nh ch&iacute;nh trong lĩnh vực bưu ch&iacute;nh, viễn th&ocirc;ng, tần số v&ocirc; tuyến điện, c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; giao dịch điện tử.&nbsp;<br />\r\n&Aacute;p dụng h&igrave;nh thức xử phạt ch&iacute;nh l&agrave; phạt tiền với số tiền xử phạt l&agrave; 7.500.000 đồng v&agrave; buộc gỡ bỏ th&ocirc;ng tin sai sự thật tr&ecirc;n t&agrave;i khoản facebook.&nbsp;<br />\r\nQuyết định xử phạt c&oacute; hiệu lực từ ng&agrave;y 14.10.2021 v&agrave; số tiền phạt c&aacute; nh&acirc;n phải nộp tại Kho bạc nh&agrave; nước tỉnh Quảng Nam trong thời hạn 10 ng&agrave;y, kể từ ng&agrave;y nhận được Quyết định xử phạt.<br />\r\nSở Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng tiếp tục phối hợp với C&ocirc;ng an tỉnh v&agrave; c&aacute;c đơn vị li&ecirc;n quan r&agrave; so&aacute;t, theo d&otilde;i, nắm bắt th&ocirc;ng tin để chấn chỉnh v&agrave; xử l&yacute; nghi&ecirc;m c&aacute;c trường hợp tung tin sai sự thật dưới mọi h&igrave;nh thức trong địa b&agrave;n tỉnh theo quy định ph&aacute;p luật.</p>\r\n', NULL, NULL);

